import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import API_CONFIG from '../config/apiConfig';

const CourseDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_CONFIG.BASE_URL}/courses/${id}`);
        setCourse(response.data.course);
      } catch (err) {
        const msg = err.response?.data?.message || 'Failed to fetch course details';
        setError(msg);
        toast.error(msg);
      } finally {
        setLoading(false);
      }
    };

    fetchCourse();
  }, [id]);

  useEffect(() => {
    const confirmPurchase = async () => {
      const query = new URLSearchParams(location.search);
      const courseId = query.get('course_id');
      const studentId = query.get('student_id');

      if (courseId && studentId) {
        try {
          const token = localStorage.getItem('token');
          await axios.post(
            `${API_CONFIG.BASE_URL}/courses/purchase-success`,
            { course_id: courseId, student_id: studentId },
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );
          toast.success('Enrollment confirmed!');
        } catch (err) {
          toast.error(err.response?.data?.message || 'Failed to confirm enrollment');
        }
      }
    };

    confirmPurchase();
  }, [location]);

  const handlePurchase = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      toast.error('Please log in as a student to enroll');
      navigate('/login');
      return;
    }

    try {
      const response = await axios.post(
        `${API_CONFIG.BASE_URL}/courses/purchase/${id}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data.url) {
        // Redirect to Stripe checkout for paid courses
        window.location.href = response.data.url;
      } else {
        // Free course enrollment success
        toast.success('Enrolled successfully!');
        navigate('/courses');
      }
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to process enrollment');
    }
  };

  if (loading) return <p className="text-center mt-10 text-gray-600">Loading course details...</p>;
  if (error) return <p className="text-center mt-10 text-red-600">{error}</p>;
  if (!course) return <p className="text-center mt-10 text-gray-600">Course not found.</p>;

  return (
    <div className="flex-1 bg-gray-50 py-12 px-4">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="max-w-5xl mx-auto">
        {/* Back Button */}
        <Link to="/courses" className="text-blue-600 hover:underline mb-6 inline-block">
          ← Back to Courses
        </Link>

        {/* Course Card */}
        <div className="bg-white rounded-2xl shadow-md overflow-hidden">
          {course.coverImage && (
            <img
              src={course.coverImage}
              alt={course.title}
              className="w-full h-64 object-cover"
            />
          )}
          <div className="p-6 md:p-8">
            {/* Title & Description */}
            <h1 className="text-3xl font-bold text-gray-800 mb-4">{course.title}</h1>
            <p className="text-gray-600 mb-6 leading-relaxed">{course.description}</p>

            {/* Metadata */}
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm text-gray-600 mb-6">
              <p><strong>Instructor:</strong> {course.instructorName}</p>
              <p><strong>Category:</strong> {course.category}</p>
              <p><strong>Level:</strong> {course.level}</p>
              <p><strong>Created:</strong> {new Date(course.createdAt).toLocaleDateString()}</p>
              <p className="sm:col-span-2">
                <strong>Price:</strong>{' '}
                {course.price === 0 ? 'Free' : `$${course.price.toFixed(2)}`}
              </p>
            </div>

            {/* Sample Video */}
            {course.sampleVideo && (
              <div className="mb-6">
                <h2 className="text-xl font-semibold text-gray-700 mb-2">Sample Video</h2>
                <video src={course.sampleVideo} controls className="w-full h-64 rounded-lg border" />
              </div>
            )}

            {/* Action Buttons */}
            <div className="flex flex-col sm:flex-row gap-4 mt-8">
              <button
                onClick={handlePurchase}
                className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-3 rounded-lg transition"
              >
                {course.price === 0 ? 'Enroll for Free' : 'Purchase Course'}
              </button>
              <Link
                to="/courses"
                className="bg-gray-200 hover:bg-gray-300 text-gray-800 font-medium px-6 py-3 rounded-lg transition text-center"
              >
                Explore More Courses
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
