import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Signup = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('Student');
  const [bio, setBio] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        firstName,
        lastName,
        email,
        password,
        role,
        ...(role === 'Instructor' && { bio }),
      };
      await axios.post(`${API_CONFIG.BASE_URL}/auth/signup`, payload);

      toast.success('Account created successfully! Redirecting to login...');
      setTimeout(() => {
        navigate('/login', { state: { message: 'Account created successfully! Please log in.' } });
      }, 2000);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Signup failed. Please try again.');
    }
  };

  return (
    <>
      <ToastContainer
        position="top-center"
        autoClose={3000}
        theme="colored"
        pauseOnHover
        draggable
      />
      <section className="min-h-screen bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 flex items-center justify-center px-4">
        <div className="bg-white rounded-xl shadow-xl max-w-md w-full p-8">
          <h2 className="text-indigo-900 text-3xl font-extrabold mb-6 text-center">
            Sign Up for EduVerse
          </h2>
          <form onSubmit={handleSubmit} className="space-y-5">
            <div>
              <label htmlFor="firstName" className="block text-indigo-900 font-semibold mb-1">
                First Name
              </label>
              <input
                id="firstName"
                type="text"
                required
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                placeholder="John"
              />
            </div>
            <div>
              <label htmlFor="lastName" className="block text-indigo-900 font-semibold mb-1">
                Last Name
              </label>
              <input
                id="lastName"
                type="text"
                required
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                placeholder="Doe"
              />
            </div>
            <div>
              <label htmlFor="email" className="block text-indigo-900 font-semibold mb-1">
                Email
              </label>
              <input
                id="email"
                type="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                placeholder="your.email@example.com"
              />
            </div>
            <div>
              <label htmlFor="password" className="block text-indigo-900 font-semibold mb-1">
                Password
              </label>
              <input
                id="password"
                type="password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                placeholder="Create a password"
              />
            </div>
            <div>
              <label htmlFor="role" className="block text-indigo-900 font-semibold mb-1">
                Role
              </label>
              <select
                id="role"
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
              >
                <option value="Student">Student</option>
                <option value="Instructor">Instructor</option>
                <option value="Admin">Admin</option>
              </select>
            </div>
            {role === 'Instructor' && (
              <div>
                <label htmlFor="bio" className="block text-indigo-900 font-semibold mb-1">
                  Bio
                </label>
                <textarea
                  id="bio"
                  rows={4}
                  value={bio}
                  onChange={(e) => setBio(e.target.value)}
                  placeholder="Tell us about yourself (expertise, experience...)"
                  className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                />
              </div>
            )}
            <button
              type="submit"
              className="w-full bg-yellow-400 text-indigo-900 font-bold py-2 rounded-lg hover:bg-yellow-500 transition focus:outline-none focus:ring-4 focus:ring-yellow-300"
            >
              Sign Up
            </button>
          </form>
          <p className="mt-6 text-indigo-900 text-center">
            Already have an account?{' '}
            <Link
              to="/login"
              className="font-semibold hover:text-yellow-400 transition"
            >
              Log in
            </Link>
          </p>
        </div>
      </section>
    </>
  );
};

export default Signup;
