import React, { useEffect, useState } from 'react';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';

export default function CourseContentList({ courseId, user }) {
  const [contents, setContents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setLoading(true);
    axios.get(`${API_CONFIG.BASE_URL}/course-content/course/${courseId}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => {
        setContents(res.data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to load course content.');
        setLoading(false);
      });
  }, [courseId]);

  if (loading) return <div className="text-primary">Loading course content...</div>;
  if (error) return <div className="text-red-600">{error}</div>;
  if (!contents.length) return <div className="text-gray-500">No content added yet.</div>;

  return (
    <div className="mt-8">
      <h2 className="text-xl font-bold mb-4">Course Content</h2>
      <ul className="space-y-4">
        {contents.map(content => (
          <li key={content._id} className="bg-white rounded-lg shadow p-4">
            <div className="flex items-center justify-between">
              <div>
                <span className="font-semibold text-primary">{content.title}</span>
                <span className="ml-2 text-xs bg-gray-200 px-2 py-1 rounded">{content.type}</span>
                {content.description && <div className="text-gray-600 mt-1">{content.description}</div>}
                {content.resourceUrl && (
                  <div className="mt-2">
                    <a href={content.resourceUrl} target="_blank" rel="noopener noreferrer" className="text-blue-500 underline">View Resource</a>
                  </div>
                )}
              </div>
              {/* Optionally, show edit/delete for instructors here */}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
