import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

// Static course data (same as Courses.js, replace with API call later)
const coursesData = [
  {
    id: 1,
    title: 'Web Development Bootcamp',
    description: 'Master HTML, CSS, and JavaScript in 8 weeks. This comprehensive course covers front-end and back-end development, including React, Node.js, and MongoDB. Build real-world projects and launch your career as a web developer.',
    image: 'https://via.placeholder.com/300x200?text=Web+Development',
    category: 'Web Development',
    price: 'Paid',
    level: 'Beginner',
    instructor: { fullName: 'Jane Doe', bio: 'Experienced web developer with 10+ years in the industry.' },
  },
  {
    id: 2,
    title: 'Data Science Fundamentals',
    description: 'Learn Python, Pandas, and Machine Learning basics. This course introduces data analysis, visualization, and predictive modeling. Perfect for those starting their data science journey.',
    image: 'data:image/png;base64,',
    category: 'Data Science',
    price: 'Paid',
    level: 'Intermediate',
    instructor: { fullName: 'John Smith', bio: 'Data scientist with expertise in AI and big data.' },
  },
  {
    id: 3,
    title: 'Digital Marketing Mastery',
    description: 'Boost your business with SEO and social media strategies. Learn to create effective campaigns, optimize websites, and analyze performance metrics in this free course.',
    image: 'https://via.placeholder.com/300x200?text=Digital+Marketing',
    category: 'Marketing',
    price: 'Free',
    level: 'Beginner',
    instructor: { fullName: 'Sarah Lee', bio: 'Digital marketing expert with a focus on SEO.' },
  },
  {
    id: 4,
    title: 'Advanced Machine Learning',
    description: 'Deep dive into neural networks and AI. Explore advanced topics like deep learning, reinforcement learning, and computer vision with hands-on projects.',
    image: 'https://via.placeholder.com/300x200?text=Machine+Learning',
    category: 'Data Science',
    price: 'Paid',
    level: 'Advanced',
    instructor: { fullName: 'Mike Brown', bio: 'AI researcher with publications in top journals.' },
  },
];

const CourseDetails = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);

  useEffect(() => {
    // Static data for now
    const selectedCourse = coursesData.find((c) => c.id === parseInt(id));
    setCourse(selectedCourse);

    // TODO: Fetch course from backend (uncomment when /api/courses/:id is ready)
    /*
    import axios from 'axios';
    axios.get(`http://localhost:5000/api/courses/${id}`)
      .then(response => setCourse(response.data))
      .catch(error => console.error('Fetch course error:', error));
    */
  }, [id]);

  if (!course) {
    return (
      <div className="flex-1 bg-gray-50 py-12 px-4 min-h-screen">
        <div className="max-w-7xl mx-auto text-center">
          <h1 className="text-3xl font-bold text-gray-800">Course Not Found</h1>
          <p className="text-gray-600 mt-4">
            The course you're looking for doesn't exist.{' '}
            <Link to="/courses" className="text-blue-600 hover:underline">
              Back to Courses
            </Link>
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-1 bg-gray-50 py-12 px-4 min-h-screen">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg overflow-hidden mb-10">
          <img
            src={course.image}
            alt={course.title}
            className="w-full h-64 md:h-96 object-cover"
          />
          <div className="p-8">
            <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">
              {course.title}
            </h1>
            <p className="text-gray-600 mb-6">{course.description}</p>
            <div className="flex flex-col sm:flex-row gap-4">
              <Link
                to="/enroll" // Placeholder for enrollment
                className="inline-block bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 transition"
              >
                Enroll Now
              </Link>
              <Link
                to="/courses"
                className="inline-block border border-blue-600 text-blue-600 px-6 py-3 rounded-md hover:bg-blue-50 transition"
              >
                Back to Courses
              </Link>
            </div>
          </div>
        </div>

        {/* Details Section */}
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Course Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Category:</span> {course.category}
              </p>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Level:</span> {course.level}
              </p>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Price:</span> {course.price}
              </p>
            </div>
            <div>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Instructor:</span> {course.instructor.fullName}
              </p>
              <p className="text-gray-600">{course.instructor.bio}</p>
            </div>
          </div>
        </div>

        {/* Footer */}
        <footer className="mt-10 text-center text-gray-600">
          <p>© 2025 EduVerse. All rights reserved.</p>
          <div className="mt-4 space-x-4">
            <Link to="/about" className="text-blue-600 hover:underline">About</Link>
            <Link to="/contact" className="text-blue-600 hover:underline">Contact</Link>
            <Link to="/privacy" className="text-blue-600 hover:underline">Privacy Policy</Link>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default CourseDetails;
