import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CreateCourse = () => {
  const [formData, setFormData] = useState({
    instructorName: '',
    title: '',
    coverImage: '',
    category: 'Programming',
    price: '',
    level: 'Beginner',
    description: '',
    sampleVideo: '',
  });
  const [uploadingImage, setUploadingImage] = useState(false);
  const [uploadingVideo, setUploadingVideo] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decoded = jwtDecode(token);
      if (decoded.role !== 'Instructor') {
        navigate('/profile');
        return;
      }
      setFormData((prev) => ({
        ...prev,
        instructorName: `${decoded.firstName} ${decoded.lastName}`,
      }));
    } catch (err) {
      localStorage.removeItem('token');
      navigate('/login');
    }

    const script = document.createElement('script');
    script.src = 'https://widget.cloudinary.com/v2.0/global/all.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const openCloudinaryWidget = (field) => {
    if (!window.cloudinary) {
      toast.error('Cloudinary widget failed to load');
      return;
    }
    const widget = window.cloudinary.createUploadWidget(
      {
        cloudName: process.env.REACT_APP_CLOUDINARY_CLOUD_NAME || 'your_cloud_name',
        uploadPreset: process.env.REACT_APP_CLOUDINARY_UPLOAD_PRESET || 'your_unsigned_preset',
        sources: ['local', 'url'],
        multiple: false,
        resourceType: field === 'coverImage' ? 'image' : 'video',
        maxFileSize: field === 'coverImage' ? 10485760 : 104857600,
        clientAllowedFormats: field === 'coverImage' ? ['jpg', 'png', 'jpeg'] : ['mp4', 'webm'],
      },
      (error, result) => {
        if (!error && result && result.event === 'success') {
          setFormData((prev) => ({
            ...prev,
            [field]: result.info.secure_url,
          }));
          toast.success(`${field === 'coverImage' ? 'Cover image' : 'Sample video'} uploaded successfully`);
          field === 'coverImage' ? setUploadingImage(false) : setUploadingVideo(false);
        } else if (error) {
          toast.error(`Failed to upload ${field === 'coverImage' ? 'cover image' : 'sample video'}`);
          field === 'coverImage' ? setUploadingImage(false) : setUploadingVideo(false);
        }
      }
    );
    widget.open();
    field === 'coverImage' ? setUploadingImage(true) : setUploadingVideo(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await axios.post(
        `${API_CONFIG.BASE_URL}/courses/create`,
        { ...formData, price: parseFloat(formData.price) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Course created successfully');
      navigate('/instructor/profile');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to create course');
    }
  };

  return (
    <section className="bg-gradient-to-b from-white to-blue-50 py-12 px-4 min-h-screen">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="max-w-4xl mx-auto bg-white p-10 rounded-2xl shadow-xl">
        <h2 className="text-4xl font-bold text-blue-700 mb-8 text-center">Create a New Course</h2>
        <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-6">
          <input type="text" name="title" placeholder="Course Title" value={formData.title} onChange={handleInputChange} className="input" required />
          <select name="category" value={formData.category} onChange={handleInputChange} className="input" required>
            <option value="Programming">Programming</option>
            <option value="Design">Design</option>
            <option value="Business">Business</option>
            <option value="Language">Language</option>
            <option value="Other">Other</option>
          </select>
          <input type="number" name="price" placeholder="Price ($)" step="0.01" value={formData.price} onChange={handleInputChange} className="input" required />
          <select name="level" value={formData.level} onChange={handleInputChange} className="input" required>
            <option value="Beginner">Beginner</option>
            <option value="Intermediate">Intermediate</option>
            <option value="Advanced">Advanced</option>
          </select>
          <textarea name="description" placeholder="Course Description" rows="5" value={formData.description} onChange={handleInputChange} className="input" required></textarea>

          <div className="space-y-2">
            <button type="button" onClick={() => openCloudinaryWidget('coverImage')} className="btn-blue w-full" disabled={uploadingImage}>
              {uploadingImage ? 'Uploading Cover Image...' : 'Upload Cover Image'}
            </button>
            {formData.coverImage && <img src={formData.coverImage} alt="Cover Preview" className="w-full h-40 object-cover rounded-md" />}
          </div>

          <div className="space-y-2">
            <button type="button" onClick={() => openCloudinaryWidget('sampleVideo')} className="btn-blue w-full" disabled={uploadingVideo}>
              {uploadingVideo ? 'Uploading Video...' : 'Upload Sample Video'}
            </button>
            {formData.sampleVideo && <video src={formData.sampleVideo} controls className="w-full h-40 object-cover rounded-md" />}
          </div>

          <div className="flex justify-between">
            <button type="button" onClick={() => navigate('/instructor/profile')} className="btn-gray">
              Cancel
            </button>
            <button type="submit" className="btn-blue">
              Create Course
            </button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default CreateCourse;
