import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { Dialog, Transition } from '@headlessui/react';
import { Fragment } from 'react';
import { jwtDecode } from 'jwt-decode';

const CourseContents = () => {
  const { courseId } = useParams();
  const [contents, setContents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [quizModalOpen, setQuizModalOpen] = useState(false);
  const [activeQuiz, setActiveQuiz] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [quizSubmitting, setQuizSubmitting] = useState(false);
  const [quizResult, setQuizResult] = useState(null);
  const [quizSubmission, setQuizSubmission] = useState({}); // {quizContentId: {score, answers}}
  const [userRole, setUserRole] = useState('');
  const [analyticsModalOpen, setAnalyticsModalOpen] = useState(false);
  const [quizAnalytics, setQuizAnalytics] = useState(null);
  const [analyticsQuiz, setAnalyticsQuiz] = useState(null);
  const [resultModalOpen, setResultModalOpen] = useState(false);
  const [resultQuiz, setResultQuiz] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`${API_CONFIG.BASE_URL}/course-content/course/${courseId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setContents(res.data.contents))
      .catch(() => setContents([]))
      .finally(() => setLoading(false));
    // Get user role
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserRole(decoded.role);
      } catch {}
    }
  }, [courseId]);

  // Fetch quiz submission for each quiz on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    contents.forEach(content => {
      if (content.type === 'quiz') {
        axios.get(`${API_CONFIG.BASE_URL}/quiz-submissions/${content._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        }).then(res => {
          setQuizSubmission(prev => ({ ...prev, [content._id]: res.data.submission }));
        }).catch(() => {});
      }
    });
    // eslint-disable-next-line
  }, [contents]);

  const openQuiz = (quiz) => {
    setActiveQuiz(quiz);
    setQuizAnswers({});
    setQuizResult(null);
    setQuizModalOpen(true);
  };

  const handleQuizAnswer = (q, val) => {
    setQuizAnswers(prev => ({ ...prev, [q]: val }));
  };

  const handleQuizSubmit = async (e) => {
    e.preventDefault();
    setQuizSubmitting(true);
    const token = localStorage.getItem('token');
    const answers = (activeQuiz.questions || []).map(q => ({ question: q.question, selected: quizAnswers[q.question] || '' }));
    try {
      const res = await axios.post(`${API_CONFIG.BASE_URL}/quiz-submissions`, {
        quizContentId: activeQuiz._id,
        answers
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuizResult(res.data);
      setQuizSubmission(prev => ({ ...prev, [activeQuiz._id]: { score: res.data.score, answers: res.data.answers } }));
    } catch (err) {
      setQuizResult({ error: err.response?.data?.message || 'Submission failed' });
    } finally {
      setQuizSubmitting(false);
    }
  };

  const openQuizAnalytics = async (quiz) => {
    setAnalyticsQuiz(quiz);
    setQuizAnalytics(null);
    setAnalyticsModalOpen(true);
    const token = localStorage.getItem('token');
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/quiz-submissions/analytics/${quiz._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuizAnalytics(res.data);
    } catch (err) {
      setQuizAnalytics({ error: err.response?.data?.message || 'Failed to load analytics' });
    }
  };

  if (loading) return <div className="p-8">Loading...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 flex flex-col">
      {/* Hero Header */}
      <div className="w-full bg-primary text-white py-10 shadow-lg rounded-b-3xl mb-8">
        <div className="max-w-4xl mx-auto px-4">
          <h1 className="text-4xl font-extrabold mb-2 tracking-tight">Course Contents</h1>
          <p className="text-lg opacity-90">All your learning materials, quizzes, and activities in one place.</p>
        </div>
      </div>
      <div className="flex-1 w-full max-w-4xl mx-auto px-4">
        {contents.length === 0 ? (
          <div className="text-center text-gray-600 text-lg mt-16">No content available for this course yet.</div>
        ) : (
          <div className="space-y-8">
            {contents.map(content => (
              <div key={content._id} className={`rounded-2xl shadow-lg p-6 bg-white flex flex-col md:flex-row md:items-center gap-6 border-l-8 ${content.type === 'quiz' ? 'border-indigo-500' : content.type === 'assignment' ? 'border-yellow-400' : content.type === 'poll' ? 'border-pink-400' : content.type === 'survey' ? 'border-green-400' : 'border-gray-200'}`}>
                <div className="flex-shrink-0 flex flex-col items-center justify-center w-16 h-16 rounded-full bg-gradient-to-br from-primary to-indigo-400 text-white text-2xl shadow">
                  {content.type === 'quiz' && <span></span>}
                  {content.type === 'assignment' && <span></span>}
                  {content.type === 'video' && <span></span>}
                  {content.type === 'slide' && <span></span>}
                  {content.type === 'document' && <span></span>}
                  {content.type === 'poll' && <span></span>}
                  {content.type === 'survey' && <span></span>}
                  {content.type === 'live' && <span></span>}
                  {content.type === 'notice' && <span></span>}
                </div>
                <div className="flex-1">
                  <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-2">
                    <div>
                      <h2 className="text-xl font-bold text-primary mb-1 flex items-center gap-2">
                        {content.title}
                        <span className={`text-xs px-2 py-1 rounded-full ml-2 ${content.type === 'quiz' ? 'bg-indigo-100 text-indigo-700' : content.type === 'assignment' ? 'bg-yellow-100 text-yellow-700' : content.type === 'poll' ? 'bg-pink-100 text-pink-700' : content.type === 'survey' ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}`}>{content.type.charAt(0).toUpperCase() + content.type.slice(1)}</span>
                      </h2>
                      <div className="text-gray-700 mb-2">{content.description}</div>
                    </div>
                    <div className="flex flex-wrap gap-2 items-center">
                      {content.contentUrl && <a href={content.contentUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline font-medium">View File/Video</a>}
                      {content.externalLink && <a href={content.externalLink} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline font-medium">External Link</a>}
                    </div>
                  </div>
                  {content.noticeText && <div className="mt-2 text-yellow-700 font-semibold flex items-center gap-2"><span></span>{content.noticeText}</div>}
                  {content.liveDate && <div className="mt-2 text-green-700 font-semibold flex items-center gap-2"><span></span>Live Date: {new Date(content.liveDate).toLocaleString()}</div>}
                  {content.dueDate && <div className="mt-2 text-red-700 font-semibold flex items-center gap-2"><span></span>Due Date: {new Date(content.dueDate).toLocaleDateString()}</div>}
                  {/* Quiz Section */}
                  {content.type === 'quiz' && content.questions && (
                    <div className="mt-4">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="text-indigo-700 font-semibold">Quiz:</span>
                        <span className="text-sm">{content.questions.length} questions</span>
                      </div>
                      {quizSubmission[content._id] ? (
                        <div className="flex gap-2">
                          <button
                            onClick={() => { setResultQuiz(content); setResultModalOpen(true); }}
                            className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 font-semibold shadow"
                          >
                            View Results
                          </button>
                        </div>
                      ) : (
                        <button onClick={() => openQuiz(content)} className="px-4 py-2 bg-primary text-white rounded hover:bg-secondary font-semibold shadow">Take Quiz</button>
                      )}
                      {userRole === 'Instructor' && (
                        <button onClick={() => openQuizAnalytics(content)} className="ml-4 px-3 py-1 bg-yellow-400 text-indigo-900 rounded hover:bg-yellow-500 font-semibold shadow">View Analytics</button>
                      )}
                    </div>
                  )}
                  {/* Assignment Section */}
                  {content.type === 'assignment' && (
                    <div className="mt-4 text-yellow-700 font-semibold flex items-center gap-2">
                      <span></span>Assignment
                    </div>
                  )}
                  {/* Poll Section */}
                  {content.type === 'poll' && content.pollOptions && (
                    <div className="mt-4 text-pink-700 font-semibold flex items-center gap-2">
                      <span></span>Poll Options: {content.pollOptions.map(opt => opt.option).join(', ')}
                    </div>
                  )}
                  {/* Survey Section */}
                  {content.type === 'survey' && content.surveyQuestions && (
                    <div className="mt-4 text-green-700 font-semibold flex items-center gap-2">
                      <span></span>Survey: {content.surveyQuestions.length} questions
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
        <Link to="/profile" className="inline-block mt-12 text-primary hover:underline font-semibold">&larr; Back to Profile</Link>
      </div>
      {/* Modern Footer */}
      <footer className="w-full mt-16 py-8 bg-white border-t text-center text-gray-500 text-sm rounded-t-3xl shadow-inner">
        &copy; {new Date().getFullYear()} CourseHub. Happy Learning! 
      </footer>
      {/* Quiz Modal */}
      <Transition appear show={quizModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setQuizModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-lg mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz: {activeQuiz?.title}</Dialog.Title>
                  {quizResult && quizResult.error && (
                    <div className="mb-4 text-red-600 font-semibold">{quizResult.error}</div>
                  )}
                  {quizResult && !quizResult.error ? (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">Your Score: {quizResult.score} / {activeQuiz.questions.length}</div>
                      <ul className="space-y-2">
                        {quizResult.answers.map((a, idx) => (
                          <li key={idx} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                            <span className="font-semibold">Q:</span> {a.question}<br />
                            <span className="font-semibold">Your Answer:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                            <span className="font-semibold">Correct:</span> {a.correct}
                          </li>
                        ))}
                      </ul>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setQuizModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  ) : (
                    <form onSubmit={handleQuizSubmit} className="space-y-6">
                      {activeQuiz?.questions?.map((q, idx) => (
                        <div key={idx} className="mb-4">
                          <div className="font-semibold mb-2">Q{idx + 1}: {q.question}</div>
                          <div className="space-y-1">
                            {q.options.map((opt, oidx) => (
                              <label key={oidx} className="flex items-center gap-2">
                                <input
                                  type="radio"
                                  name={`q_${idx}`}
                                  value={opt}
                                  checked={quizAnswers[q.question] === opt}
                                  onChange={() => handleQuizAnswer(q.question, opt)}
                                  className="form-radio text-primary"
                                  required
                                />
                                {opt}
                              </label>
                            ))}
                          </div>
                        </div>
                      ))}
                      <div className="flex justify-end gap-2 mt-4">
                        <button type="button" onClick={() => setQuizModalOpen(false)} className="px-4 py-2 bg-gray-200 rounded">Cancel</button>
                        <button type="submit" className="px-4 py-2 bg-primary text-white rounded" disabled={quizSubmitting}>{quizSubmitting ? 'Submitting...' : 'Submit Quiz'}</button>
                      </div>
                    </form>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* Quiz Results Modal */}
      <Transition appear show={resultModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setResultModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-lg mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz Results: {resultQuiz?.title}</Dialog.Title>
                  {resultQuiz && quizSubmission[resultQuiz._id] && (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">
                        Your Score: {quizSubmission[resultQuiz._id].score} / {resultQuiz.questions.length}
                      </div>
                      <ul className="space-y-2">
                        {quizSubmission[resultQuiz._id].answers.map((a, idx) => (
                          <li key={idx} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                            <span className="font-semibold">Q:</span> {a.question}<br />
                            <span className="font-semibold">Your Answer:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                            <span className="font-semibold">Correct:</span> {a.correct}
                          </li>
                        ))}
                      </ul>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setResultModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* Quiz Analytics Modal (Instructor) */}
      <Transition appear show={analyticsModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setAnalyticsModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-2xl mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz Analytics: {analyticsQuiz?.title}</Dialog.Title>
                  {quizAnalytics && quizAnalytics.error && (
                    <div className="mb-4 text-red-600 font-semibold">{quizAnalytics.error}</div>
                  )}
                  {quizAnalytics && !quizAnalytics.error ? (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">Total Submissions: {quizAnalytics.totalSubmissions}</div>
                      <div className="mb-4 text-blue-700 font-semibold">Average Score: {quizAnalytics.averageScore?.toFixed(2)}</div>
                      {/* Per-question stats */}
                      {quizAnalytics.questionStats && quizAnalytics.questionStats.length > 0 && (
                        <div className="mb-6">
                          <h3 className="font-bold text-lg mb-2 text-indigo-700">Per-Question Stats</h3>
                          <ul className="space-y-2">
                            {quizAnalytics.questionStats.map((qs, idx) => (
                              <li key={idx} className="bg-gray-50 rounded p-3 border">
                                <div className="font-semibold">Q{idx + 1}: {qs.question}</div>
                                <div className="text-green-700">Correct: {qs.correct}</div>
                                <div className="text-red-700">Incorrect: {qs.incorrect}</div>
                                {qs.mostCommonWrong && (
                                  <div className="text-yellow-700">Most Common Wrong Answer: {qs.mostCommonWrong}</div>
                                )}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                      <div className="overflow-x-auto">
                        <table className="min-w-full bg-white border rounded-xl overflow-hidden">
                          <thead className="bg-gray-100">
                            <tr>
                              <th className="border px-4 py-2">Student</th>
                              <th className="border px-4 py-2">Score</th>
                              <th className="border px-4 py-2">Submitted At</th>
                              <th className="border px-4 py-2">Answers</th>
                            </tr>
                          </thead>
                          <tbody>
                            {quizAnalytics.submissions.map((s, idx) => (
                              <tr key={idx}>
                                <td className="border px-4 py-2">{s.student?.firstName} {s.student?.lastName} <br /><span className="text-xs text-gray-500">{s.student?.email}</span></td>
                                <td className="border px-4 py-2">{s.score}</td>
                                <td className="border px-4 py-2">{new Date(s.submittedAt).toLocaleString()}</td>
                                <td className="border px-4 py-2">
                                  <ul className="text-xs">
                                    {s.answers.map((a, i) => (
                                      <li key={i} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                                        <span className="font-semibold">Q:</span> {a.question}<br />
                                        <span className="font-semibold">Ans:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                                        <span className="font-semibold">Correct:</span> {a.correct}
                                      </li>
                                    ))}
                                  </ul>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setAnalyticsModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  ) : (
                    <div className="text-gray-600">Loading...</div>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
};

export default CourseContents;
