import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [formData, setFormData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);

      axios
        .get(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setUserData(res.data.user);
          setFormData({
            firstName: res.data.user.firstName,
            lastName: res.data.user.lastName,
            bio: res.data.user.bio || '',
            contactInfo: res.data.user.contactInfo || '',
          });
        })
        .catch(() => toast.error('Failed to load user profile'));

      axios
        .get(`${API_CONFIG.BASE_URL}/courses/enrolled`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setEnrolledCourses(res.data.courses))
        .catch(() => toast.error('Failed to load enrolled courses'));
    } catch (err) {
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put(
        `${API_CONFIG.BASE_URL}/auth/update-profile`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserData(res.data.updatedUser);
      toast.success('Profile updated successfully!');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  const handleDeactivate = async () => {
    const confirm = window.confirm('Are you sure you want to deactivate your account?');
    if (!confirm) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${API_CONFIG.BASE_URL}/auth/deactivate`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Account deactivated');
      localStorage.removeItem('token');
      setTimeout(() => navigate('/signup'), 2000);
    } catch (err) {
      toast.error('Failed to deactivate account');
    }
  };

  if (!userData) return null;

  return (
    <section className="bg-gray-50 py-12 px-4 min-h-screen">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="max-w-5xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-8 mb-10 text-center">
          <img
            src={userData.profilePicture || 'https://via.placeholder.com/150?text=Profile'}
            alt={userData.fullName}
            className="w-24 h-24 rounded-full mx-auto mb-4 object-cover"
          />
          <h1 className="text-3xl font-bold text-gray-800 mb-2">
            {userData.firstName} {userData.lastName}
          </h1>
          <p className="text-gray-600">{userData.role}</p>
        </div>

        {/* Update Form */}
        <form
          onSubmit={handleProfileUpdate}
          className="bg-white rounded-xl shadow-lg p-8 mb-10"
        >
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Edit Profile</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">First Name</label>
              <input
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-indigo-200"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Last Name</label>
              <input
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-indigo-200"
                required
              />
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700">Bio</label>
              <textarea
                name="bio"
                value={formData.bio}
                onChange={handleInputChange}
                rows="3"
                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-indigo-200"
              />
            </div>
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700">Contact Info</label>
              <input
                name="contactInfo"
                value={formData.contactInfo}
                onChange={handleInputChange}
                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-indigo-200"
              />
            </div>
          </div>
          <div className="mt-6 flex justify-between items-center">
            <button
              type="submit"
              className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700 transition"
            >
              Update Profile
            </button>
            <button
              type="button"
              onClick={handleDeactivate}
              className="bg-red-600 text-white px-6 py-2 rounded hover:bg-red-700 transition"
            >
              Deactivate Account
            </button>
          </div>
        </form>

        {/* Enrolled Courses */}
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Enrolled Courses</h2>
          {enrolledCourses.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {enrolledCourses.map((course) => (
                <div
                  key={course._id}
                  className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-transform duration-300 hover:-translate-y-1"
                >
                  <img
                    src={course.image || 'https://via.placeholder.com/300x200?text=Course'}
                    alt={course.title}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-5">
                    <h3 className="text-lg font-semibold text-gray-800 mb-3">{course.title}</h3>
                    <div className="relative pt-1">
                      <div className="flex mb-2 items-center justify-between">
                        <span className="text-xs font-semibold text-gray-600">Progress</span>
                        <span className="text-xs font-semibold text-gray-600">
                          {course.progress || 0}%
                        </span>
                      </div>
                      <div className="overflow-hidden h-2 mb-4 flex rounded bg-gray-200">
                        <div
                          style={{ width: `${course.progress || 0}%` }}
                          className="bg-blue-600"
                        ></div>
                      </div>
                    </div>
                    <Link
                      to={`/courses/${course._id}`}
                      className="inline-block bg-blue-600 text-white text-sm px-4 py-2 rounded hover:bg-blue-700 transition"
                    >
                      Continue Learning
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600 text-center">No courses enrolled yet.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default Profile;
