import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import API_CONFIG from '../config/apiConfig';

const CourseDetails = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_CONFIG.BASE_URL}/courses/${id}`);
        setCourse(response.data.course);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch course details. Please try again later.');
        toast.error(err.response?.data?.message || 'Failed to fetch course details');
      } finally {
        setLoading(false);
      }
    };

    fetchCourse();
  }, [id]);

  if (loading) {
    return <p className="text-center mt-10 text-gray-600">Loading course details...</p>;
  }

  if (error) {
    return <p className="text-center mt-10 text-red-600">{error}</p>;
  }

  if (!course) {
    return <p className="text-center mt-10 text-gray-600">Course not found.</p>;
  }

  return (
    <div className="flex-1 bg-gray-100 py-12 px-4">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="container mx-auto max-w-4xl">
        {/* Back Link */}
        <Link
          to="/courses"
          className="inline-block mb-6 text-primary hover:underline"
        >
          &larr; Back to Courses
        </Link>

        {/* Course Details */}
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          {course.coverImage && (
            <img
              src={course.coverImage}
              alt={course.title}
              className="w-full h-64 object-cover"
            />
          )}
          <div className="p-6">
            <h1 className="text-3xl font-bold text-primary mb-4">{course.title}</h1>
            <p className="text-gray-600 mb-4">{course.description}</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div>
                <p className="text-sm text-gray-500">
                  <span className="font-semibold">Instructor:</span> {course.instructorName}
                </p>
                <p className="text-sm text-gray-500">
                  <span className="font-semibold">Category:</span> {course.category}
                </p>
                <p className="text-sm text-gray-500">
                  <span className="font-semibold">Level:</span> {course.level}
                </p>
                <p className="text-sm text-gray-500">
                  <span className="font-semibold">Price:</span>{' '}
                  {course.price === 0 ? 'Free' : `$${course.price.toFixed(2)}`}
                </p>
                <p className="text-sm text-gray-500">
                  <span className="font-semibold">Created At:</span>{' '}
                  {new Date(course.createdAt).toLocaleDateString()}
                </p>
              </div>
            </div>
            {course.sampleVideo && (
              <div className="mb-6">
                <h2 className="text-xl font-semibold text-primary mb-2">Sample Video</h2>
                <video
                  src={course.sampleVideo}
                  controls
                  className="w-full h-64 rounded-md"
                >
                  Your browser does not support the video tag.
                </video>
              </div>
            )}
            <Link
              to="/courses"
              className="inline-block bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition"
            >
              Explore More Courses
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
