import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CreateCourse = () => {
  const [formData, setFormData] = useState({
    instructorName: '',
    title: '',
    coverImage: '',
    category: 'Programming',
    price: '',
    level: 'Beginner',
    description: '',
    sampleVideo: '',
  });
  const [uploadingImage, setUploadingImage] = useState(false);
  const [uploadingVideo, setUploadingVideo] = useState(false);
  const navigate = useNavigate();

  // Initialize Cloudinary Upload Widget
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decoded = jwtDecode(token);
      if (decoded.role !== 'Instructor') {
        navigate('/profile');
        return;
      }
      setFormData((prev) => ({
        ...prev,
        instructorName: `${decoded.firstName} ${decoded.lastName}`,
      }));
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }

    // Load Cloudinary script
    const script = document.createElement('script');
    script.src = 'https://widget.cloudinary.com/v2.0/global/all.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const openCloudinaryWidget = (field) => {
    if (!window.cloudinary) {
      toast.error('Cloudinary widget failed to load');
      return;
    }
    const widget = window.cloudinary.createUploadWidget(
      {
        cloudName: process.env.REACT_APP_CLOUDINARY_CLOUD_NAME || 'your_cloud_name',
        uploadPreset: process.env.REACT_APP_CLOUDINARY_UPLOAD_PRESET || 'your_unsigned_preset',
        sources: ['local', 'url'],
        multiple: false,
        resourceType: field === 'coverImage' ? 'image' : 'video',
        maxFileSize: field === 'coverImage' ? 10485760 : 104857600, // 10MB for images, 100MB for videos
        clientAllowedFormats: field === 'coverImage' ? ['jpg', 'png', 'jpeg'] : ['mp4', 'webm'],
      },
      (error, result) => {
        if (!error && result && result.event === 'success') {
          setFormData((prev) => ({
            ...prev,
            [field]: result.info.secure_url,
          }));
          toast.success(`${field === 'coverImage' ? 'Cover image' : 'Sample video'} uploaded successfully`);
          field === 'coverImage' ? setUploadingImage(false) : setUploadingVideo(false);
        } else if (error) {
          toast.error(`Failed to upload ${field === 'coverImage' ? 'cover image' : 'sample video'}`);
          field === 'coverImage' ? setUploadingImage(false) : setUploadingVideo(false);
        }
      }
    );
    widget.open();
    field === 'coverImage' ? setUploadingImage(true) : setUploadingVideo(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      await axios.post(
        `${API_CONFIG.BASE_URL}/courses/create`,
        { ...formData, price: parseFloat(formData.price) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      toast.success('Course created successfully');
      navigate('/instructor/profile');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to create course');
    }
  };

  return (
    <section className="bg-gray-50 py-12 px-4 min-h-screen">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Create a New Course</h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                Course Title
              </label>
              <input
                id="title"
                name="title"
                type="text"
                value={formData.title}
                onChange={handleInputChange}
                className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                required
                aria-required="true"
              />
            </div>
            <div>
              <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                Category
              </label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                required
                aria-required="true"
              >
                <option value="Programming">Programming</option>
                <option value="Design">Design</option>
                <option value="Business">Business</option>
                <option value="Language">Language</option>
                <option value="Other">Other</option>
              </select>
            </div>
            <div>
              <label htmlFor="price" className="block text-sm font-medium text-gray-700">
                Price ($)
              </label>
              <input
                id="price"
                name="price"
                type="number"
                step="0.01"
                value={formData.price}
                onChange={handleInputChange}
                className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                required
                aria-required="true"
              />
            </div>
            <div>
              <label htmlFor="level" className="block text-sm font-medium text-gray-700">
                Level
              </label>
              <select
                id="level"
                name="level"
                value={formData.level}
                onChange={handleInputChange}
                className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                required
                aria-required="true"
              >
                <option value="Beginner">Beginner</option>
                <option value="Intermediate">Intermediate</option>
                <option value="Advanced">Advanced</option>
              </select>
            </div>
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                rows="4"
                required
                aria-required="true"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Cover Image</label>
              <button
                type="button"
                onClick={() => openCloudinaryWidget('coverImage')}
                className="mt-1 w-full bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition disabled:bg-gray-400"
                disabled={uploadingImage}
                aria-label="Upload Cover Image"
              >
                {uploadingImage ? 'Uploading...' : 'Upload Cover Image'}
              </button>
              {formData.coverImage && (
                <img
                  src={formData.coverImage}
                  alt="Cover Preview"
                  className="mt-2 w-full h-32 object-cover rounded-md"
                />
              )}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Sample Video</label>
              <button
                type="button"
                onClick={() => openCloudinaryWidget('sampleVideo')}
                className="mt-1 w-full bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition disabled:bg-gray-400"
                disabled={uploadingVideo}
                aria-label="Upload Sample Video"
              >
                {uploadingVideo ? 'Uploading...' : 'Upload Sample Video'}
              </button>
              {formData.sampleVideo && (
                <video
                  src={formData.sampleVideo}
                  controls
                  className="mt-2 w-full h-32 object-cover rounded-md"
                />
              )}
            </div>
            <div className="flex justify-end space-x-4">
              <button
                type="button"
                onClick={() => navigate('/instructor/profile')}
                className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400 transition"
                aria-label="Cancel"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition"
                aria-label="Create Course"
              >
                Create Course
              </button>
            </div>
          </form>
        </div>
      </div>
    </section>
  );
};

export default CreateCourse;
