import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AdminDashboard = () => {
  const [adminData, setAdminData] = useState(null);
  const [userList, setUserList] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);
      if (decoded.role !== 'Admin') {
        navigate('/');
        return;
      }

      // Fetch admin details
      axios
        .get(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setAdminData(res.data.user))
        .catch(() => toast.error('Failed to fetch admin details'));

      // Fetch user list
      axios
        .get(`${API_CONFIG.BASE_URL}/admin/users`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setUserList(res.data.users))
        .catch(() => toast.error('Failed to fetch users'));

    } catch (error) {
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  const handleDelete = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`${API_CONFIG.BASE_URL}/admin/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      toast.success('User deleted successfully!');
      setUserList(userList.filter((user) => user._id !== userId));
    } catch (error) {
      toast.error('Failed to delete user');
    }
  };

  return (
    <section className="bg-gray-50 py-12 px-4 min-h-screen">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />

      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-8 mb-10 text-center">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">
            Welcome, {adminData ? `${adminData.firstName} ${adminData.lastName}` : '...'}
          </h1>
          <p className="text-gray-600 mb-4">Admin Dashboard</p>
        </div>

        {/* Admin Details */}
        {adminData && (
          <div className="bg-white rounded-xl shadow-lg p-8 mb-10">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Admin Details</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <p className="text-gray-600 mb-2">
                  <span className="font-semibold">Email:</span> {adminData.email}
                </p>
                <p className="text-gray-600 mb-2">
                  <span className="font-semibold">Role:</span> {adminData.role}
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Manage Users */}
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Manage Users</h2>
          {userList.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {userList.map((user) => (
                <div
                  key={user._id}
                  className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-transform duration-300 hover:-translate-y-1"
                >
                  <div className="p-5">
                    <h3 className="text-lg font-semibold text-gray-800 mb-3">
                      {user.firstName} {user.lastName}
                    </h3>
                    <p className="text-gray-600 mb-2">
                      <span className="font-semibold">Email:</span> {user.email}
                    </p>
                    <p className="text-gray-600 mb-3">
                      <span className="font-semibold">Role:</span> {user.role}
                    </p>
                    <button
                      onClick={() => handleDelete(user._id)}
                      className="inline-block bg-red-600 text-white text-sm px-4 py-2 rounded hover:bg-red-700 transition"
                    >
                      Delete User
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600 text-center">No users to manage.</p>
          )}
        </div>

        {/* Footer */}
        <footer className="mt-10 text-center text-gray-600">
          <p>© 2025 EduVerse. All rights reserved.</p>
          <div className="mt-4 space-x-4">
            <Link to="/about" className="text-blue-600 hover:underline">About</Link>
            <Link to="/contact" className="text-blue-600 hover:underline">Contact</Link>
            <Link to="/privacy" className="text-blue-600 hover:underline">Privacy Policy</Link>
          </div>
        </footer>
      </div>
    </section>
  );
};

export default AdminDashboard;
