import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const InstructorProfile = () => {
  const [userData, setUserData] = useState(null);
  const [createdCourses, setCreatedCourses] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bio: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);
      if (decoded.role !== 'Instructor') {
        navigate('/profile');
        return;
      }

      // Fetch user profile
      axios
        .get(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setUserData(res.data.user);
          setFormData({
            firstName: res.data.user.firstName,
            lastName: res.data.user.lastName,
            email: res.data.user.email,
            bio: res.data.user.bio || '',
          });
        })
        .catch(() => {
          toast.error('Failed to load Instructor profile');
          localStorage.removeItem('token');
          navigate('/login');
        });

      // Fetch created courses
      axios
        .get(`${API_CONFIG.BASE_URL}/courses/created`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setCreatedCourses(res.data.courses);
        })
        .catch(() => {
          toast.error('Failed to load created courses');
          setCreatedCourses([]);
        });
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(
        `${API_CONFIG.BASE_URL}/auth/update`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserData(response.data.user);
      setIsModalOpen(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  if (!userData) return null;

  return (
    <section className="bg-gray-50 py-12 px-4 min-h-screen">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-8 mb-10 text-center">
          <div className="w-24 h-24 rounded-full mx-auto mb-4 bg-gray-200 flex items-center justify-center">
            <span className="text-3xl text-gray-600">
              {userData.firstName[0]}{userData.lastName[0]}
            </span>
          </div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">
            {userData.firstName} {userData.lastName}
          </h1>
          <p className="text-gray-600 mb-4">Instructor</p>
          <div className="flex justify-center space-x-4">
            <button
              onClick={() => setIsModalOpen(true)}
              className="bg-primary text-white px-6 py-2 rounded-md hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 transition"
              aria-label="Edit Profile"
            >
              Edit Profile
            </button>
            <Link
              to="/instructor/create-course"
              className="bg-primary text-white px-6 py-2 rounded-md hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 transition"
              aria-label="Create New Course"
            >
              New Course
            </Link>
          </div>
        </div>

        {/* Instructor Details */}
        <div className="bg-white rounded-xl shadow-lg p-8 mb-10">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Instructor Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Email:</span> {userData.email}
              </p>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Earnings:</span> ${userData.earnings?.toFixed(2) || '0.00'}
              </p>
            </div>
            <div>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Bio:</span> {userData.bio || 'No bio provided.'}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Joined:</span>{' '}
                {new Date(userData.createdDate).toLocaleDateString()}
              </p>
            </div>
          </div>
        </div>

        {/* Created Courses */}
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Created Courses</h2>
          {createdCourses.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {createdCourses.map((course) => (
                <div
                  key={course._id}
                  className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-transform duration-300 hover:-translate-y-1"
                >
                  <img
                    src={course.coverImage || 'https://via.placeholder.com/300x200?text=Course'}
                    alt={course.title}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-5">
                    <h3 className="text-lg font-semibold text-gray-800 mb-2">{course.title}</h3>
                    <p className="text-gray-600 mb-2 text-sm"><span className="font-semibold">Category:</span> {course.category}</p>
                    <p className="text-gray-600 mb-2 text-sm"><span className="font-semibold">Level:</span> {course.level}</p>
                    <p className="text-gray-600 mb-3 text-sm"><span className="font-semibold">Price:</span> ${course.price.toFixed(2)}</p>
                    <p className="text-gray-600 mb-3 text-sm line-clamp-2">{course.description}</p>
                    {course.sampleVideo && (
                      <a
                        href={course.sampleVideo}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-primary hover:underline text-sm"
                        aria-label={`Watch sample video for ${course.title}`}
                      >
                        Watch Sample Video
                      </a>
                    )}
                    <Link
                      to={`/courses/${course._id}`}
                      className="inline-block bg-primary text-white text-sm px-4 py-2 rounded mt-3 hover:bg-secondary transition"
                      aria-label={`View ${course.title}`}
                    >
                      View Course
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600 text-center">No courses created yet.</p>
          )}
        </div>

        {/* Edit Profile Modal */}
        {isModalOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
              <h2 className="text-2xl font-bold text-center text-primary mb-6">Edit Instructor Profile</h2>
              <form onSubmit={handleUpdate} className="space-y-6">
                <div>
                  <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
                    First Name
                  </label>
                  <input
                    id="firstName"
                    name="firstName"
                    type="text"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="lastName" className="block text-sm font-medium text-gray-700">
                    Last Name
                  </label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="bio" className="block text-sm font-medium text-gray-700">
                    Bio
                  </label>
                  <textarea
                    id="bio"
                    name="bio"
                    value={formData.bio}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    rows="4"
                    placeholder="Tell us about your expertise and experience"
                  />
                </div>
                <div className="flex justify-end space-x-4">
                  <button
                    type="button"
                    onClick={() => setIsModalOpen(false)}
                    className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400 transition"
                    aria-label="Cancel"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition"
                    aria-label="Save Changes"
                  >
                    Save Changes
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Footer */}
        <footer className="mt-10 text-center text-gray-600">
          <p>© 2025 EduVerse. All rights reserved.</p>
          <div className="mt-4 space-x-4">
            <Link to="/about" className="text-blue-600 hover:underline">About</Link>
            <Link to="/contact" className="text-blue-600 hover:underline">Contact</Link>
            <Link to="/privacy" className="text-blue-600 hover:underline">Privacy Policy</Link>
          </div>
        </footer>
      </div>
    </section>
  );
};

export default InstructorProfile;
