import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import API_CONFIG from '../config/apiConfig';
import { jwtDecode } from 'jwt-decode';

const getRole = () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) return null;
    return jwtDecode(token).role;
  } catch {
    return null;
  }
};

const getUserId = () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) return null;
    return jwtDecode(token).id;
  } catch {
    return null;
  }
};

const defaultForm = {
  type: 'video',
  title: '',
  description: '',
  videoUrl: '',
  slideUrl: '',
  resourceUrl: '',
  quiz: [],
  visible: true,
};

function emptyQuizQuestion() {
  return {
    question: '',
    options: ['', ''],
    correctAnswer: '',
    feedback: '',
  };
}

const CourseContentPage = () => {
  const { courseId } = useParams();
  const [content, setContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState(defaultForm);
  const [saving, setSaving] = useState(false);
  const role = getRole();
  const userId = getUserId();
  const [progress, setProgress] = useState({ completedContent: [], completedAt: null });
  const [quizModal, setQuizModal] = useState({ open: false, quiz: null, contentId: null });
  const [quizAnswers, setQuizAnswers] = useState([]);
  const [quizResult, setQuizResult] = useState(null);
  const [quizError, setQuizError] = useState('');
  const [showCert, setShowCert] = useState(false);
  const certRef = useRef();
  const [courseDetails, setCourseDetails] = useState(null);

  const fetchContent = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_CONFIG.BASE_URL}/courses/${courseId}/content`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Failed to load content');
      setContent(data.content);
    } catch (err) {
      setError(err.message || 'Failed to load content');
    } finally {
      setLoading(false);
    }
  };

  const fetchProgress = async () => {
    if (role !== 'Student') return;
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_CONFIG.BASE_URL}/course-content/${courseId}/progress`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok && data.progress) setProgress(data.progress);
      else setProgress({ completedContent: [], completedAt: null });
    } catch {
      setProgress({ completedContent: [], completedAt: null });
    }
  };

  const fetchCourseDetails = async () => {
    try {
      const res = await fetch(`${API_CONFIG.BASE_URL}/courses/${courseId}`);
      const data = await res.json();
      if (res.ok && data.course) setCourseDetails(data.course);
      else setCourseDetails(null);
    } catch {
      setCourseDetails(null);
    }
  };

  useEffect(() => { fetchContent(); fetchProgress(); fetchCourseDetails(); }, [courseId]);

  const handleFormChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleQuizChange = (qIdx, field, value) => {
    setForm(f => {
      const quiz = [...f.quiz];
      if (field === 'question' || field === 'correctAnswer' || field === 'feedback') {
        quiz[qIdx][field] = value;
      } else if (field.startsWith('option')) {
        const optIdx = parseInt(field.split('-')[1], 10);
        quiz[qIdx].options[optIdx] = value;
      }
      return { ...f, quiz };
    });
  };

  const addQuizQuestion = () => setForm(f => ({ ...f, quiz: [...f.quiz, emptyQuizQuestion()] }));
  const removeQuizQuestion = idx => setForm(f => ({ ...f, quiz: f.quiz.filter((_, i) => i !== idx) }));
  const addQuizOption = qIdx => setForm(f => {
    const quiz = [...f.quiz];
    quiz[qIdx].options.push('');
    return { ...f, quiz };
  });
  const removeQuizOption = (qIdx, optIdx) => setForm(f => {
    const quiz = [...f.quiz];
    quiz[qIdx].options.splice(optIdx, 1);
    return { ...f, quiz };
  });

  const handleEdit = item => {
    setEditId(item._id);
    setForm({
      type: item.type,
      title: item.title,
      description: item.description,
      videoUrl: item.videoUrl || '',
      slideUrl: item.slideUrl || '',
      resourceUrl: item.resourceUrl || '',
      quiz: item.quiz ? item.quiz.map(q => ({ ...q, options: [...q.options] })) : [],
      visible: item.visible,
    });
    setShowModal(true);
  };

  const handleDelete = async contentId => {
    if (!window.confirm('Delete this content?')) return;
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_CONFIG.BASE_URL}/courses/content/${contentId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchContent();
    } catch {}
  };

  const handleAddOrEditContent = async e => {
    e.preventDefault();
    setSaving(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const url = editId
        ? `${API_CONFIG.BASE_URL}/courses/content/${editId}`
        : `${API_CONFIG.BASE_URL}/courses/${courseId}/content`;
      const method = editId ? 'PUT' : 'POST';
      const res = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Failed to save content');
      setShowModal(false);
      setForm(defaultForm);
      setEditId(null);
      fetchContent();
    } catch (err) {
      setError(err.message || 'Failed to save content');
    } finally {
      setSaving(false);
    }
  };

  const handleToggleVisibility = async (contentId, visible) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_CONFIG.BASE_URL}/courses/content/${contentId}/visibility`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ visible: !visible }),
      });
      fetchContent();
    } catch {}
  };

  const markCompleted = async contentId => {
    if (role !== 'Student') return;
    try {
      const token = localStorage.getItem('token');
      await fetch(`${API_CONFIG.BASE_URL}/course-content/${courseId}/content/${contentId}/complete`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProgress();
    } catch {}
  };

  const openQuiz = (quiz, contentId) => {
    setQuizModal({ open: true, quiz, contentId });
    setQuizAnswers(Array(quiz.length).fill(''));
    setQuizResult(null);
    setQuizError('');
  };

  const closeQuiz = () => {
    setQuizModal({ open: false, quiz: null, contentId: null });
    setQuizAnswers([]);
    setQuizResult(null);
    setQuizError('');
  };

  const handleQuizAnswer = (idx, value) => {
    setQuizAnswers(a => a.map((ans, i) => (i === idx ? value : ans)));
  };

  const submitQuiz = async () => {
    setQuizError('');
    if (quizAnswers.some(a => !a)) {
      setQuizError('Please answer all questions.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_CONFIG.BASE_URL}/course-content/content/${quizModal.contentId}/submit-quiz`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({ answers: quizAnswers }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Failed to submit quiz');
      setQuizResult(data);
      markCompleted(quizModal.contentId);
      fetchProgress();
    } catch (err) {
      setQuizError(err.message || 'Failed to submit quiz');
    }
  };

  if (loading) return <div className="min-h-[60vh] flex items-center justify-center text-primary text-xl">Loading...</div>;
  if (error) return <div className="min-h-[60vh] flex items-center justify-center text-red-600 text-xl">{error}</div>;

  return (
    <div className="min-h-[80vh] bg-gray-50 py-12 px-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-extrabold text-primary mb-8 text-center">Course Content</h1>
        {role === 'Instructor' && (
          <div className="mb-8 flex justify-end">
            <button onClick={() => { setShowModal(true); setEditId(null); setForm(defaultForm); }} className="bg-primary text-white px-6 py-2 rounded-lg font-bold hover:bg-secondary transition">+ Add Content</button>
          </div>
        )}
        {role === 'Student' && content.length > 0 && (
          <div className="mb-8">
            <div className="w-full bg-gray-200 rounded-full h-4 mb-2">
              <div
                className="bg-primary h-4 rounded-full transition-all"
                style={{ width: `${Math.round((progress.completedContent?.length || 0) / content.length * 100)}%` }}
              />
            </div>
            <div className="flex justify-between text-sm text-gray-600">
              <span>Progress: {progress.completedContent?.length || 0} / {content.length}</span>
              {progress.completedAt && (
                <span className="text-green-600 font-bold">Completed! <button className="ml-2 bg-yellow-400 text-indigo-900 px-3 py-1 rounded font-semibold" onClick={() => setShowCert(true)}>Download Certificate</button></span>
              )}
            </div>
          </div>
        )}
        {content.length === 0 ? (
          <p className="text-center text-gray-500">No content yet.</p>
        ) : (
          <ol className="space-y-6">
            {content.map((item, idx) => (
              <li key={item._id} className="bg-white rounded-xl shadow p-6">
                <div className="flex items-center justify-between mb-2">
                  <span className="font-bold text-lg text-primary">{idx + 1}. {item.title}</span>
                  {role === 'Instructor' && (
                    <div className="flex gap-2 items-center">
                      <span className={`px-3 py-1 rounded-full text-xs font-semibold ${item.visible ? 'bg-green-100 text-green-700' : 'bg-gray-200 text-gray-500'}`}>{item.visible ? 'Visible' : 'Hidden'}</span>
                      <button onClick={() => handleToggleVisibility(item._id, item.visible)} className="text-xs px-3 py-1 rounded bg-gray-100 hover:bg-gray-200 font-semibold border border-gray-300">{item.visible ? 'Hide' : 'Unhide'}</button>
                      <button onClick={() => handleEdit(item)} className="text-xs px-3 py-1 rounded bg-blue-100 hover:bg-blue-200 font-semibold border border-blue-300">Edit</button>
                      <button onClick={() => handleDelete(item._id)} className="text-xs px-3 py-1 rounded bg-red-100 hover:bg-red-200 font-semibold border border-red-300">Delete</button>
                    </div>
                  )}
                </div>
                <div className="mb-2 text-gray-700">{item.description}</div>
                {item.type === 'video' && item.videoUrl && (
                  <video
                    src={item.videoUrl}
                    controls
                    className="w-full h-64 rounded-lg border mb-2"
                    onPlay={() => role === 'Student' && !progress.completedContent?.includes(item._id) && markCompleted(item._id)}
                  />
                )}
                {item.type === 'slide' && item.slideUrl && (
                  <a
                    href={item.slideUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 underline"
                    onClick={() => role === 'Student' && !progress.completedContent?.includes(item._id) && markCompleted(item._id)}
                  >View Slides</a>
                )}
                {item.type === 'resource' && item.resourceUrl && (
                  <a
                    href={item.resourceUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 underline"
                    onClick={() => role === 'Student' && !progress.completedContent?.includes(item._id) && markCompleted(item._id)}
                  >Resource Link</a>
                )}
                {item.type === 'quiz' && (
                  <div className="mt-2 text-sm text-indigo-700">
                    Quiz ({item.quiz.length} questions)
                    {role === 'Student' && !progress.completedContent?.includes(item._id) && (
                      <button
                        className="ml-4 bg-primary text-white px-3 py-1 rounded text-xs font-semibold hover:bg-secondary"
                        onClick={() => openQuiz(item.quiz, item._id)}
                      >Take Quiz</button>
                    )}
                    {role === 'Student' && progress.completedContent?.includes(item._id) && (
                      <span className="ml-4 text-green-600 font-semibold">Completed</span>
                    )}
                  </div>
                )}
                {role === 'Student' && progress.completedContent?.includes(item._id) && item.type !== 'quiz' && (
                  <div className="mt-2 text-green-600 text-xs font-semibold">Completed</div>
                )}
              </li>
            ))}
          </ol>
        )}
      </div>
      {/* Add/Edit Content Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-lg relative">
            <button onClick={() => { setShowModal(false); setEditId(null); setForm(defaultForm); }} className="absolute top-2 right-2 text-gray-400 hover:text-gray-700 text-2xl">&times;</button>
            <h2 className="text-2xl font-bold text-primary mb-6">{editId ? 'Edit Content' : 'Add Content'}</h2>
            <form onSubmit={handleAddOrEditContent} className="space-y-4">
              <select name="type" value={form.type} onChange={handleFormChange} className="w-full px-4 py-2 rounded border border-gray-300">
                <option value="video">Video</option>
                <option value="slide">Lecture Slide</option>
                <option value="resource">Resource Link</option>
                <option value="quiz">Quiz</option>
              </select>
              <input type="text" name="title" value={form.title} onChange={handleFormChange} placeholder="Title" className="w-full px-4 py-2 rounded border border-gray-300" required />
              <textarea name="description" value={form.description} onChange={handleFormChange} placeholder="Description" className="w-full px-4 py-2 rounded border border-gray-300" />
              {form.type === 'video' && (
                <input type="text" name="videoUrl" value={form.videoUrl} onChange={handleFormChange} placeholder="Video URL" className="w-full px-4 py-2 rounded border border-gray-300" required />
              )}
              {form.type === 'slide' && (
                <input type="text" name="slideUrl" value={form.slideUrl} onChange={handleFormChange} placeholder="Slide URL" className="w-full px-4 py-2 rounded border border-gray-300" required />
              )}
              {form.type === 'resource' && (
                <input type="text" name="resourceUrl" value={form.resourceUrl} onChange={handleFormChange} placeholder="Resource Link URL" className="w-full px-4 py-2 rounded border border-gray-300" required />
              )}
              {form.type === 'quiz' && (
                <div className="space-y-4">
                  <div className="flex justify-between items-center mb-2">
                    <span className="font-semibold text-primary">Quiz Questions</span>
                    <button type="button" onClick={addQuizQuestion} className="bg-green-100 text-green-700 px-3 py-1 rounded text-xs font-bold">+ Add Question</button>
                  </div>
                  {form.quiz.map((q, qIdx) => (
                    <div key={qIdx} className="border rounded p-3 mb-2 bg-gray-50">
                      <input type="text" value={q.question} onChange={e => handleQuizChange(qIdx, 'question', e.target.value)} placeholder="Question" className="w-full px-2 py-1 mb-2 rounded border border-gray-300" required />
                      <div className="mb-2">
                        <span className="text-xs font-semibold">Options:</span>
                        {q.options.map((opt, optIdx) => (
                          <div key={optIdx} className="flex items-center gap-2 mt-1">
                            <input type="text" value={opt} onChange={e => handleQuizChange(qIdx, `option-${optIdx}`, e.target.value)} placeholder={`Option ${optIdx + 1}`} className="px-2 py-1 rounded border border-gray-300 flex-1" required />
                            {q.options.length > 2 && (
                              <button type="button" onClick={() => removeQuizOption(qIdx, optIdx)} className="text-red-500 text-xs">Remove</button>
                            )}
                          </div>
                        ))}
                        <button type="button" onClick={() => addQuizOption(qIdx)} className="text-xs text-green-700 ml-2">+ Add Option</button>
                      </div>
                      <input type="text" value={q.correctAnswer} onChange={e => handleQuizChange(qIdx, 'correctAnswer', e.target.value)} placeholder="Correct Answer (must match an option)" className="w-full px-2 py-1 mb-2 rounded border border-gray-300" required />
                      <input type="text" value={q.feedback} onChange={e => handleQuizChange(qIdx, 'feedback', e.target.value)} placeholder="Feedback (optional)" className="w-full px-2 py-1 mb-2 rounded border border-gray-300" />
                      <button type="button" onClick={() => removeQuizQuestion(qIdx)} className="text-xs text-red-500 mt-2">Remove Question</button>
                    </div>
                  ))}
                </div>
              )}
              <div className="flex gap-4 mt-4">
                <button type="button" onClick={() => { setShowModal(false); setEditId(null); setForm(defaultForm); }} className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300">Cancel</button>
                <button type="submit" disabled={saving} className="bg-primary text-white px-4 py-2 rounded hover:bg-secondary font-bold">{saving ? 'Saving...' : (editId ? 'Save Changes' : 'Add Content')}</button>
              </div>
              {error && <p className="text-red-600 text-center mt-2">{error}</p>}
            </form>
          </div>
        </div>
      )}
      {quizModal.open && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-lg relative">
            <button onClick={closeQuiz} className="absolute top-2 right-2 text-gray-400 hover:text-gray-700 text-2xl">&times;</button>
            <h2 className="text-2xl font-bold text-primary mb-6">Quiz</h2>
            {quizResult ? (
              <div>
                <div className="mb-4 text-lg font-bold text-green-700">Score: {quizResult.score} / {quizResult.total}</div>
                <ul className="mb-4">
                  {quizResult.feedback.map((f, i) => (
                    <li key={i} className={f.correct ? 'text-green-700' : 'text-red-600'}>
                      Q{i + 1}: {f.question} <br />
                      {f.correct ? 'Correct!' : 'Incorrect.'} {f.feedback && <span className="italic">Feedback: {f.feedback}</span>}
                    </li>
                  ))}
                </ul>
                <button onClick={closeQuiz} className="bg-primary text-white px-4 py-2 rounded font-bold">Close</button>
              </div>
            ) : (
              <form onSubmit={e => { e.preventDefault(); submitQuiz(); }} className="space-y-4">
                {quizModal.quiz.map((q, i) => (
                  <div key={i} className="mb-4">
                    <div className="font-semibold mb-2">Q{i + 1}: {q.question}</div>
                    {q.options.map((opt, j) => (
                      <label key={j} className="block mb-1">
                        <input
                          type="radio"
                          name={`q${i}`}
                          value={opt}
                          checked={quizAnswers[i] === opt}
                          onChange={() => handleQuizAnswer(i, opt)}
                          className="mr-2"
                        />
                        {opt}
                      </label>
                    ))}
                  </div>
                ))}
                {quizError && <div className="text-red-600 mb-2">{quizError}</div>}
                <div className="flex gap-4 mt-4">
                  <button type="button" onClick={closeQuiz} className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300">Cancel</button>
                  <button type="submit" className="bg-primary text-white px-4 py-2 rounded font-bold">Submit Quiz</button>
                </div>
              </form>
            )}
          </div>
        </div>
      )}
      {showCert && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-xl relative" ref={certRef}>
            <button onClick={() => setShowCert(false)} className="absolute top-2 right-2 text-gray-400 hover:text-gray-700 text-2xl">&times;</button>
            <div className="border-4 border-yellow-400 rounded-lg p-8 text-center">
              <img src="https://cdn-icons-png.flaticon.com/512/3135/3135768.png" alt="Platform Logo" className="mx-auto mb-4 w-20 h-20 object-contain" />
              <h2 className="text-3xl font-extrabold text-primary mb-2">Certificate of Completion</h2>
              <p className="text-lg mb-6">This certifies that</p>
              <p className="text-2xl font-bold text-indigo-900 mb-2">{jwtDecode(localStorage.getItem('token')).firstName} {jwtDecode(localStorage.getItem('token')).lastName}</p>
              <p className="text-lg mb-6">has successfully completed the course</p>
              <p className="text-xl font-semibold text-primary mb-2">{courseDetails?.title || 'Course'}</p>
              <p className="text-lg mb-6">on {progress.completedAt && new Date(progress.completedAt).toLocaleDateString()}</p>
              {courseDetails?.instructorName && (
                <p className="text-md mb-2">Instructor: <span className="font-semibold">{courseDetails.instructorName}</span></p>
              )}
              <div className="mt-8 flex justify-center">
                <button
                  className="bg-primary text-white px-6 py-2 rounded font-bold hover:bg-secondary"
                  onClick={() => {
                    window.print();
                  }}
                >Print / Save as PDF</button>
              </div>
              <div className="mt-8 text-gray-400 text-xs">EduVerse &bull; {new Date().getFullYear()}</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CourseContentPage;
