import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import EditCourseModal from '../components/EditCourseModal';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';
import { Dialog, Transition } from '@headlessui/react';
import { Fragment } from 'react';

const InstructorProfile = () => {
  const [userData, setUserData] = useState(null);
  const [createdCourses, setCreatedCourses] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bio: '',
  });
  const [enrolledStudents, setEnrolledStudents] = useState({});
  const [analytics, setAnalytics] = useState([]);
  const [totalEarnings, setTotalEarnings] = useState(0);
  const [courseQuizzes, setCourseQuizzes] = useState({});
  const [quizResultsModal, setQuizResultsModal] = useState({ open: false, quiz: null, analytics: null });
  const navigate = useNavigate();

  const fetchCourses = async (token) => {
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/courses/created`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCreatedCourses(res.data.courses);
    } catch {
      setCreatedCourses([]);
    }
  };

  // Fetch students for a course
  const fetchEnrolledStudents = async (courseId, token) => {
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/enrollments/course/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEnrolledStudents((prev) => ({ ...prev, [courseId]: res.data.enrollments.map(e => e.student) }));
    } catch {
      setEnrolledStudents((prev) => ({ ...prev, [courseId]: [] }));
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);
      if (decoded.role !== 'Instructor') {
        navigate('/profile');
        return;
      }

      // Fetch user profile
      axios
        .get(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setUserData(res.data.user);
          setFormData({
            firstName: res.data.user.firstName,
            lastName: res.data.user.lastName,
            email: res.data.user.email,
            bio: res.data.user.bio || '',
          });
        })
        .catch(() => {
          toast.error('Failed to load Instructor profile');
          localStorage.removeItem('token');
          navigate('/login');
        });

      // Fetch created courses
      fetchCourses(token).then(() => {
        // After fetching courses, fetch students for each course
        createdCourses.forEach((course) => {
          fetchEnrolledStudents(course._id, token);
        });
      });
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  // Fetch analytics when userData is available
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (userData && userData.id) {
      axios
        .get(`${API_CONFIG.BASE_URL}/enrollments/analytics/instructor/${userData.id}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => {
          setAnalytics(res.data.analytics);
          setTotalEarnings(res.data.totalEarnings || 0);
        })
        .catch(() => {
          setAnalytics([]);
          setTotalEarnings(0);
        });
    }
  }, [userData]);

  // Fetch quizzes for each course after createdCourses is loaded
  useEffect(() => {
    const token = localStorage.getItem('token');
    createdCourses.forEach((course) => {
      axios.get(`${API_CONFIG.BASE_URL}/course-content/course/${course._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      }).then(res => {
        setCourseQuizzes(prev => ({ ...prev, [course._id]: res.data.contents.filter(c => c.type === 'quiz') }));
      }).catch(() => {
        setCourseQuizzes(prev => ({ ...prev, [course._id]: [] }));
      });
    });
  }, [createdCourses]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(
        `${API_CONFIG.BASE_URL}/auth/update`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserData(response.data.user);
      setIsModalOpen(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setIsModalOpen(true);
  };

  const handleSaveCourse = async (form) => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      if (value !== null && value !== undefined) formData.append(key, value);
    });
    try {
      await axios.put(`${API_CONFIG.BASE_URL}/courses/${editingCourse._id}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Course updated successfully!');
      setIsModalOpen(false);
      setEditingCourse(null);
      fetchCourses(token);
    } catch (err) {
      toast.error('Failed to update course.');
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`${API_CONFIG.BASE_URL}/courses/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Course deleted successfully!');
      fetchCourses(token);
    } catch (err) {
      toast.error('Failed to delete course.');
    }
  };

  const handleViewQuizResults = async (quiz) => {
    setQuizResultsModal({ open: true, quiz, analytics: null });
    const token = localStorage.getItem('token');
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/quiz-submissions/analytics/${quiz._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuizResultsModal({ open: true, quiz, analytics: res.data });
    } catch (err) {
      setQuizResultsModal({ open: true, quiz, analytics: { error: 'Failed to load results' } });
    }
  };

  if (!userData) return null;

  return (
    <section className="min-h-screen px-4 py-12 bg-gray-50">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <EditCourseModal
        open={isModalOpen}
        onClose={() => { setIsModalOpen(false); setEditingCourse(null); }}
        course={editingCourse}
        onSave={handleSaveCourse}
      />
      <div className="mx-auto max-w-7xl">
        {/* Header */}
        <div className="p-8 mb-10 text-center bg-white shadow-lg rounded-xl">
          <div className="flex items-center justify-center w-24 h-24 mx-auto mb-4 bg-gray-200 rounded-full">
            <span className="text-3xl text-gray-600">
              {userData.firstName[0]}{userData.lastName[0]}
            </span>
          </div>
          <h1 className="mb-2 text-3xl font-bold text-gray-800">
            {userData.firstName} {userData.lastName}
          </h1>
          <p className="mb-4 text-gray-600">Instructor</p>
          <div className="flex justify-center space-x-4">
            <button
              onClick={() => setIsModalOpen(true)}
              className="px-6 py-2 text-white transition rounded-md bg-primary hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
              aria-label="Edit Profile"
            >
              Edit Profile
            </button>
            <Link
              to="/instructor/create-course"
              className="px-6 py-2 text-white transition rounded-md bg-primary hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
              aria-label="Create New Course"
            >
              New Course
            </Link>
          </div>
        </div>

        {/* Instructor Details */}
        <div className="p-8 mb-10 bg-white shadow-lg rounded-xl">
          <h2 className="mb-6 text-2xl font-semibold text-gray-800">Instructor Details</h2>
          <div className="grid grid-cols-1 gap-6 md:grid-cols-2">
            <div>
              <p className="mb-2 text-gray-600">
                <span className="font-semibold">Email:</span> {userData.email}
              </p>
              <p className="mb-2 text-gray-600">
                <span className="font-semibold">Earnings:</span> ${userData.earnings?.toFixed(2) || '0.00'}
              </p>
            </div>
            <div>
              <p className="mb-2 text-gray-600">
                <span className="font-semibold">Bio:</span> {userData.bio || 'No bio provided.'}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Joined:</span>{' '}
                {new Date(userData.createdDate).toLocaleDateString()}
              </p>
            </div>
          </div>
        </div>

        {/* Enrollment Analytics Dashboard */}
        {analytics.length > 0 && (
          <div className="p-8 mb-10 bg-white shadow-lg rounded-xl">
            <h2 className="mb-6 text-2xl font-semibold text-gray-800">Instructor Analytics</h2>
            <div className="grid grid-cols-1 gap-6 md:grid-cols-2">
              <div>
                <p className="mb-2 text-lg font-bold text-green-700">
                  Total Earnings: ${totalEarnings?.toFixed(2) || '0.00'}
                </p>
                <p className="mb-2 text-lg font-bold text-indigo-700">
                  Total Enrollments: {analytics.reduce((sum, c) => sum + c.total, 0)}
                </p>
                <p className="mb-2 text-lg font-bold text-blue-700">
                  Paid Enrollments: {analytics.reduce((sum, c) => sum + c.paid, 0)}
                </p>
                <p className="mb-2 text-lg font-bold text-gray-700">
                  Free Enrollments: {analytics.reduce((sum, c) => sum + c.free, 0)}
                </p>
                <p className="mb-2 text-lg font-bold text-yellow-700">
                  Total Courses: {analytics.length}
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Created Courses */}
        <div className="p-8 bg-white shadow-lg rounded-xl">
          <h2 className="mb-6 text-2xl font-semibold text-gray-800">Created Courses</h2>
          {createdCourses.length > 0 ? (
            <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
              {createdCourses.map((course) => (
                <div
                  key={course._id}
                  className="overflow-hidden transition-transform duration-300 bg-white shadow-lg rounded-xl hover:shadow-2xl hover:-translate-y-1"
                >
                  <img
                    src={course.coverImage || 'https://via.placeholder.com/300x200?text=Course'}
                    alt={course.title}
                    className="object-cover w-full h-48"
                  />
                  <div className="p-5">
                    <h3 className="mb-2 text-lg font-semibold text-gray-800">{course.title}</h3>
                    <p className="mb-2 text-sm text-gray-600"><span className="font-semibold">Category:</span> {course.category}</p>
                    <p className="mb-2 text-sm text-gray-600"><span className="font-semibold">Level:</span> {course.level}</p>
                    <p className="mb-3 text-sm text-gray-600"><span className="font-semibold">Price:</span> ${course.price.toFixed(2)}</p>
                    <p className="mb-3 text-sm text-gray-600 line-clamp-2">{course.description}</p>
                    {course.sampleVideo && (
                      <a
                        href={course.sampleVideo}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-sm text-primary hover:underline"
                        aria-label={`Watch sample video for ${course.title}`}
                      >
                        Watch Sample Video
                      </a>
                    )}
                    {courseQuizzes[course._id] && courseQuizzes[course._id].length > 0 && (
                      <div className="mt-4">
                        <p className="mb-1 text-xs font-semibold text-indigo-700">Quizzes:</p>
                        <ul className="space-y-1">
                          {courseQuizzes[course._id].map((quiz) => (
                            <li key={quiz._id} className="flex items-center gap-2">
                              <span className="text-xs font-medium text-gray-700">{quiz.title}</span>
                              <button
                                onClick={() => handleViewQuizResults(quiz)}
                                className="px-2 py-1 text-xs text-white transition rounded bg-primary hover:bg-secondary"
                              >
                                View Results
                              </button>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                    <div className="flex gap-2 mt-3">
                      <button
                        onClick={() => handleEditCourse(course)}
                        className="px-4 py-2 font-bold text-indigo-900 transition bg-yellow-400 rounded hover:bg-yellow-500"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteCourse(course._id)}
                        className="px-4 py-2 font-bold text-white transition bg-red-500 rounded hover:bg-red-600"
                      >
                        Delete
                      </button>
                      <Link
                        to={`/instructor/course/${course._id}`}
                        className="inline-block px-4 py-2 text-sm text-white transition rounded bg-primary hover:bg-secondary"
                        aria-label={`View ${course.title}`}
                      >
                        View
                      </Link>
                    </div>
                    {/* Enrolled Students */}
                    {enrolledStudents[course._id] && enrolledStudents[course._id].length > 0 && (
                      <div className="mt-2">
                        <p className="mb-1 text-sm font-semibold text-gray-700">Enrolled Students:</p>
                        <ul className="text-xs text-gray-600">
                          {enrolledStudents[course._id].map((student) => (
                            <li key={student._id}>{student.firstName} {student.lastName} ({student.email})</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-600">No courses created yet.</p>
          )}
        </div>

        {/* Edit Profile Modal */}
        {isModalOpen && !editingCourse && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
            <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-lg">
              <h2 className="mb-6 text-2xl font-bold text-center text-primary">Edit Instructor Profile</h2>
              <form onSubmit={handleUpdate} className="space-y-6">
                <div>
                  <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
                    First Name
                  </label>
                  <input
                    id="firstName"
                    name="firstName"
                    type="text"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    className="w-full px-4 py-2 mt-1 transition border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="lastName" className="block text-sm font-medium text-gray-700">
                    Last Name
                  </label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    className="w-full px-4 py-2 mt-1 transition border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="w-full px-4 py-2 mt-1 transition border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="bio" className="block text-sm font-medium text-gray-700">
                    Bio
                  </label>
                  <textarea
                    id="bio"
                    name="bio"
                    value={formData.bio}
                    onChange={handleInputChange}
                    className="w-full px-4 py-2 mt-1 transition border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                    rows="4"
                    placeholder="Tell us about your expertise and experience"
                  />
                </div>
                <div className="flex justify-end space-x-4">
                  <button
                    type="button"
                    onClick={() => setIsModalOpen(false)}
                    className="px-4 py-2 text-gray-700 transition bg-gray-300 rounded-md hover:bg-gray-400"
                    aria-label="Cancel"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 text-white transition rounded-md bg-primary hover:bg-secondary"
                    aria-label="Save Changes"
                  >
                    Save Changes
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Quiz Results Modal */}
        <Transition appear show={quizResultsModal.open} as={Fragment}>
          <Dialog as="div" className="relative z-50" onClose={() => setQuizResultsModal({ open: false, quiz: null, analytics: null })}>
            <Transition.Child
              as={Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-black bg-opacity-30" />
            </Transition.Child>
            <div className="fixed inset-0 overflow-y-auto">
              <div className="flex items-center justify-center min-h-screen px-4">
                <Transition.Child
                  as={Fragment}
                  enter="ease-out duration-300"
                  enterFrom="opacity-0 scale-95"
                  enterTo="opacity-100 scale-100"
                  leave="ease-in duration-200"
                  leaveFrom="opacity-100 scale-100"
                  leaveTo="opacity-0 scale-95"
                >
                  <Dialog.Panel className="relative w-full max-w-2xl p-8 mx-auto bg-white rounded-lg shadow-xl">
                    <Dialog.Title className="mb-4 text-xl font-bold">Quiz Results: {quizResultsModal.quiz?.title}</Dialog.Title>
                    {quizResultsModal.analytics ? (
                      quizResultsModal.analytics.error ? (
                        <div className="mb-4 font-semibold text-red-600">{quizResultsModal.analytics.error}</div>
                      ) : (
                        <div>
                          <div className="mb-4 font-semibold text-green-700">Total Submissions: {quizResultsModal.analytics.totalSubmissions}</div>
                          <div className="mb-4 font-semibold text-blue-700">Average Score: {quizResultsModal.analytics.averageScore?.toFixed(2)}</div>
                          <div className="overflow-x-auto">
                            <table className="min-w-full overflow-hidden text-xs bg-white border rounded-xl">
                              <thead className="bg-gray-100">
                                <tr>
                                  <th className="px-4 py-2 border">Student</th>
                                  <th className="px-4 py-2 border">Score</th>
                                  <th className="px-4 py-2 border">Submitted At</th>
                                  <th className="px-4 py-2 border">Answers</th>
                                </tr>
                              </thead>
                              <tbody>
                                {quizResultsModal.analytics.submissions.map((s, idx) => (
                                  <tr key={idx}>
                                    <td className="px-4 py-2 border">{s.student?.firstName} {s.student?.lastName} <br /><span className="text-xs text-gray-500">{s.student?.email}</span></td>
                                    <td className="px-4 py-2 border">{s.score}</td>
                                    <td className="px-4 py-2 border">{new Date(s.submittedAt).toLocaleString()}</td>
                                    <td className="px-4 py-2 border">
                                      <ul className="text-xs">
                                        {s.answers.map((a, i) => (
                                          <li key={i} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                                            <span className="font-semibold">Q:</span> {a.question}<br />
                                            <span className="font-semibold">Ans:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                                            <span className="font-semibold">Correct:</span> {a.correct}
                                          </li>
                                        ))}
                                      </ul>
                                    </td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>
                          <div className="flex justify-end mt-6">
                            <button onClick={() => setQuizResultsModal({ open: false, quiz: null, analytics: null })} className="px-4 py-2 text-white rounded bg-primary">Close</button>
                          </div>
                        </div>
                      )
                    ) : (
                      <div className="text-gray-600">Loading...</div>
                    )}
                  </Dialog.Panel>
                </Transition.Child>
              </div>
            </div>
          </Dialog>
        </Transition>
      </div>
    </section>
  );
};

export default InstructorProfile;
