import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bio: '',
  });
  const navigate = useNavigate();

  // Static enrolled courses for Students
  const staticCourses = [
    {
      _id: 'course1',
      title: 'Introduction to JavaScript',
      image: 'https://via.placeholder.com/300x200?text=JavaScript',
    },
    {
      _id: 'course2',
      title: 'Web Development Basics',
      image: 'https://via.placeholder.com/300x200?text=Web+Dev',
    },
    {
      _id: 'course3',
      title: 'Python for Beginners',
      image: 'https://via.placeholder.com/300x200?text=Python',
    },
  ];

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await fetch(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        setUser(data.user);
        setFormData({
          firstName: data.user.firstName,
          lastName: data.user.lastName,
          email: data.user.email,
          bio: data.user.bio || '',
        });
      } catch (err) {
        setError('Failed to load user profile');
      }
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);

      // Fetch enrolled courses (for Students)
      if (decoded.role === 'Student') {
        axios
          .get(`${API_CONFIG.BASE_URL}/courses/enrolled`, {
            headers: { Authorization: `Bearer ${token}` },
          })
          .then((res) => {
            // Use API data if available, otherwise fallback to staticCourses
            setEnrolledCourses(res.data.courses.length > 0 ? res.data.courses : staticCourses);
          })
          .catch(() => {
            toast.error('Failed to load enrolled courses, showing static data');
            setEnrolledCourses(staticCourses);
          });
      }
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(
        `${API_CONFIG.BASE_URL}/auth/update`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(response.data.user);
      setIsModalOpen(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  if (error) return <div className="min-h-[60vh] flex items-center justify-center text-red-600 text-xl">{error}</div>;
  if (!user) return <div className="min-h-[60vh] flex items-center justify-center text-primary text-xl">Loading...</div>;

  return (
    <div className="min-h-[80vh] bg-gray-50 py-12 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-lg p-8 flex flex-col items-center">
        <div className="w-32 h-32 rounded-full bg-gradient-to-br from-primary to-secondary flex items-center justify-center mb-6">
          <span className="text-5xl font-bold text-white">{user.firstName[0]}{user.lastName[0]}</span>
        </div>
        <h1 className="text-3xl font-extrabold text-primary mb-2">{user.firstName} {user.lastName}</h1>
        <p className="text-gray-600 mb-2">{user.email}</p>
        <span className="inline-block bg-yellow-400 text-indigo-900 px-3 py-1 rounded-full text-sm font-semibold mb-4">{user.role}</span>
        {user.bio && <p className="text-gray-700 mb-4 italic">{user.bio}</p>}
        <div className="flex gap-4 mt-4">
          <button
            onClick={() => setIsModalOpen(true)}
            className="bg-primary text-white px-6 py-2 rounded-lg font-bold hover:bg-secondary transition"
          >
            Edit Profile
          </button>
          <button className="bg-red-500 text-white px-6 py-2 rounded-lg font-bold hover:bg-red-600 transition">Delete Account</button>
        </div>
      </div>

      {/* Edit Profile Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
            <h2 className="text-2xl font-bold text-center text-primary mb-6">Edit Profile</h2>
            <form onSubmit={handleUpdate} className="space-y-6">
              <div>
                <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
                  First Name
                </label>
                <input
                  id="firstName"
                  name="firstName"
                  type="text"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                  required
                  aria-required="true"
                />
              </div>
              <div>
                <label htmlFor="lastName" className="block text-sm font-medium text-gray-700">
                  Last Name
                </label>
                <input
                  id="lastName"
                  name="lastName"
                  type="text"
                  value={formData.lastName}
                  onChange={handleInputChange}
                  className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                  required
                  aria-required="true"
                />
              </div>
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                  Email
                </label>
                <input
                  id="email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                  required
                  aria-required="true"
                />
              </div>
              {user.role === 'Instructor' && (
                <div>
                  <label htmlFor="bio" className="block text-sm font-medium text-gray-700">
                    Bio
                  </label>
                  <textarea
                    id="bio"
                    name="bio"
                    value={formData.bio}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    rows="4"
                    placeholder="Tell us about yourself (e.g., expertise, experience)"
                  />
                </div>
              )}
              <div className="flex justify-end space-x-4">
                <button
                  type="button"
                  onClick={() => setIsModalOpen(false)}
                  className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400 transition"
                  aria-label="Cancel"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition"
                  aria-label="Save Changes"
                >
                  Save Changes
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Enrolled Courses (for Students) */}
      {user.role === 'Student' && (
        <div className="bg-white rounded-xl shadow-lg p-8 mt-10">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Enrolled Courses</h2>
          {enrolledCourses.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {enrolledCourses.map((course) => (
                <div
                  key={course._id}
                  className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-transform duration-300 hover:-translate-y-1"
                >
                  <img
                    src={course.image || 'https://via.placeholder.com/300x200?text=Course'}
                    alt={course.title}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-5">
                    <h3 className="text-lg font-semibold text-gray-800 mb-3">{course.title}</h3>
                    <Link
                      to={`/courses/${course._id}`}
                      className="inline-block bg-primary text-white text-sm px-4 py-2 rounded hover:bg-secondary transition"
                      aria-label={`Continue ${course.title}`}
                    >
                      Continue Learning
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600 text-center">No courses enrolled yet.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
