import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  let role = null;

  if (token) {
    try {
      const decoded = jwtDecode(token);
      role = decoded.role;
    } catch (error) {
      console.error('Invalid token:', error);
    }
  }

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-blue-600 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">
          EduVerse
        </Link>
        <div className="space-x-4">
          <Link to="/" className="hover:text-gray-200 transition">
            Home
          </Link>
          <Link to="/courses" className="hover:text-gray-200 transition">
            Courses
          </Link>
          {token ? (
            <>
              <Link to="/profile" className="hover:text-gray-200 transition">
                Profile
              </Link>
              {role === 'Admin' && (
                <Link to="/admin/dashboard" className="hover:text-gray-200 transition">
                  Admin Dashboard
                </Link>
              )}
              <button
                onClick={handleLogout}
                className="hover:text-gray-200 transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="hover:text-gray-200 transition">
                Login
              </Link>
              <Link to="/signup" className="hover:text-gray-200 transition">
                Signup
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
