import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API_CONFIG from '../config/apiConfig';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
import CourseContentList from '../components/CourseContentList';
import CourseContentForm from '../components/CourseContentForm';

const CourseDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [alreadyEnrolled, setAlreadyEnrolled] = useState(false);
  const [user, setUser] = useState(null);
  const [contentRefresh, setContentRefresh] = useState(0);

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const res = await fetch(`${API_CONFIG.BASE_URL}/courses/${id}`);
        const data = await res.json();
        setCourse(data.course);
      } catch (err) {
        setError('Failed to fetch course details');
      }
    };
    fetchCourse();
  }, [id]);

  useEffect(() => {
    // Check if student is already enrolled
    const token = localStorage.getItem('token');
    if (!token) return;
    let role = '';
    try {
      const decoded = jwtDecode(token);
      role = decoded.role;
      if (role === 'Student') {
        axios
          .get(`${API_CONFIG.BASE_URL}/courses/enrolled`, {
            headers: { Authorization: `Bearer ${token}` },
          })
          .then((res) => {
            if (res.data.courses.some((c) => c._id === id)) {
              setAlreadyEnrolled(true);
              setMessage('You are already enrolled in this course.');
            }
          })
          .catch(() => {});
      }
    } catch (err) {}
  }, [id]);

  useEffect(() => {
    // Fetch user info for role-based content
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUser({ id: decoded.id, role: decoded.role });
      } catch {}
    }
  }, []);

  const handleEnroll = async () => {
    setMessage('');
    setError('');
    setLoading(true);
    const token = localStorage.getItem('token');
    if (!token) {
      setLoading(false);
      navigate('/login');
      return;
    }
    let role = '';
    try {
      const decoded = jwtDecode(token);
      role = decoded.role;
    } catch (err) {
      setLoading(false);
      localStorage.removeItem('token');
      navigate('/login');
      return;
    }
    if (role !== 'Student') {
      setLoading(false);
      setMessage('Only students can enroll in courses.');
      return;
    }
    try {
      const res = await fetch(`${API_CONFIG.BASE_URL}/courses/purchase/${id}`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Failed to enroll.');
      if (data.url) {
        // Paid course: redirect to Stripe
        window.location.href = data.url;
      } else {
        // Free course: show success and redirect
        setMessage('Enrolled successfully!');
        setTimeout(() => navigate('/courses'), 1500);
      }
    } catch (err) {
      setError(err.message || 'Failed to enroll.');
    } finally {
      setLoading(false);
    }
  };

  if (error) return <div className="min-h-[60vh] flex items-center justify-center text-red-600 text-xl">{error}</div>;
  if (!course) return <div className="min-h-[60vh] flex items-center justify-center text-primary text-xl">Loading...</div>;

  return (
    <div className="min-h-[80vh] bg-gray-50 py-12 px-4">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg p-8 flex flex-col md:flex-row gap-8">
        <img src={course.coverImage || 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80'} alt={course.title} className="w-full md:w-1/2 h-64 object-cover rounded-xl" />
        <div className="flex-1 flex flex-col">
          <h1 className="text-3xl font-extrabold text-primary mb-2">{course.title}</h1>
          <p className="text-gray-700 mb-4">{course.description}</p>
          <div className="mb-4">
            <span className="inline-block bg-primary text-white px-3 py-1 rounded-full text-sm font-semibold mr-2">{course.category}</span>
            <span className="inline-block bg-yellow-400 text-indigo-900 px-3 py-1 rounded-full text-sm font-semibold">{course.level}</span>
          </div>
          <div className="flex items-center gap-4 mb-4">
            <span className="text-lg font-bold text-indigo-700">${course.price}</span>
            <span className="text-gray-500">by {course.instructorName || 'Instructor'}</span>
          </div>
          {course.sampleVideo && (
            <div className="mb-4">
              <label className="block text-gray-700 font-semibold mb-2">Sample Video</label>
              <video src={course.sampleVideo} controls className="w-full h-64 rounded-lg border" />
            </div>
          )}
          <button
            onClick={handleEnroll}
            disabled={loading || alreadyEnrolled}
            className="mt-auto bg-primary text-white px-6 py-3 rounded-lg font-bold hover:bg-secondary transition text-lg text-center disabled:opacity-60"
          >
            {alreadyEnrolled ? 'Already Enrolled' : loading ? 'Processing...' : 'Enroll Now'}
          </button>
          {message && <p className="mt-4 text-center text-yellow-600 font-semibold">{message}</p>}
          {error && <p className="mt-4 text-center text-red-600 font-semibold">{error}</p>}
        </div>
      </div>
      <div className="max-w-3xl mx-auto">
        {user && user.role === 'Instructor' && course && course.instructor && user.id === course.instructor.toString() && (
          <CourseContentForm courseId={course._id} onContentAdded={() => setContentRefresh(r => r + 1)} />
        )}
        <CourseContentList courseId={id} user={user} key={contentRefresh} />
      </div>
    </div>
  );
};

export default CourseDetails;
