import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const coursesData = [
  {
    id: 1,
    title: 'Web Development Bootcamp',
    description: 'Master HTML, CSS, and JavaScript in 8 weeks.',
    image: 'https://via.placeholder.com/300x200?text=Web+Development',
    category: 'Web Development',
    price: 'Paid',
    level: 'Beginner',
  },
  {
    id: 2,
    title: 'Data Science Fundamentals',
    description: 'Learn Python, Pandas, and Machine Learning basics.',
    image: 'https://via.placeholder.com/300x200?text=Data+Science',
    category: 'Data Science',
    price: 'Paid',
    level: 'Intermediate',
  },
  {
    id: 3,
    title: 'Digital Marketing Mastery',
    description: 'Boost your business with SEO and social media strategies.',
    image: 'https://via.placeholder.com/300x200?text=Digital+Marketing',
    category: 'Marketing',
    price: 'Free',
    level: 'Beginner',
  },
  {
    id: 4,
    title: 'Advanced Machine Learning',
    description: 'Deep dive into neural networks and AI.',
    image: 'https://via.placeholder.com/300x200?text=Machine+Learning',
    category: 'Data Science',
    price: 'Paid',
    level: 'Advanced',
  },
];

const Courses = () => {
  const [categoryFilter, setCategoryFilter] = useState('All');
  const [priceFilter, setPriceFilter] = useState('All');
  const [levelFilter, setLevelFilter] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');

  const filteredCourses = coursesData.filter((course) => {
    return (
      (categoryFilter === 'All' || course.category === categoryFilter) &&
      (priceFilter === 'All' || course.price === priceFilter) &&
      (levelFilter === 'All' || course.level === levelFilter) &&
      course.title.toLowerCase().includes(searchQuery.toLowerCase())
    );
  });

  return (
    <section className="bg-gray-50 py-12 px-4 min-h-screen">
      <div className="max-w-7xl mx-auto">
        {/* Title */}
        <h1 className="text-4xl font-bold text-center text-gray-800 mb-10">Explore Our Courses</h1>

        {/* Filters */}
        <div className="bg-white rounded-2xl shadow-md p-6 mb-10 border border-gray-100">
  <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
    {/* Search Input */}
    <div className="w-full lg:w-1/3">
      <label htmlFor="search" className="block text-sm font-medium text-gray-700 mb-1">
        🔍 Search
      </label>
      <input
        id="search"
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Search for a course..."
        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
    </div>

    {/* Filters */}
    <div className="flex flex-wrap gap-4 justify-start lg:justify-end w-full lg:w-auto">
      {/* Category */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">📚 Category</label>
        <select
          value={categoryFilter}
          onChange={(e) => setCategoryFilter(e.target.value)}
          className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="All">All</option>
          <option value="Web Development">Web Development</option>
          <option value="Data Science">Data Science</option>
          <option value="Marketing">Marketing</option>
        </select>
      </div>

      {/* Price */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">💰 Price</label>
        <select
          value={priceFilter}
          onChange={(e) => setPriceFilter(e.target.value)}
          className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="All">All</option>
          <option value="Free">Free</option>
          <option value="Paid">Paid</option>
        </select>
      </div>

      {/* Level */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">📈 Level</label>
        <select
          value={levelFilter}
          onChange={(e) => setLevelFilter(e.target.value)}
          className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          <option value="All">All</option>
          <option value="Beginner">Beginner</option>
          <option value="Intermediate">Intermediate</option>
          <option value="Advanced">Advanced</option>
        </select>
      </div>
    </div>
  </div>
</div>


        {/* Course Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredCourses.length > 0 ? (
            filteredCourses.map((course) => (
              <div
                key={course.id}
                className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-transform duration-300 hover:-translate-y-1"
              >
                <img
                  src={course.image}
                  alt={course.title}
                  className="w-full h-48 object-cover"
                />
                <div className="p-5">
                  <h3 className="text-lg font-semibold text-gray-800 mb-1">{course.title}</h3>
                  <p className="text-gray-600 text-sm mb-3">{course.description}</p>
                  <div className="text-xs text-gray-500 mb-1">Category: {course.category}</div>
                  <div className="text-xs text-gray-500 mb-1">Level: {course.level}</div>
                  <div className="text-xs text-gray-500 mb-3">Price: {course.price}</div>
                  <Link
                    to={`/courses/${course.id}`}
                    className="inline-block bg-blue-600 text-white text-sm px-4 py-2 rounded hover:bg-blue-700 transition"
                  >
                    View Course
                  </Link>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-600 col-span-full">No courses found.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default Courses;
