import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';

const InstructorCourseDetails = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [enrollments, setEnrollments] = useState([]);
  const [analytics, setAnalytics] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    // Fetch course details
    axios.get(`${API_CONFIG.BASE_URL}/courses/${courseId}`)
      .then(res => setCourse(res.data.course))
      .catch(() => setCourse(null));
    // Fetch enrollments for this course
    axios.get(`${API_CONFIG.BASE_URL}/enrollments/course/${courseId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setEnrollments(res.data.enrollments))
      .catch(() => setEnrollments([]));
    // Fetch analytics for this course
    axios.get(`${API_CONFIG.BASE_URL}/enrollments/analytics/instructor/${token ? JSON.parse(atob(token.split('.')[1])).id : ''}`)
      .then(res => {
        const found = res.data.analytics.find(a => a.courseId === courseId);
        setAnalytics(found || null);
      })
      .catch(() => setAnalytics(null));
  }, [courseId, navigate]);

  if (!course) return <div className="p-8">Loading...</div>;

  return (
    <div className="min-h-screen px-4 py-12 bg-gray-50">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg p-8">
        <h1 className="text-3xl font-extrabold text-primary mb-2">{course.title}</h1>
        <p className="mb-2 text-gray-700">{course.description}</p>
        <p className="mb-2"><span className="font-semibold">Category:</span> {course.category}</p>
        <p className="mb-2"><span className="font-semibold">Level:</span> {course.level}</p>
        <p className="mb-2"><span className="font-semibold">Price:</span> ${course.price}</p>
        <p className="mb-2"><span className="font-semibold">Created:</span> {new Date(course.createdAt).toLocaleDateString()}</p>
        {course.coverImage && <img src={course.coverImage} alt={course.title} className="w-full h-64 object-cover rounded-lg my-4" />}
        {analytics && (
          <div className="my-6">
            <h2 className="text-xl font-bold mb-2">Enrollment Analytics</h2>
            <p>Total Enrollments: {analytics.total}</p>
            <p>Paid: {analytics.paid} | Free: {analytics.free}</p>
            <p>Earnings: ${analytics.earnings.toFixed(2)}</p>
            <div className="mt-4">
              <p className="text-sm font-semibold mb-2">Enrollments Over Time:</p>
              <ResponsiveContainer width="100%" height={200}>
                <LineChart data={analytics.dailyEnrollments}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis allowDecimals={false} />
                  <Tooltip />
                  <Line type="monotone" dataKey="count" stroke="#6366f1" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>
        )}
        <div className="my-6">
          <h2 className="text-xl font-bold mb-2">Enrolled Students</h2>
          {enrollments.length === 0 ? (
            <p>No students enrolled yet.</p>
          ) : (
            <table className="min-w-full bg-white border">
              <thead>
                <tr>
                  <th className="border px-4 py-2">Name</th>
                  <th className="border px-4 py-2">Email</th>
                  <th className="border px-4 py-2">Payment</th>
                  <th className="border px-4 py-2">Enrolled At</th>
                </tr>
              </thead>
              <tbody>
                {enrollments.map(e => (
                  <tr key={e.student._id}>
                    <td className="border px-4 py-2">{e.student.firstName} {e.student.lastName}</td>
                    <td className="border px-4 py-2">{e.student.email}</td>
                    <td className="border px-4 py-2">{e.paymentStatus}</td>
                    <td className="border px-4 py-2">{new Date(e.enrolledAt).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
        <Link to="/instructor/profile" className="text-primary hover:underline">Back to Profile</Link>
      </div>
    </div>
  );
};

export default InstructorCourseDetails;
