import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import API_CONFIG from '../config/apiConfig';

const CourseDetails = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const res = await fetch(`${API_CONFIG.BASE_URL}/courses/${id}`);
        const data = await res.json();
        setCourse(data.course);
      } catch (err) {
        setError('Failed to fetch course details');
      }
    };
    fetchCourse();
  }, [id]);

  if (error) return <div className="min-h-[60vh] flex items-center justify-center text-red-600 text-xl">{error}</div>;
  if (!course) return <div className="min-h-[60vh] flex items-center justify-center text-primary text-xl">Loading...</div>;

  return (
    <div className="min-h-[80vh] bg-gray-50 py-12 px-4">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg p-8 flex flex-col md:flex-row gap-8">
        <img src={course.coverImage || 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80'} alt={course.title} className="w-full md:w-1/2 h-64 object-cover rounded-xl" />
        <div className="flex-1 flex flex-col">
          <h1 className="text-3xl font-extrabold text-primary mb-2">{course.title}</h1>
          <p className="text-gray-700 mb-4">{course.description}</p>
          <div className="mb-4">
            <span className="inline-block bg-primary text-white px-3 py-1 rounded-full text-sm font-semibold mr-2">{course.category}</span>
            <span className="inline-block bg-yellow-400 text-indigo-900 px-3 py-1 rounded-full text-sm font-semibold">{course.level}</span>
          </div>
          <div className="flex items-center gap-4 mb-4">
            <span className="text-lg font-bold text-indigo-700">${course.price}</span>
            <span className="text-gray-500">by {course.instructorName || 'Instructor'}</span>
          </div>
          {course.sampleVideo && (
            <div className="mb-4">
              <label className="block text-gray-700 font-semibold mb-2">Sample Video</label>
              <video src={course.sampleVideo} controls className="w-full h-64 rounded-lg border" />
            </div>
          )}
          <Link
            to="/signup"
            className="mt-auto bg-primary text-white px-6 py-3 rounded-lg font-bold hover:bg-secondary transition text-lg text-center"
          >
            Enroll Now
          </Link>
        </div>
      </div>
    </div>
  );
};

export default CourseDetails;
