import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AdminAnalytics = () => {
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedPeriod, setSelectedPeriod] = useState('30');

  useEffect(() => {
    fetchAnalytics();
  }, [selectedPeriod]);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      
      const [analyticsRes, enrollmentRes] = await Promise.all([
        axios.get(`${API_CONFIG.BASE_URL}/admin/analytics`, {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get(`${API_CONFIG.BASE_URL}/admin/analytics/enrollments?period=${selectedPeriod}`, {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);
      
      setAnalytics({
        ...analyticsRes.data,
        enrollmentData: enrollmentRes.data.enrollments
      });
    } catch (error) {
      toast.error('Failed to fetch analytics');
      console.error('Fetch analytics error:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatNumber = (num) => {
    return new Intl.NumberFormat('en-US').format(num);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getChartData = () => {
    if (!analytics?.monthlyRegistrations) return [];
    
    return analytics.monthlyRegistrations.map(item => ({
      month: `${item._id.year}-${item._id.month.toString().padStart(2, '0')}`,
      count: item.count
    }));
  };

  const getEnrollmentChartData = () => {
    if (!analytics?.enrollmentData) return [];
    
    return analytics.enrollmentData.map(item => ({
      date: `${item._id.year}-${item._id.month.toString().padStart(2, '0')}-${item._id.day.toString().padStart(2, '0')}`,
      count: item.count
    }));
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="text-center text-gray-500 py-8">
        Failed to load analytics data
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-800">Analytics Dashboard</h2>
        <div className="flex items-center space-x-4">
          <label className="text-sm font-medium text-gray-700">Enrollment Period:</label>
          <select
            value={selectedPeriod}
            onChange={(e) => setSelectedPeriod(e.target.value)}
            className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary"
          >
            <option value="7">Last 7 days</option>
            <option value="30">Last 30 days</option>
            <option value="90">Last 90 days</option>
          </select>
          <button
            onClick={fetchAnalytics}
            className="px-4 py-1 bg-primary text-white rounded-md text-sm hover:bg-primary-dark transition-colors"
          >
            Refresh
          </button>
        </div>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* User Statistics */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-blue-100 text-blue-600">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197m13.5-9a2.5 2.5 0 11-5 0 2.5 2.5 0 015 0z" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Users</p>
              <p className="text-2xl font-semibold text-gray-900">{formatNumber(analytics.userStats.total)}</p>
            </div>
          </div>
          <div className="mt-4 grid grid-cols-3 gap-2 text-xs">
            <div className="text-center">
              <p className="text-gray-500">Students</p>
              <p className="font-semibold text-green-600">{formatNumber(analytics.userStats.students)}</p>
            </div>
            <div className="text-center">
              <p className="text-gray-500">Instructors</p>
              <p className="font-semibold text-blue-600">{formatNumber(analytics.userStats.instructors)}</p>
            </div>
            <div className="text-center">
              <p className="text-gray-500">Admins</p>
              <p className="font-semibold text-red-600">{formatNumber(analytics.userStats.admins)}</p>
            </div>
          </div>
        </div>

        {/* Course Statistics */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-green-100 text-green-600">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Courses</p>
              <p className="text-2xl font-semibold text-gray-900">{formatNumber(analytics.courseStats.total)}</p>
            </div>
          </div>
          <div className="mt-4 grid grid-cols-2 gap-2 text-xs">
            <div className="text-center">
              <p className="text-gray-500">Published</p>
              <p className="font-semibold text-green-600">{formatNumber(analytics.courseStats.published)}</p>
            </div>
            <div className="text-center">
              <p className="text-gray-500">Draft</p>
              <p className="font-semibold text-yellow-600">{formatNumber(analytics.courseStats.draft)}</p>
            </div>
          </div>
        </div>

        {/* Enrollment Statistics */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-purple-100 text-purple-600">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Total Enrollments</p>
              <p className="text-2xl font-semibold text-gray-900">{formatNumber(analytics.enrollmentStats.total)}</p>
            </div>
          </div>
          <div className="mt-4 text-xs">
            <div className="text-center">
              <p className="text-gray-500">Active Enrollments</p>
              <p className="font-semibold text-purple-600">{formatNumber(analytics.enrollmentStats.active)}</p>
            </div>
          </div>
        </div>

        {/* Content Statistics */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <div className="flex items-center">
            <div className="p-3 rounded-full bg-orange-100 text-orange-600">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
              </svg>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Content Items</p>
              <p className="text-2xl font-semibold text-gray-900">{formatNumber(analytics.content?.total || 0)}</p>
            </div>
          </div>
          <div className="mt-4 grid grid-cols-2 gap-2 text-xs">
            <div className="text-center">
              <p className="text-gray-500">Quizzes</p>
              <p className="font-semibold text-blue-600">{formatNumber(analytics.content?.quizzes || 0)}</p>
            </div>
            <div className="text-center">
              <p className="text-gray-500">Assignments</p>
              <p className="font-semibold text-green-600">{formatNumber(analytics.content?.assignments || 0)}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* User Registration Chart */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">User Registrations (Last 6 Months)</h3>
          <div className="h-64 flex items-end justify-between space-x-1">
            {getChartData().map((item, index) => {
              const maxValue = Math.max(...getChartData().map(d => d.count));
              const height = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
              
              return (
                <div key={index} className="flex flex-col items-center flex-1">
                  <div 
                    className="w-full bg-primary rounded-t"
                    style={{ height: `${height}%` }}
                  ></div>
                  <div className="text-xs text-gray-500 mt-2 transform rotate-45 origin-left">
                    {item.month}
                  </div>
                  <div className="text-xs font-medium text-gray-700 mt-1">
                    {item.count}
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* Enrollment Chart */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Enrollments (Last {selectedPeriod} Days)</h3>
          <div className="h-64 flex items-end justify-between space-x-1">
            {getEnrollmentChartData().slice(-10).map((item, index) => {
              const maxValue = Math.max(...getEnrollmentChartData().slice(-10).map(d => d.count));
              const height = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
              
              return (
                <div key={index} className="flex flex-col items-center flex-1">
                  <div 
                    className="w-full bg-green-500 rounded-t"
                    style={{ height: `${height}%` }}
                  ></div>
                  <div className="text-xs text-gray-500 mt-2 transform rotate-45 origin-left">
                    {item.date.split('-').slice(1).join('/')}
                  </div>
                  <div className="text-xs font-medium text-gray-700 mt-1">
                    {item.count}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Users */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Recent User Registrations</h3>
          <div className="space-y-3">
            {analytics.recentActivity?.users?.map((user, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center">
                  <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center text-white text-sm font-semibold">
                    {user.firstName.charAt(0)}{user.lastName.charAt(0)}
                  </div>
                  <div className="ml-3">
                    <p className="text-sm font-medium text-gray-900">
                      {user.firstName} {user.lastName}
                    </p>
                    <p className="text-xs text-gray-500">{user.email}</p>
                  </div>
                </div>
                <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                  user.role === 'Admin' ? 'bg-red-100 text-red-800' :
                  user.role === 'Instructor' ? 'bg-blue-100 text-blue-800' :
                  'bg-green-100 text-green-800'
                }`}>
                  {user.role}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Recent Courses */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Recent Course Creations</h3>
          <div className="space-y-3">
            {analytics.recentActivity?.courses?.map((course, index) => (
              <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center">
                  <div className="w-8 h-8 bg-green-500 rounded-lg flex items-center justify-center text-white text-xs">
                    
                  </div>
                  <div className="ml-3">
                    <p className="text-sm font-medium text-gray-900 line-clamp-1">
                      {course.title}
                    </p>
                    <p className="text-xs text-gray-500">
                      by {course.instructor?.firstName} {course.instructor?.lastName}
                    </p>
                  </div>
                </div>
                <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                  course.status === 'published' ? 'bg-green-100 text-green-800' :
                  course.status === 'draft' ? 'bg-yellow-100 text-yellow-800' :
                  'bg-gray-100 text-gray-800'
                }`}>
                  {course.status}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      <ToastContainer />
    </div>
  );
};

export default AdminAnalytics;
