import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, PieChart, Pie, Cell, BarChart, Bar } from 'recharts';
import { saveAs } from 'file-saver';

const COLORS = ['#6366f1', '#fbbf24'];

const InstructorCourseDetails = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    setLoading(true);
    axios.get(`${API_CONFIG.BASE_URL}/courses/${courseId}`)
      .then(res => setCourse(res.data.course))
      .catch(() => setCourse(null));
    axios.get(`${API_CONFIG.BASE_URL}/enrollments/analytics/course/${courseId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setAnalytics(res.data))
      .catch(() => setAnalytics(null))
      .finally(() => setLoading(false));
  }, [courseId, navigate]);

  // Memoized chart data
  const pieData = React.useMemo(() => [
    { name: 'Paid', value: analytics?.paid || 0 },
    { name: 'Free', value: analytics?.free || 0 },
  ], [analytics]);
  const revenueData = React.useMemo(() => analytics?.dailyRevenue || [], [analytics]);
  const enrollData = React.useMemo(() => analytics?.dailyEnrollments || [], [analytics]);

  const handleExportCSV = () => {
    if (!analytics?.students) return;
    const header = 'Name,Email,Enrolled At,Payment,Country,Progress,Certificate Issued\n';
    const rows = analytics.students.map(s =>
      `"${s.name}","${s.email}","${new Date(s.enrolledAt).toLocaleString()}","${s.paymentStatus}","${s.country || ''}",${s.progress},${s.certificateIssued}`
    ).join('\n');
    const csv = header + rows;
    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    saveAs(blob, `course_${courseId}_students.csv`);
  };

  if (loading) return <div className="p-8">Loading...</div>;
  if (!course) return <div className="p-8 text-red-600">Course not found.</div>;

  return (
    <div className="min-h-screen px-4 py-12 bg-gray-50">
      <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-lg p-8">
        <h1 className="text-3xl font-extrabold text-primary mb-2">{course.title}</h1>
        <p className="mb-2 text-gray-700">{course.description}</p>
        <p className="mb-2"><span className="font-semibold">Category:</span> {course.category}</p>
        <p className="mb-2"><span className="font-semibold">Level:</span> {course.level}</p>
        <p className="mb-2"><span className="font-semibold">Price:</span> ${course.price}</p>
        <p className="mb-2"><span className="font-semibold">Created:</span> {new Date(course.createdAt).toLocaleDateString()}</p>
        {course.coverImage && <img src={course.coverImage} alt={course.title} className="w-full h-64 object-cover rounded-lg my-4" />}
        {analytics && (
          <div className="my-6">
            <h2 className="text-xl font-bold mb-2">Course Analytics</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <p className="mb-2 text-lg font-bold text-green-700">Total Earnings: ${analytics.earnings?.toFixed(2) || '0.00'}</p>
                <p className="mb-2 text-lg font-bold text-indigo-700">Total Enrollments: {analytics.total}</p>
                <p className="mb-2 text-lg font-bold text-blue-700">Paid: {analytics.paid} | Free: {analytics.free}</p>
                <p className="mb-2 text-lg font-bold text-yellow-700">Completion Rate: {analytics.completionRate?.toFixed(1)}%</p>
                <button onClick={handleExportCSV} className="mt-2 px-4 py-2 bg-primary text-white rounded hover:bg-secondary">Export Students CSV</button>
              </div>
              <div className="flex flex-col gap-4">
                <div className="w-full h-40">
                  <PieChart width={180} height={160}>
                    <Pie data={pieData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={60} label>
                      {pieData.map((entry, idx) => <Cell key={`cell-${idx}`} fill={COLORS[idx % COLORS.length]} />)}
                    </Pie>
                  </PieChart>
                  <div className="flex justify-center gap-4 text-sm mt-2">
                    <span className="flex items-center"><span className="inline-block w-3 h-3 mr-1 rounded-full" style={{background: COLORS[0]}}></span>Paid</span>
                    <span className="flex items-center"><span className="inline-block w-3 h-3 mr-1 rounded-full" style={{background: COLORS[1]}}></span>Free</span>
                  </div>
                </div>
                <div className="w-full h-40">
                  <BarChart width={250} height={160} data={revenueData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="revenue" fill="#6366f1" />
                  </BarChart>
                  <div className="text-center text-xs mt-1">Revenue Over Time</div>
                </div>
              </div>
            </div>
            <div className="mt-8">
              <p className="text-sm font-semibold mb-2">Enrollments Over Time:</p>
              <ResponsiveContainer width="100%" height={200}>
                <LineChart data={enrollData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis allowDecimals={false} />
                  <Tooltip />
                  <Line type="monotone" dataKey="count" stroke="#6366f1" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>
        )}
        <div className="my-6">
          <h2 className="text-xl font-bold mb-2">Enrolled Students</h2>
          {!analytics?.students?.length ? (
            <p>No students enrolled yet.</p>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white border">
                <thead>
                  <tr>
                    <th className="border px-4 py-2">Name</th>
                    <th className="border px-4 py-2">Email</th>
                    <th className="border px-4 py-2">Payment</th>
                    <th className="border px-4 py-2">Enrolled At</th>
                    <th className="border px-4 py-2">Country</th>
                    <th className="border px-4 py-2">Progress</th>
                    <th className="border px-4 py-2">Certificate</th>
                  </tr>
                </thead>
                <tbody>
                  {analytics.students.map(s => (
                    <tr key={s.id}>
                      <td className="border px-4 py-2">{s.name}</td>
                      <td className="border px-4 py-2">{s.email}</td>
                      <td className="border px-4 py-2">{s.paymentStatus}</td>
                      <td className="border px-4 py-2">{new Date(s.enrolledAt).toLocaleString()}</td>
                      <td className="border px-4 py-2">{s.country || '-'}</td>
                      <td className="border px-4 py-2">{s.progress}%</td>
                      <td className="border px-4 py-2">{s.certificateIssued ? 'Yes' : 'No'}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
        <Link to="/instructor/profile" className="text-primary hover:underline">Back to Profile</Link>
      </div>
    </div>
  );
};

export default InstructorCourseDetails;
