import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [enrollments, setEnrollments] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bio: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await fetch(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        setUser(data.user);
        setFormData({
          firstName: data.user.firstName,
          lastName: data.user.lastName,
          email: data.user.email,
          bio: data.user.bio || '',
        });
      } catch (err) {
        setError('Failed to load user profile');
      }
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);

      // Fetch enrolled courses (for Students)
      if (decoded.role === 'Student') {
        axios
          .get(`${API_CONFIG.BASE_URL}/enrollments/student/${decoded.id}`, {
            headers: { Authorization: `Bearer ${token}` },
          })
          .then((res) => {
            // Remove duplicate enrollments by course._id
            const uniqueEnrollments = [];
            const seen = new Set();
            for (const e of res.data.enrollments) {
              if (!seen.has(e.course._id)) {
                uniqueEnrollments.push(e);
                seen.add(e.course._id);
              }
            }
            setEnrollments(uniqueEnrollments);
          })
          .catch(() => {
            toast.error('Failed to load enrolled courses.');
            setEnrollments([]);
          });
      }
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(
        `${API_CONFIG.BASE_URL}/auth/update`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(response.data.user);
      setIsModalOpen(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  if (error) return <div className="min-h-[60vh] flex items-center justify-center text-red-600 text-xl">{error}</div>;
  if (!user) return <div className="min-h-[60vh] flex items-center justify-center text-primary text-xl">Loading...</div>;

  return (
    <section className="min-h-screen px-4 py-12 bg-gradient-to-br from-gray-50 to-blue-50">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <div className="mx-auto max-w-7xl">


        {/* User Statistics Dashboard */}
        <div className="grid grid-cols-1 gap-6 mb-10 sm:grid-cols-2 lg:grid-cols-4">
          <div className="p-6 shadow-lg bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-blue-100">Total Courses</p>
                <p className="text-2xl font-bold text-white">{enrollments.length}</p>
              </div>
              <div className="p-3 bg-white rounded-full bg-opacity-20">
                <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                </svg>
              </div>
            </div>
          </div>

          <div className="p-6 shadow-lg bg-gradient-to-br from-green-500 to-green-600 rounded-2xl">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-green-100">Completed</p>
                <p className="text-2xl font-bold text-white">{enrollments.filter(e => e.progress === 100).length}</p>
              </div>
              <div className="p-3 bg-white rounded-full bg-opacity-20">
                <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
            </div>
          </div>

          <div className="p-6 shadow-lg bg-gradient-to-br from-purple-500 to-purple-600 rounded-2xl">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-purple-100">In Progress</p>
                <p className="text-2xl font-bold text-white">{enrollments.filter(e => e.progress > 0 && e.progress < 100).length}</p>
              </div>
              <div className="p-3 bg-white rounded-full bg-opacity-20">
                <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                </svg>
              </div>
            </div>
          </div>

          <div className="p-6 shadow-lg bg-gradient-to-br from-orange-500 to-orange-600 rounded-2xl">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-orange-100">Certificates</p>
                <p className="text-2xl font-bold text-white">{enrollments.filter(e => e.certificateIssued).length}</p>
              </div>
              <div className="p-3 bg-white rounded-full bg-opacity-20">
                <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Profile Card */}
        <div className="p-8 mb-10 bg-white border border-gray-100 shadow-xl rounded-3xl">
          <div className="flex flex-col items-center gap-8 lg:flex-row">
            {/* Enhanced Avatar */}
            <div className="relative">
              <div className="flex items-center justify-center w-32 h-32 text-4xl font-extrabold text-white border-4 border-white rounded-full shadow-2xl bg-gradient-to-br from-blue-500 to-purple-600">
                {user.firstName[0]}{user.lastName[0]}
              </div>
              <div className="absolute flex items-center justify-center w-8 h-8 bg-green-500 border-4 border-white rounded-full -bottom-2 -right-2">
                <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
              </div>
            </div>

            {/* Enhanced User Info */}
            <div className="flex-1 text-center lg:text-left">
              <h1 className="mb-2 text-4xl font-bold text-gray-800">
              {user.firstName} {user.lastName}
            </h1>
              <div className="flex items-center justify-center gap-3 mb-4 lg:justify-start">
                <div className="flex items-center gap-2 text-gray-600">
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207" />
                  </svg>
                  <span className="text-lg">{user.email}</span>
                </div>
                <span className="px-4 py-2 text-sm font-bold tracking-wider text-white uppercase rounded-full bg-gradient-to-r from-yellow-400 to-orange-500">
              {user.role}
            </span>
              </div>
            {user.bio && (
                <div className="max-w-2xl mx-auto lg:mx-0">
                  <p className="text-lg leading-relaxed text-gray-700">{user.bio}</p>
                </div>
            )}
          </div>

            {/* Enhanced Action Buttons */}
            <div className="flex flex-col gap-3">
            <button
              onClick={() => setIsModalOpen(true)}
                className="px-8 py-3 font-semibold text-white transition-all duration-300 rounded-full bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-blue-300 focus:ring-opacity-50"
            >
                <svg className="inline-block w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                </svg>
              Edit Profile
            </button>
              <button className="px-8 py-3 font-semibold text-red-600 transition-all duration-300 rounded-full bg-red-50 hover:bg-red-100 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-red-300 focus:ring-opacity-50">
                <svg className="inline-block w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                </svg>
              Delete Account
            </button>
            </div>
          </div>
        </div>

        {/* Enhanced Edit Profile Modal */}
        {isModalOpen && (
          <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black bg-opacity-50">
            <div className="w-full max-w-md p-8 bg-white shadow-2xl rounded-2xl">
              <div className="flex items-center mb-6">
                <div className="p-3 mr-4 bg-blue-100 rounded-full">
                  <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                  </svg>
                </div>
                <h2 className="text-2xl font-bold text-gray-800">Edit Profile</h2>
              </div>
              <form onSubmit={handleUpdate} className="space-y-6">
                <div>
                  <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
                    First Name
                  </label>
                  <input
                    id="firstName"
                    name="firstName"
                    type="text"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 mt-1 transition border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="lastName" className="block text-sm font-medium text-gray-700">
                    Last Name
                  </label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 mt-1 transition border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="w-full px-4 py-3 mt-1 transition border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                    aria-required="true"
                  />
                </div>
                {user.role === 'Instructor' && (
                  <div>
                    <label htmlFor="bio" className="block text-sm font-medium text-gray-700">
                      Bio
                    </label>
                    <textarea
                      id="bio"
                      name="bio"
                      value={formData.bio}
                      onChange={handleInputChange}
                      className="w-full px-4 py-3 mt-1 transition border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      rows="4"
                      placeholder="Tell us about yourself (e.g., expertise, experience)"
                    />
                  </div>
                )}
                <div className="flex justify-end space-x-4">
                  <button
                    type="button"
                    onClick={() => setIsModalOpen(false)}
                    className="px-6 py-3 font-semibold text-gray-700 transition-colors bg-gray-200 rounded-xl hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-gray-300"
                    aria-label="Cancel"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-6 py-3 font-semibold text-white transition-all duration-300 bg-gradient-to-r from-blue-500 to-purple-600 rounded-xl hover:from-blue-600 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-blue-300"
                    aria-label="Save Changes"
                  >
                    Save Changes
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Enhanced Enrolled Courses Section (for Students) */}
        {user.role === 'Student' && (
          <div className="p-8 bg-white border border-gray-100 shadow-xl rounded-3xl">
            <div className="flex items-center justify-between mb-8">
              <div className="flex items-center">
                <div className="p-3 mr-4 bg-indigo-100 rounded-full">
                  <svg className="w-6 h-6 text-indigo-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                  </svg>
                </div>
                <div>
                  <h2 className="text-3xl font-bold text-gray-800">Enrolled Courses</h2>
                  <p className="text-gray-600">Continue your learning journey</p>
                </div>
              </div>
            </div>

            {enrollments.length > 0 ? (
              <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
                {enrollments.map((enrollment) => {
                  const course = enrollment.course;
                  const instructor = course.instructor;
                  return (
                    <div
                      key={course._id}
                      className="overflow-hidden transition-all duration-300 bg-white border border-gray-100 shadow-lg group rounded-2xl hover:shadow-2xl hover:-translate-y-2"
                    >
                      <div className="relative overflow-hidden">
                        <img
                          src={course.image || course.coverImage || 'https://via.placeholder.com/300x200?text=Course'}
                          alt={course.title}
                          className="object-cover object-center w-full h-48 transition-transform duration-300 group-hover:scale-105"
                          onError={e => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/300x200?text=Course'; }}
                        />
                        <div className="absolute top-4 right-4">
                          {enrollment.certificateIssued && (
                            <span className="px-3 py-1 text-xs font-semibold text-white bg-green-500 rounded-full">
                              Certified
                            </span>
                          )}
                        </div>
                      </div>

                      <div className="p-6">
                        <h3 className="mb-3 text-xl font-bold text-gray-800 line-clamp-2" title={course.title}>{course.title}</h3>

                        {instructor && (
                          <div className="flex items-center gap-2 mb-3 text-sm text-gray-600">
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                            </svg>
                            <span>{instructor.firstName ? `${instructor.firstName} ${instructor.lastName}` : instructor.name || instructor.email || 'N/A'}</span>
                          </div>
                        )}

                        <div className="mb-4">
                          <div className="flex justify-between mb-2 text-sm text-gray-600">
                            <span>Progress</span>
                            <span className="font-semibold">{enrollment.progress || 0}%</span>
                          </div>
                          <div className="w-full h-3 bg-gray-200 rounded-full">
                            <div
                              className="h-3 transition-all duration-300 rounded-full bg-gradient-to-r from-blue-500 to-purple-600"
                              style={{ width: `${enrollment.progress || 0}%` }}
                            ></div>
                          </div>
                        </div>

                        <div className="flex items-center justify-between mb-4">
                          <div className="text-xs text-gray-500">
                            Enrolled: {enrollment.enrolledAt ? new Date(enrollment.enrolledAt).toLocaleDateString() : '-'}
                          </div>
                        </div>

                        <div className="flex flex-wrap gap-2">
                          <Link
                            to={`/course-contents/${course._id}`}
                            className="flex-1 px-4 py-2 font-semibold text-center text-white transition-all duration-300 rounded-lg bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700"
                            aria-label={`Continue ${course.title}`}
                          >
                            Continue Learning
                          </Link>
                          {enrollment.certificateIssued && (
                            <button
                              className="px-4 py-2 text-sm font-semibold text-white transition-colors bg-green-500 rounded-lg hover:bg-green-600"
                              disabled
                              title="Certificate download coming soon"
                            >
                              Download Certificate
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="py-16 text-center">
                <div className="flex items-center justify-center w-24 h-24 mx-auto mb-6 bg-gray-100 rounded-full">
                  <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                  </svg>
                </div>
                <h3 className="mb-2 text-xl font-semibold text-gray-800">No courses enrolled yet</h3>
                <p className="mb-6 text-gray-600">Start your learning journey by enrolling in courses</p>
                <Link
                  to="/courses"
                  className="inline-flex items-center px-6 py-3 font-semibold text-white transition-all duration-300 rounded-full bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-blue-300 focus:ring-opacity-50"
                >
                  <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                  </svg>
                  Browse Courses
                </Link>
              </div>
            )}
          </div>
        )}
      </div>
    </section>
  );
};

export default Profile;
