                      <button type="submit" className="px-4 py-2 bg-primary text-white rounded" disabled={submitting}>{submitting ? 'Adding...' : 'Add Content'}</button>
                    </div>
                  </form>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
