                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative w-full max-w-6xl p-8 mx-auto bg-white rounded-lg shadow-xl">
                  <Dialog.Title className="mb-6 text-2xl font-bold text-primary">
                    Assignment Submissions: {instructorActiveAssignment?.title}
                  </Dialog.Title>
                  <div className="mb-4 text-gray-600">
                    <p>Total Submissions: <span className="font-semibold text-primary">{instructorAssignmentSubmissions.length}</span></p>
                    <p>Due Date: <span className="font-semibold">{instructorActiveAssignment?.dueDate ? new Date(instructorActiveAssignment.dueDate).toLocaleDateString() : 'No due date'}</span></p>
                  </div>

                  {instructorAssignmentSubmissions.length === 0 ? (
                    <div className="text-center py-12">
                      <div className="text-gray-400 text-6xl mb-4"></div>
                      <div className="text-gray-600 text-xl">No submissions yet</div>
                      <div className="text-gray-500">Students haven't submitted their assignments for this content.</div>
                    </div>
                  ) : (
                    <div className="space-y-4 max-h-96 overflow-y-auto">
                      {instructorAssignmentSubmissions.map((submission) => {
                        const isGraded = submission.grade !== undefined && submission.grade !== null && submission.grade !== '';
                        const isLate = instructorActiveAssignment?.dueDate && new Date(submission.submittedAt) > new Date(instructorActiveAssignment.dueDate);
                        const changed = grading[submission._id]?.grade !== undefined && grading[submission._id]?.grade !== submission.grade || grading[submission._id]?.feedback !== undefined && grading[submission._id]?.feedback !== submission.feedback;

                            return (
                          <div key={submission._id} className={`border rounded-lg p-4 ${isGraded ? 'bg-green-50 border-green-200' : 'bg-white border-gray-200'} ${isLate ? 'border-red-300 bg-red-50' : ''}`}>
                            <div className="flex items-start justify-between">
                              <div className="flex-1">
                                <div className="flex items-center gap-3 mb-3">
                                  <div className="w-10 h-10 bg-primary text-white rounded-full flex items-center justify-center font-semibold">
                                    {submission.student?.firstName?.charAt(0)}{submission.student?.lastName?.charAt(0)}
                                  </div>
                                  <div>
                                    <h3 className="font-semibold text-lg">
                                      {submission.student?.firstName} {submission.student?.lastName}
                                    </h3>
                                    <p className="text-gray-600 text-sm">{submission.student?.email}</p>
                                  </div>
                                  <div className="flex gap-2">
                                    {isLate && (
                                      <span className="px-2 py-1 bg-red-100 text-red-700 rounded-full text-xs font-semibold">
                                        Late Submission
                                      </span>
                                    )}
                                    {isGraded && (
                                      <span className="px-2 py-1 bg-green-100 text-green-700 rounded-full text-xs font-semibold">
                                        Graded
                                      </span>
                                    )}
                                  </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                                  <div>
                                    <label className="text-sm font-semibold text-gray-700">Submitted At:</label>
                                    <p className="text-sm text-gray-600">{new Date(submission.submittedAt).toLocaleString()}</p>
                                  </div>
                                  <div>
                                    <label className="text-sm font-semibold text-gray-700">File:</label>
                                    <div className="flex items-center gap-2">
                                      <span className="text-xs text-gray-500">
                                        {submission.fileUrl?.split('/').pop() || 'No file'}
                                      </span>
                                      {submission.fileUrl ? (
                                        <>
                                          <a 
                                            href={submission.fileUrl} 
                                            target="_blank" 
                                            rel="noopener noreferrer" 
                                            className="text-blue-600 hover:text-blue-800 underline text-sm font-medium"
                                          >
                                            Download
                                          </a>
                                          <button
                                            onClick={() => openSubmissionDetail(submission)}
                                            className="text-indigo-600 hover:text-indigo-800 text-sm font-medium"
                                          >
                                            Details
                                          </button>
                                        </>
                                      ) : (
                                        <span className="text-xs text-gray-400">No file</span>
                                      )}
                                    </div>
                                  </div>
                                  <div>
                                    <label className="text-sm font-semibold text-gray-700">Comments:</label>
                                    <p className="text-sm text-gray-600">
                                      {submission.comments || <span className="italic text-gray-400">No comments</span>}
                                    </p>
                                  </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                  <div>
                                    <label className="text-sm font-semibold text-gray-700">Grade:</label>
                                  <div className="flex items-center gap-2">
                                    <input
                                      type="number"
                                        min="0"
                                        max="100"
                                        className="w-20 border rounded px-2 py-1 text-sm"
                                        value={grading[submission._id]?.grade ?? submission.grade ?? ''}
                                        onChange={e => handleGradeChange(submission._id, 'grade', e.target.value)}
                                        placeholder="0-100"
                                      />
                                      {isGraded && <span className="text-green-700 font-bold"></span>}
                                  </div>
                                  </div>
                                  <div>
                                    <label className="text-sm font-semibold text-gray-700">Feedback:</label>
                                  <input
                                    type="text"
                                      className="w-full border rounded px-2 py-1 text-sm"
                                      value={grading[submission._id]?.feedback ?? submission.feedback ?? ''}
                                      onChange={e => handleGradeChange(submission._id, 'feedback', e.target.value)}
                                      placeholder="Add feedback..."
                                    />
                                  </div>
                                </div>

                                {submission.gradedBy && (
                                  <div className="mt-3 text-xs text-gray-500">
                                    Graded by: {submission.gradedBy?.name || submission.gradedBy?.email || submission.gradedBy} 
                                    on {submission.gradedAt && new Date(submission.gradedAt).toLocaleString()}
                                  </div>
                                )}
                              </div>

                              <div className="ml-4">
                                  <button
                                  onClick={() => handleGradeSubmit(submission._id)}
                                  className={`px-4 py-2 bg-primary text-white rounded font-semibold text-sm ${!changed ? 'opacity-50 cursor-not-allowed' : 'hover:bg-secondary'}`}
                                  disabled={grading[submission._id]?.loading || !changed}
                                >
                                  {grading[submission._id]?.loading ? 'Saving...' : 'Save Grade'}
                                  </button>
                                    </div>
                            </div>
                          </div>
                            );
                          })}
                    </div>
                  )}

                  <div className="flex justify-end mt-6 pt-4 border-t">
                    <button 
                      onClick={() => setInstructorModalOpen(false)} 
                      className="px-6 py-2 text-white rounded bg-primary hover:bg-secondary transition"
                    >
                      Close
                    </button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>

      {/* Submission Detail Modal */}
      <Transition appear show={submissionDetailModal} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setSubmissionDetailModal(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative w-full max-w-4xl p-8 mx-auto bg-white rounded-lg shadow-xl">
                  <Dialog.Title className="mb-6 text-2xl font-bold text-primary">
                    Submission Details
                  </Dialog.Title>

                  {selectedSubmission && (
                    <div className="space-y-6">
                      {/* Student Information */}
                      <div className="bg-gray-50 rounded-lg p-4">
                        <h3 className="text-lg font-semibold mb-3 text-primary">Student Information</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <label className="text-sm font-semibold text-gray-700">Name:</label>
                            <p className="text-gray-900">{selectedSubmission.student?.firstName} {selectedSubmission.student?.lastName}</p>
                          </div>
                          <div>
                            <label className="text-sm font-semibold text-gray-700">Email:</label>
                            <p className="text-gray-900">{selectedSubmission.student?.email}</p>
                          </div>
                        </div>
                      </div>

                      {/* Submission Information */}
                      <div className="bg-blue-50 rounded-lg p-4">
                        <h3 className="text-lg font-semibold mb-3 text-primary">Submission Information</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <label className="text-sm font-semibold text-gray-700">Submitted At:</label>
                            <p className="text-gray-900">{new Date(selectedSubmission.submittedAt).toLocaleString()}</p>
                          </div>
                          <div>
                            <label className="text-sm font-semibold text-gray-700">Status:</label>
                            <div className="flex gap-2">
                              {selectedSubmission.grade !== undefined && selectedSubmission.grade !== null && selectedSubmission.grade !== '' ? (
                                <span className="px-2 py-1 bg-green-100 text-green-700 rounded-full text-xs font-semibold">
                                  Graded ({selectedSubmission.grade}/100)
                                </span>
                              ) : (
                                <span className="px-2 py-1 bg-yellow-100 text-yellow-700 rounded-full text-xs font-semibold">
                                  Pending Grade
                                </span>
                              )}
                              {instructorActiveAssignment?.dueDate && new Date(selectedSubmission.submittedAt) > new Date(instructorActiveAssignment.dueDate) && (
                                <span className="px-2 py-1 bg-red-100 text-red-700 rounded-full text-xs font-semibold">
                                  Late Submission
                                </span>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* File Information */}
                      <div className="bg-green-50 rounded-lg p-4">
                        <h3 className="text-lg font-semibold mb-3 text-primary">Submitted File</h3>
                        <div className="flex items-center gap-4">
                          <div className="w-12 h-12 bg-primary text-white rounded-lg flex items-center justify-center">
                            {selectedSubmission.fileUrl?.includes('.pdf') ? '' : 
                             selectedSubmission.fileUrl?.includes('.doc') ? '' :
                             selectedSubmission.fileUrl?.includes('.jpg') || selectedSubmission.fileUrl?.includes('.png') ? '' :
                             selectedSubmission.fileUrl?.includes('.mp4') || selectedSubmission.fileUrl?.includes('.mov') ? '' : ''}
                          </div>
                          <div className="flex-1">
                            <p className="font-semibold">Assignment File</p>
                            <p className="text-sm text-gray-600">
                              {selectedSubmission.fileUrl?.split('/').pop() || 'File'}
                            </p>
                            <p className="text-xs text-gray-500">
                              {selectedSubmission.fileUrl?.startsWith('https://') ? 'Cloud Storage' : 'Local File'}
                            </p>
                          </div>
                          <div className="flex gap-2">
                            {selectedSubmission.fileUrl ? (
                              <>
                                <a 
                                  href={selectedSubmission.fileUrl} 
                                  target="_blank" 
                                  rel="noopener noreferrer" 
                                  className="px-4 py-2 bg-primary text-white rounded hover:bg-secondary transition"
                                >
                                  Download File
                                </a>
                                {(selectedSubmission.fileUrl?.includes('.pdf') || 
                                  selectedSubmission.fileUrl?.includes('.jpg') || 
                                  selectedSubmission.fileUrl?.includes('.png') ||
                                  selectedSubmission.fileUrl?.includes('.mp4')) && (
                                  <a 
                                    href={selectedSubmission.fileUrl} 
                                    target="_blank" 
                                    rel="noopener noreferrer" 
                                    className="px-4 py-2 bg-indigo-500 text-white rounded hover:bg-indigo-600 transition"
                                  >
                                    Preview
                                  </a>
                                )}
                              </>
                            ) : (
                              <span className="px-4 py-2 bg-gray-300 text-gray-600 rounded cursor-not-allowed">
                                No file available
                              </span>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Comments */}
                      {selectedSubmission.comments && (
                        <div className="bg-yellow-50 rounded-lg p-4">
                          <h3 className="text-lg font-semibold mb-3 text-primary">Student Comments</h3>
                          <p className="text-gray-900 bg-white p-3 rounded border">{selectedSubmission.comments}</p>
                        </div>
                      )}

                      {/* Grading Information */}
                      {selectedSubmission.gradedBy && (
                        <div className="bg-purple-50 rounded-lg p-4">
                          <h3 className="text-lg font-semibold mb-3 text-primary">Grading Information</h3>
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                              <label className="text-sm font-semibold text-gray-700">Graded By:</label>
                              <p className="text-gray-900">{selectedSubmission.gradedBy?.name || selectedSubmission.gradedBy?.email || selectedSubmission.gradedBy}</p>
                            </div>
                            <div>
                              <label className="text-sm font-semibold text-gray-700">Graded At:</label>
                              <p className="text-gray-900">{selectedSubmission.gradedAt && new Date(selectedSubmission.gradedAt).toLocaleString()}</p>
                            </div>
                            {selectedSubmission.feedback && (
                              <div className="md:col-span-2">
                                <label className="text-sm font-semibold text-gray-700">Feedback:</label>
                                <p className="text-gray-900 bg-white p-3 rounded border">{selectedSubmission.feedback}</p>
                              </div>
                            )}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  <div className="flex justify-end mt-6 pt-4 border-t">
                    <button 
                      onClick={() => setSubmissionDetailModal(false)} 
                      className="px-6 py-2 text-white rounded bg-primary hover:bg-secondary transition"
                    >
                      Close
                    </button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
    </>
  );
};

export default InstructorCourseDetails;
