import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const location = useLocation();
  const successMessage = location.state?.message;

  useEffect(() => {
    if (successMessage) {
      toast.success(successMessage);
    }
  }, [successMessage]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_CONFIG.BASE_URL}/auth/login`, {
        email,
        password,
      });

      localStorage.setItem('token', res.data.token);

      toast.success('Login successful! Redirecting...');

      setTimeout(() => {
        const role = res.data.user.role;
        if (role === 'Admin') {
          navigate('/admin/dashboard');
        } else {
          navigate('/');
        }
      }, 1500);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Login failed. Please try again.');
    }
  };

  return (
    <>
      <ToastContainer
        position="top-center"
        autoClose={3000}
        theme="colored"
        pauseOnHover
        draggable
      />
      <section className="min-h-screen bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 flex items-center justify-center px-4">
        <div className="bg-white rounded-xl shadow-xl max-w-md w-full p-8">
          <h2 className="text-indigo-900 text-3xl font-extrabold mb-6 text-center">
            Login to EduVerse
          </h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label htmlFor="email" className="block text-indigo-900 font-semibold mb-1">
                Email
              </label>
              <input
                id="email"
                type="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                placeholder="your.email@example.com"
              />
            </div>
            <div>
              <label htmlFor="password" className="block text-indigo-900 font-semibold mb-1">
                Password
              </label>
              <input
                id="password"
                type="password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 border border-indigo-300 rounded-md focus:outline-none focus:ring-2 focus:ring-yellow-400 transition"
                placeholder="Enter your password"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-yellow-400 text-indigo-900 font-bold py-2 rounded-lg hover:bg-yellow-500 transition focus:outline-none focus:ring-4 focus:ring-yellow-300"
            >
              Login
            </button>
          </form>
          <p className="mt-6 text-indigo-900 text-center">
            Don't have an account?{' '}
            <Link
              to="/signup"
              className="font-semibold hover:text-yellow-400 transition"
            >
              Sign up
            </Link>
          </p>
        </div>
      </section>
    </>
  );
};

export default Login;
