import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  let role = null;

  if (token) {
    try {
      const decoded = jwtDecode(token);
      role = decoded.role;
    } catch (error) {
      console.error('Invalid token:', error);
      localStorage.removeItem('token'); // Clear invalid token
      navigate('/login'); // Redirect to login
    }
  }

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login', { state: { message: 'Logged out successfully!' } });
  };

  return (
    <nav className="bg-blue-600 text-white p-4">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        <Link to="/" className="text-2xl font-bold" aria-label="EduVerse Home">
          EduVerse
        </Link>
        <div className="space-x-4 flex flex-wrap items-center">
          <Link
            to="/"
            className="hover:text-gray-200 transition py-2 px-3"
            aria-label="Home"
          >
            Home
          </Link>
          <Link
            to="/courses"
            className="hover:text-gray-200 transition py-2 px-3"
            aria-label="Courses"
          >
            Courses
          </Link>
          {token ? (
            <>
              <Link
                to="/profile"
                className="hover:text-gray-200 transition py-2 px-3"
                aria-label="Profile"
              >
                Profile
              </Link>
              {role === 'Admin' && (
                <Link
                  to="/admin/dashboard"
                  className="hover:text-gray-200 transition py-2 px-3"
                  aria-label="Admin Dashboard"
                >
                  Admin Dashboard
                </Link>
              )}
              <button
                onClick={handleLogout}
                className="bg-primary text-white py-2 px-4 rounded-md hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 transition"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="hover:text-gray-200 transition py-2 px-3"
                aria-label="Login"
              >
                Login
              </Link>
              <Link
                to="/signup"
                className="bg-primary text-white py-2 px-4 rounded-md hover:bg-secondary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 transition"
                aria-label="Signup"
              >
                Signup
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
