import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-primary text-white p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold">EduVerse</Link>
        <div className="space-x-4">
          <Link to="/" className="hover:text-gray-200 transition">Home</Link>
          {!token ? (
            <>
              <Link to="/login" className="hover:text-gray-200 transition">Login</Link>
              <Link to="/signup" className="hover:text-gray-200 transition">Sign Up</Link>
            </>
          ) : (
            <button
              onClick={handleLogout}
              className="bg-secondary text-white px-3 py-1 rounded-md hover:bg-blue-800 transition"
            >
              Logout
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
