import React, { useEffect, useState, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { Dialog, Transition } from '@headlessui/react';
import { Fragment } from 'react';
import { jwtDecode } from 'jwt-decode';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CourseContents = () => {
  const { courseId } = useParams();
  const [contents, setContents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [quizModalOpen, setQuizModalOpen] = useState(false);
  const [activeQuiz, setActiveQuiz] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [quizSubmitting, setQuizSubmitting] = useState(false);
  const [quizResult, setQuizResult] = useState(null);
  const [quizSubmission, setQuizSubmission] = useState({}); // {quizContentId: {score, answers}}
  const [userRole, setUserRole] = useState('');
  const [analyticsModalOpen, setAnalyticsModalOpen] = useState(false);
  const [quizAnalytics, setQuizAnalytics] = useState(null);
  const [analyticsQuiz, setAnalyticsQuiz] = useState(null);
  const [resultModalOpen, setResultModalOpen] = useState(false);
  const [resultQuiz, setResultQuiz] = useState(null);
  const [assignmentSubmission, setAssignmentSubmission] = useState({}); // {assignmentContentId: submission}
  const [assignmentModalOpen, setAssignmentModalOpen] = useState(false);
  const [activeAssignment, setActiveAssignment] = useState(null);
  const [assignmentFile, setAssignmentFile] = useState(null);
  const [assignmentComments, setAssignmentComments] = useState('');
  const [assignmentSubmitting, setAssignmentSubmitting] = useState(false);
  const [viewSubmissionModal, setViewSubmissionModal] = useState(false);
  const [viewSubmissionAssignment, setViewSubmissionAssignment] = useState(null);
  const [instructorModalOpen, setInstructorModalOpen] = useState(false);
  const [instructorActiveAssignment, setInstructorActiveAssignment] = useState(null);
  const [instructorAssignmentSubmissions, setInstructorAssignmentSubmissions] = useState([]);
  const [grading, setGrading] = useState({}); // {submissionId: {grade, feedback, loading}}
  const fileInputRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`${API_CONFIG.BASE_URL}/course-content/course/${courseId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setContents(res.data.contents))
      .catch(() => setContents([]))
      .finally(() => setLoading(false));
    // Get user role
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserRole(decoded.role);
      } catch {}
    }
  }, [courseId]);

  // Fetch quiz submission for each quiz on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    contents.forEach(content => {
      if (content.type === 'quiz') {
        axios.get(`${API_CONFIG.BASE_URL}/quiz-submissions/${content._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        }).then(res => {
          setQuizSubmission(prev => ({ ...prev, [content._id]: res.data.submission }));
        }).catch(() => {});
      }
    });
    // eslint-disable-next-line
  }, [contents]);

  // Fetch assignment submission for each assignment on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    contents.forEach(content => {
      if (content.type === 'assignment') {
        axios.get(`${API_CONFIG.BASE_URL}/assignment-submissions/${content._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        }).then(res => {
          setAssignmentSubmission(prev => ({ ...prev, [content._id]: res.data.submission }));
        }).catch(() => {});
      }
    });
    // eslint-disable-next-line
  }, [contents]);

  const openQuiz = (quiz) => {
    setActiveQuiz(quiz);
    setQuizAnswers({});
    setQuizResult(null);
    setQuizModalOpen(true);
  };

  const handleQuizAnswer = (q, val) => {
    setQuizAnswers(prev => ({ ...prev, [q]: val }));
  };

  const handleQuizSubmit = async (e) => {
    e.preventDefault();
    setQuizSubmitting(true);
    const token = localStorage.getItem('token');
    const answers = (activeQuiz.questions || []).map(q => ({ question: q.question, selected: quizAnswers[q.question] || '' }));
    try {
      const res = await axios.post(`${API_CONFIG.BASE_URL}/quiz-submissions`, {
        quizContentId: activeQuiz._id,
        answers
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuizResult(res.data);
      setQuizSubmission(prev => ({ ...prev, [activeQuiz._id]: { score: res.data.score, answers: res.data.answers } }));
    } catch (err) {
      setQuizResult({ error: err.response?.data?.message || 'Submission failed' });
    } finally {
      setQuizSubmitting(false);
    }
  };

  const openQuizAnalytics = async (quiz) => {
    setAnalyticsQuiz(quiz);
    setQuizAnalytics(null);
    setAnalyticsModalOpen(true);
    const token = localStorage.getItem('token');
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/quiz-submissions/analytics/${quiz._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuizAnalytics(res.data);
    } catch (err) {
      setQuizAnalytics({ error: err.response?.data?.message || 'Failed to load analytics' });
    }
  };

  const openAssignmentModal = (assignment) => {
    setActiveAssignment(assignment);
    setAssignmentFile(null);
    setAssignmentComments(assignmentSubmission[assignment._id]?.comments || '');
    setAssignmentModalOpen(true);
  };

  const handleAssignmentFileChange = (e) => {
    setAssignmentFile(e.target.files[0]);
  };

  const handleAssignmentSubmit = async (e) => {
    e.preventDefault();
    setAssignmentSubmitting(true);
    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('assignmentContentId', activeAssignment._id);
    formData.append('comments', assignmentComments);
    if (assignmentFile) formData.append('file', assignmentFile);
    try {
      const res = await axios.post(`${API_CONFIG.BASE_URL}/assignment-submissions`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAssignmentSubmission(prev => ({ ...prev, [activeAssignment._id]: res.data.submission }));
      setAssignmentModalOpen(false);
    } catch (err) {
      alert(err.response?.data?.message || 'Submission failed');
    } finally {
      setAssignmentSubmitting(false);
    }
  };

  const openViewSubmissionModal = (assignment) => {
    setViewSubmissionAssignment(assignment);
    setViewSubmissionModal(true);
  };

  const openInstructorModal = async (assignment) => {
    setInstructorActiveAssignment(assignment);
    setInstructorModalOpen(true);
    setInstructorAssignmentSubmissions([]);
    const token = localStorage.getItem('token');
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/assignment-submissions/all/${assignment._id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setInstructorAssignmentSubmissions(res.data.submissions);
    } catch {}
  };

  const handleGradeChange = (submissionId, field, value) => {
    setGrading(prev => ({ ...prev, [submissionId]: { ...prev[submissionId], [field]: value } }));
  };

  const handleGradeSubmit = async (submissionId) => {
    setGrading(prev => ({ ...prev, [submissionId]: { ...prev[submissionId], loading: true } }));
    const token = localStorage.getItem('token');
    const { grade, feedback } = grading[submissionId] || {};
    try {
      const res = await axios.put(`${API_CONFIG.BASE_URL}/assignment-submissions/${submissionId}/grade`, { grade, feedback }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setInstructorAssignmentSubmissions(subs => subs.map(s => s._id === submissionId ? res.data.submission : s));
      toast.success('Grade saved!');
    } catch {
      toast.error('Failed to save grade');
    }
    setGrading(prev => ({ ...prev, [submissionId]: { ...prev[submissionId], loading: false } }));
  };

  if (loading) return <div className="p-8">Loading...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 flex flex-col">
      {/* Hero Header */}
      <div className="w-full bg-primary text-white py-10 shadow-lg rounded-b-3xl mb-8">
        <div className="max-w-4xl mx-auto px-4">
          <h1 className="text-4xl font-extrabold mb-2 tracking-tight">Course Contents</h1>
          <p className="text-lg opacity-90">All your learning materials, quizzes, and activities in one place.</p>
        </div>
      </div>
      <div className="flex-1 w-full max-w-4xl mx-auto px-4">
        {contents.length === 0 ? (
          <div className="text-center text-gray-600 text-lg mt-16">No content available for this course yet.</div>
        ) : (
          <div className="space-y-8">
            {contents.map(content => (
              <div key={content._id} className={`rounded-2xl shadow-lg p-6 bg-white flex flex-col md:flex-row md:items-center gap-6 border-l-8 ${content.type === 'quiz' ? 'border-indigo-500' : content.type === 'assignment' ? 'border-yellow-400' : content.type === 'poll' ? 'border-pink-400' : content.type === 'survey' ? 'border-green-400' : 'border-gray-200'}`}>
                <div className="flex-shrink-0 flex flex-col items-center justify-center w-16 h-16 rounded-full bg-gradient-to-br from-primary to-indigo-400 text-white text-2xl shadow">
                  {content.type === 'quiz' && <span></span>}
                  {content.type === 'assignment' && <span></span>}
                  {content.type === 'video' && <span></span>}
                  {content.type === 'slide' && <span></span>}
                  {content.type === 'document' && <span></span>}
                  {content.type === 'poll' && <span></span>}
                  {content.type === 'survey' && <span></span>}
                  {content.type === 'live' && <span></span>}
                  {content.type === 'notice' && <span></span>}
                </div>
                <div className="flex-1">
                  <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-2">
                    <div>
                      <h2 className="text-xl font-bold text-primary mb-1 flex items-center gap-2">
                        {content.title}
                        <span className={`text-xs px-2 py-1 rounded-full ml-2 ${content.type === 'quiz' ? 'bg-indigo-100 text-indigo-700' : content.type === 'assignment' ? 'bg-yellow-100 text-yellow-700' : content.type === 'poll' ? 'bg-pink-100 text-pink-700' : content.type === 'survey' ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}`}>{content.type.charAt(0).toUpperCase() + content.type.slice(1)}</span>
                      </h2>
                      <div className="text-gray-700 mb-2">{content.description}</div>
                    </div>
                    <div className="flex flex-wrap gap-2 items-center">
                      {content.contentUrl && <a href={content.contentUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline font-medium">View File/Video</a>}
                      {content.externalLink && <a href={content.externalLink} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline font-medium">External Link</a>}
                    </div>
                  </div>
                  {content.noticeText && <div className="mt-2 text-yellow-700 font-semibold flex items-center gap-2"><span></span>{content.noticeText}</div>}
                  {content.liveDate && <div className="mt-2 text-green-700 font-semibold flex items-center gap-2"><span></span>Live Date: {new Date(content.liveDate).toLocaleString()}</div>}
                  {content.dueDate && <div className="mt-2 text-red-700 font-semibold flex items-center gap-2"><span></span>Due Date: {new Date(content.dueDate).toLocaleDateString()}</div>}
                  {/* Quiz Section */}
                  {content.type === 'quiz' && content.questions && (
                    <div className="mt-4">
                      <div className="flex items-center gap-2 mb-2">
                        <span className="text-indigo-700 font-semibold">Quiz:</span>
                        <span className="text-sm">{content.questions.length} questions</span>
                      </div>
                      {quizSubmission[content._id] ? (
                        <div className="flex gap-2">
                          <button
                            onClick={() => { setResultQuiz(content); setResultModalOpen(true); }}
                            className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 font-semibold shadow"
                          >
                            View Results
                          </button>
                        </div>
                      ) : (
                        <button onClick={() => openQuiz(content)} className="px-4 py-2 bg-primary text-white rounded hover:bg-secondary font-semibold shadow">Take Quiz</button>
                      )}
                      {userRole === 'Instructor' && (
                        <button onClick={() => openQuizAnalytics(content)} className="ml-4 px-3 py-1 bg-yellow-400 text-indigo-900 rounded hover:bg-yellow-500 font-semibold shadow">View Analytics</button>
                      )}
                    </div>
                  )}
                  {/* Assignment Section */}
                  {content.type === 'assignment' && (
                    <div className="mt-4 flex flex-col gap-2">
                      <div className="flex items-center gap-2 text-yellow-700 font-semibold">
                        <span></span>Assignment
                        {content.dueDate && new Date(content.dueDate) < new Date() && (
                          <span className="ml-2 px-2 py-0.5 bg-red-100 text-red-700 rounded text-xs">Past Due</span>
                        )}
                        {assignmentSubmission[content._id] && (
                          <span className="ml-2 px-2 py-0.5 bg-green-100 text-green-700 rounded text-xs">Submitted</span>
                        )}
                      </div>
                      <div className="text-gray-700">{content.description}</div>
                      {content.contentUrl && <a href={content.contentUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline font-medium">Download Assignment</a>}
                      <div className="flex gap-2 mt-2">
                        {userRole !== 'Instructor' && (!assignmentSubmission[content._id] || (content.dueDate && new Date(content.dueDate) > new Date())) && (
                          <button onClick={() => openAssignmentModal(content)} className="px-4 py-2 bg-yellow-400 text-indigo-900 rounded hover:bg-yellow-500 font-semibold shadow">{assignmentSubmission[content._id] ? 'Resubmit Assignment' : 'Submit Assignment'}</button>
                        )}
                        {assignmentSubmission[content._id] && userRole !== 'Instructor' && (
                          <button onClick={() => openViewSubmissionModal(content)} className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600 font-semibold shadow">View Submission</button>
                        )}
                        {userRole === 'Instructor' && (
                          <button onClick={() => openInstructorModal(content)} className="px-4 py-2 bg-yellow-400 text-indigo-900 rounded hover:bg-yellow-500 font-semibold shadow">View Submissions</button>
                        )}
                      </div>
                    </div>
                  )}
                  {/* Poll Section */}
                  {content.type === 'poll' && content.pollOptions && (
                    <div className="mt-4 text-pink-700 font-semibold flex items-center gap-2">
                      <span></span>Poll Options: {content.pollOptions.map(opt => opt.option).join(', ')}
                    </div>
                  )}
                  {/* Survey Section */}
                  {content.type === 'survey' && content.surveyQuestions && (
                    <div className="mt-4 text-green-700 font-semibold flex items-center gap-2">
                      <span></span>Survey: {content.surveyQuestions.length} questions
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
        <Link to="/profile" className="inline-block mt-12 text-primary hover:underline font-semibold">&larr; Back to Profile</Link>
      </div>

      {/* Quiz Modal */}
      <Transition appear show={quizModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setQuizModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-lg mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz: {activeQuiz?.title}</Dialog.Title>
                  {quizResult && quizResult.error && (
                    <div className="mb-4 text-red-600 font-semibold">{quizResult.error}</div>
                  )}
                  {quizResult && !quizResult.error ? (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">Your Score: {quizResult.score} / {activeQuiz.questions.length}</div>
                      <ul className="space-y-2">
                        {quizResult.answers.map((a, idx) => (
                          <li key={idx} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                            <span className="font-semibold">Q:</span> {a.question}<br />
                            <span className="font-semibold">Your Answer:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                            <span className="font-semibold">Correct:</span> {a.correct}
                          </li>
                        ))}
                      </ul>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setQuizModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  ) : (
                    <form onSubmit={handleQuizSubmit} className="space-y-6">
                      {activeQuiz?.questions?.map((q, idx) => (
                        <div key={idx} className="mb-4">
                          <div className="font-semibold mb-2">Q{idx + 1}: {q.question}</div>
                          <div className="space-y-1">
                            {q.options.map((opt, oidx) => (
                              <label key={oidx} className="flex items-center gap-2">
                                <input
                                  type="radio"
                                  name={`q_${idx}`}
                                  value={opt}
                                  checked={quizAnswers[q.question] === opt}
                                  onChange={() => handleQuizAnswer(q.question, opt)}
                                  className="form-radio text-primary"
                                  required
                                />
                                {opt}
                              </label>
                            ))}
                          </div>
                        </div>
                      ))}
                      <div className="flex justify-end gap-2 mt-4">
                        <button type="button" onClick={() => setQuizModalOpen(false)} className="px-4 py-2 bg-gray-200 rounded">Cancel</button>
                        <button type="submit" className="px-4 py-2 bg-primary text-white rounded" disabled={quizSubmitting}>{quizSubmitting ? 'Submitting...' : 'Submit Quiz'}</button>
                      </div>
                    </form>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* Quiz Results Modal */}
      <Transition appear show={resultModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setResultModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-lg mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz Results: {resultQuiz?.title}</Dialog.Title>
                  {resultQuiz && quizSubmission[resultQuiz._id] && (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">
                        Your Score: {quizSubmission[resultQuiz._id].score} / {resultQuiz.questions.length}
                      </div>
                      <ul className="space-y-2">
                        {quizSubmission[resultQuiz._id].answers.map((a, idx) => (
                          <li key={idx} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                            <span className="font-semibold">Q:</span> {a.question}<br />
                            <span className="font-semibold">Your Answer:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                            <span className="font-semibold">Correct:</span> {a.correct}
                          </li>
                        ))}
                      </ul>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setResultModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* Assignment Submission Modal (Student) */}
      <Transition appear show={assignmentModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setAssignmentModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative w-full max-w-lg p-8 mx-auto bg-white rounded-lg shadow-xl">
                  <Dialog.Title className="mb-4 text-xl font-bold">{assignmentSubmission[activeAssignment?._id] ? 'Resubmit Assignment' : 'Submit Assignment'}</Dialog.Title>
                  <form onSubmit={handleAssignmentSubmit} className="space-y-6">
                    <div>
                      <label className="block font-medium mb-1">Upload File</label>
                      <input type="file" ref={fileInputRef} onChange={handleAssignmentFileChange} className="w-full" required={!assignmentSubmission[activeAssignment?._id]} />
                    </div>
                    <div>
                      <label className="block font-medium mb-1">Comments/Answer</label>
                      <textarea value={assignmentComments} onChange={e => setAssignmentComments(e.target.value)} className="w-full border rounded px-3 py-2" rows="4" placeholder="Add any comments or your answer here..." />
                    </div>
                    <div className="flex justify-end gap-2 mt-4">
                      <button type="button" onClick={() => setAssignmentModalOpen(false)} className="px-4 py-2 bg-gray-200 rounded">Cancel</button>
                      <button type="submit" className="px-4 py-2 bg-yellow-400 text-indigo-900 rounded font-semibold" disabled={assignmentSubmitting}>{assignmentSubmitting ? 'Submitting...' : assignmentSubmission[activeAssignment?._id] ? 'Resubmit' : 'Submit'}</button>
                    </div>
                  </form>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* View Submission Modal (Student) */}
      <Transition appear show={viewSubmissionModal} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setViewSubmissionModal(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative w-full max-w-lg p-8 mx-auto bg-white rounded-lg shadow-xl">
                  <Dialog.Title className="mb-4 text-xl font-bold">Your Submission</Dialog.Title>
                  {assignmentSubmission[viewSubmissionAssignment?._id] ? (
                    <div className="space-y-4">
                      <div><span className="font-semibold">File:</span> <a href={`/${assignmentSubmission[viewSubmissionAssignment._id].fileUrl}`} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">Download</a></div>
                      <div><span className="font-semibold">Comments:</span> {assignmentSubmission[viewSubmissionAssignment._id].comments || <span className="italic text-gray-400">None</span>}</div>
                      <div><span className="font-semibold">Submitted At:</span> {new Date(assignmentSubmission[viewSubmissionAssignment._id].submittedAt).toLocaleString()}</div>
                      {assignmentSubmission[viewSubmissionAssignment._id].grade !== undefined && (
                        <div><span className="font-semibold">Grade:</span> <span className="text-green-700 font-bold">{assignmentSubmission[viewSubmissionAssignment._id].grade}</span></div>
                      )}
                      {assignmentSubmission[viewSubmissionAssignment._id].feedback && (
                        <div><span className="font-semibold">Feedback:</span> {assignmentSubmission[viewSubmissionAssignment._id].feedback}</div>
                      )}
                    </div>
                  ) : (
                    <div className="text-gray-600">No submission found.</div>
                  )}
                  <div className="flex justify-end mt-6">
                    <button onClick={() => setViewSubmissionModal(false)} className="px-4 py-2 text-white rounded bg-primary">Close</button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* Instructor View Submissions Modal */}
      <Transition appear show={instructorModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setInstructorModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative w-full max-w-2xl p-8 mx-auto bg-white rounded-lg shadow-xl">
                  <Dialog.Title className="mb-4 text-xl font-bold">Assignment Submissions: {instructorActiveAssignment?.title}</Dialog.Title>
                  {instructorAssignmentSubmissions.length === 0 ? (
                    <div className="text-gray-600">No submissions yet.</div>
                  ) : (
                    <div className="overflow-x-auto">
                      <table className="min-w-full bg-white border rounded-xl overflow-hidden text-xs">
                        <thead className="bg-gray-100">
                          <tr>
                            <th className="px-4 py-2 border">Student</th>
                            <th className="px-4 py-2 border">File</th>
                            <th className="px-4 py-2 border">Comments</th>
                            <th className="px-4 py-2 border">Submitted At</th>
                            <th className="px-4 py-2 border">Grade</th>
                            <th className="px-4 py-2 border">Feedback</th>
                            <th className="px-4 py-2 border">Action</th>
                          </tr>
                        </thead>
                        <tbody>
                          {instructorAssignmentSubmissions.map((s) => {
                            const isGraded = s.grade !== undefined && s.grade !== null && s.grade !== '';
                            const changed = grading[s._id]?.grade !== undefined && grading[s._id]?.grade !== s.grade || grading[s._id]?.feedback !== undefined && grading[s._id]?.feedback !== s.feedback;
                            return (
                              <tr key={s._id} className={isGraded ? 'bg-green-50' : ''}>
                                <td className="px-4 py-2 border">{s.student?.firstName} {s.student?.lastName}<br /><span className="text-xs text-gray-500">{s.student?.email}</span></td>
                                <td className="px-4 py-2 border"><a href={`/${s.fileUrl}`} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">Download</a></td>
                                <td className="px-4 py-2 border">{s.comments || <span className="italic text-gray-400">None</span>}</td>
                                <td className="px-4 py-2 border">{new Date(s.submittedAt).toLocaleString()}</td>
                                <td className="px-4 py-2 border">
                                  <div className="flex items-center gap-2">
                                    <input
                                      type="number"
                                      className="w-16 border rounded px-2 py-1"
                                      value={grading[s._id]?.grade ?? s.grade ?? ''}
                                      onChange={e => handleGradeChange(s._id, 'grade', e.target.value)}
                                    />
                                    {isGraded && <span className="ml-1 text-green-700 font-bold"></span>}
                                  </div>
                                  {!isGraded && <div className="text-xs text-gray-400">Not graded</div>}
                                </td>
                                <td className="px-4 py-2 border">
                                  <input
                                    type="text"
                                    className="w-32 border rounded px-2 py-1"
                                    value={grading[s._id]?.feedback ?? s.feedback ?? ''}
                                    onChange={e => handleGradeChange(s._id, 'feedback', e.target.value)}
                                  />
                                </td>
                                <td className="px-4 py-2 border">
                                  <button
                                    onClick={() => handleGradeSubmit(s._id)}
                                    className={`px-3 py-1 bg-primary text-white rounded text-xs ${!changed ? 'opacity-50 cursor-not-allowed' : ''}`}
                                    disabled={grading[s._id]?.loading || !changed}
                                  >
                                    {grading[s._id]?.loading ? 'Saving...' : 'Save'}
                                  </button>
                                  {s.gradedBy && (
                                    <div className="text-xs text-gray-500 mt-1">
                                      By: {s.gradedBy?.name || s.gradedBy?.email || s.gradedBy}<br />
                                      {s.gradedAt && new Date(s.gradedAt).toLocaleString()}
                                    </div>
                                  )}
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  )}
                  <div className="flex justify-end mt-6">
                    <button onClick={() => setInstructorModalOpen(false)} className="px-4 py-2 text-white rounded bg-primary">Close</button>
                  </div>
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      {/* Quiz Analytics Modal (Instructor) */}
      <Transition appear show={analyticsModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setAnalyticsModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-2xl mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz Analytics: {analyticsQuiz?.title}</Dialog.Title>
                  {quizAnalytics && quizAnalytics.error && (
                    <div className="mb-4 text-red-600 font-semibold">{quizAnalytics.error}</div>
                  )}
                  {quizAnalytics && !quizAnalytics.error ? (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">Total Submissions: {quizAnalytics.totalSubmissions}</div>
                      <div className="mb-4 text-blue-700 font-semibold">Average Score: {quizAnalytics.averageScore?.toFixed(2)}</div>
                      {/* Per-question stats */}
                      {quizAnalytics.questionStats && quizAnalytics.questionStats.length > 0 && (
                        <div className="mb-6">
                          <h3 className="font-bold text-lg mb-2 text-indigo-700">Per-Question Stats</h3>
                          <ul className="space-y-2">
                            {quizAnalytics.questionStats.map((qs, idx) => (
                              <li key={idx} className="bg-gray-50 rounded p-3 border">
                                <div className="font-semibold">Q{idx + 1}: {qs.question}</div>
                                <div className="text-green-700">Correct: {qs.correct}</div>
                                <div className="text-red-700">Incorrect: {qs.incorrect}</div>
                                {qs.mostCommonWrong && (
                                  <div className="text-yellow-700">Most Common Wrong Answer: {qs.mostCommonWrong}</div>
                                )}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                      <div className="overflow-x-auto">
                        <table className="min-w-full bg-white border rounded-xl overflow-hidden">
                          <thead className="bg-gray-100">
                            <tr>
                              <th className="border px-4 py-2">Student</th>
                              <th className="border px-4 py-2">Score</th>
                              <th className="border px-4 py-2">Submitted At</th>
                              <th className="border px-4 py-2">Answers</th>
                            </tr>
                          </thead>
                          <tbody>
                            {quizAnalytics.submissions.map((s, idx) => (
                              <tr key={idx}>
                                <td className="border px-4 py-2">{s.student?.firstName} {s.student?.lastName} <br /><span className="text-xs text-gray-500">{s.student?.email}</span></td>
                                <td className="border px-4 py-2">{s.score}</td>
                                <td className="border px-4 py-2">{new Date(s.submittedAt).toLocaleString()}</td>
                                <td className="border px-4 py-2">
                                  <ul className="text-xs">
                                    {s.answers.map((a, i) => (
                                      <li key={i} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                                        <span className="font-semibold">Q:</span> {a.question}<br />
                                        <span className="font-semibold">Ans:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                                        <span className="font-semibold">Correct:</span> {a.correct}
                                      </li>
                                    ))}
                                  </ul>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setAnalyticsModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  ) : (
                    <div className="text-gray-600">Loading...</div>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
    </div>
  );
};

export default CourseContents;
