import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);

      axios
        .get(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setUserData(res.data.user))
        .catch(() => toast.error('Failed to load user profile'));

      axios
        .get(`${API_CONFIG.BASE_URL}/courses/enrolled`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((res) => setEnrolledCourses(res.data.courses))
        .catch(() => toast.error('Failed to load enrolled courses'));
    } catch (err) {
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  if (!userData) return null;

  return (
    <section className="bg-gray-50 py-12 px-4 min-h-screen">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />

      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-8 mb-10 text-center">
          <img
            src={userData.profilePicture || 'https://via.placeholder.com/150?text=Profile'}
            alt={userData.fullName}
            className="w-24 h-24 rounded-full mx-auto mb-4 object-cover"
          />
          <h1 className="text-3xl font-bold text-gray-800 mb-2">
            {userData.firstName} {userData.lastName}
          </h1>
          <p className="text-gray-600 mb-4">{userData.role}</p>
          <Link
            to="/edit-profile"
            className="inline-block bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition"
          >
            Edit Profile
          </Link>
        </div>

        {/* User Details */}
        <div className="bg-white rounded-xl shadow-lg p-8 mb-10">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">User Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Email:</span> {userData.email}
              </p>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Role:</span> {userData.role}
              </p>
            </div>
            <div>
              <p className="text-gray-600 mb-2">
                <span className="font-semibold">Bio:</span> {userData.bio || 'No bio provided.'}
              </p>
              <p className="text-gray-600">
                <span className="font-semibold">Contact:</span> {userData.contactInfo || 'Not specified'}
              </p>
            </div>
          </div>
        </div>

        {/* Enrolled Courses */}
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Enrolled Courses</h2>
          {enrolledCourses.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {enrolledCourses.map((course) => (
                <div
                  key={course._id}
                  className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-2xl transition-transform duration-300 hover:-translate-y-1"
                >
                  <img
                    src={course.image || 'https://via.placeholder.com/300x200?text=Course'}
                    alt={course.title}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-5">
                    <h3 className="text-lg font-semibold text-gray-800 mb-3">{course.title}</h3>
                    <div className="relative pt-1">
                      <div className="flex mb-2 items-center justify-between">
                        <span className="text-xs font-semibold text-gray-600">Progress</span>
                        <span className="text-xs font-semibold text-gray-600">
                          {course.progress || 0}%
                        </span>
                      </div>
                      <div className="overflow-hidden h-2 mb-4 text-xs flex rounded bg-gray-200">
                        <div
                          style={{ width: `${course.progress || 0}%` }}
                          className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-blue-600"
                        ></div>
                      </div>
                    </div>
                    <Link
                      to={`/courses/${course._id}`}
                      className="inline-block bg-blue-600 text-white text-sm px-4 py-2 rounded hover:bg-blue-700 transition"
                    >
                      Continue Learning
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-600 text-center">No courses enrolled yet.</p>
          )}
        </div>

        {/* Footer */}
        <footer className="mt-10 text-center text-gray-600">
          <p>© 2025 EduVerse. All rights reserved.</p>
          <div className="mt-4 space-x-4">
            <Link to="/about" className="text-blue-600 hover:underline">About</Link>
            <Link to="/contact" className="text-blue-600 hover:underline">Contact</Link>
            <Link to="/privacy" className="text-blue-600 hover:underline">Privacy Policy</Link>
          </div>
        </footer>
      </div>
    </section>
  );
};

export default Profile;
