import React from 'react';
import { Link } from 'react-router-dom';

const PurchaseSuccess = () => {
  return (
    <div className="flex-1 bg-gray-50 py-12 px-4">
      <div className="max-w-5xl mx-auto text-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">Purchase Successful!</h1>
        <p className="text-gray-600 mb-6">You have successfully enrolled in the course. Check your email for confirmation.</p>
        <Link
          to="/courses"
          className="inline-block bg-blue-600 text-white font-semibold px-6 py-3 rounded-lg hover:bg-blue-700 transition"
        >
          Back to Courses
        </Link>
      </div>
    </div>
  );
};

export default PurchaseSuccess;
