import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [enrollments, setEnrollments] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bio: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await fetch(`${API_CONFIG.BASE_URL}/auth/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        setUser(data.user);
        setFormData({
          firstName: data.user.firstName,
          lastName: data.user.lastName,
          email: data.user.email,
          bio: data.user.bio || '',
        });
      } catch (err) {
        setError('Failed to load user profile');
      }
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    try {
      const decoded = jwtDecode(token);

      // Fetch enrolled courses (for Students)
      if (decoded.role === 'Student') {
        axios
          .get(`${API_CONFIG.BASE_URL}/enrollments/student/${decoded.id}`, {
            headers: { Authorization: `Bearer ${token}` },
          })
          .then((res) => {
            // Remove duplicate enrollments by course._id
            const uniqueEnrollments = [];
            const seen = new Set();
            for (const e of res.data.enrollments) {
              if (!seen.has(e.course._id)) {
                uniqueEnrollments.push(e);
                seen.add(e.course._id);
              }
            }
            setEnrollments(uniqueEnrollments);
          })
          .catch(() => {
            toast.error('Failed to load enrolled courses.');
            setEnrollments([]);
          });
      }
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(
        `${API_CONFIG.BASE_URL}/auth/update`,
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(response.data.user);
      setIsModalOpen(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  if (error) return <div className="min-h-[60vh] flex items-center justify-center text-red-600 text-xl">{error}</div>;
  if (!user) return <div className="min-h-[60vh] flex items-center justify-center text-primary text-xl">Loading...</div>;

  return (
    <div className="min-h-[80vh] bg-gray-50 py-12 px-4">
      <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-lg p-8 flex flex-col items-center">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-6 mb-6 text-center flex flex-col items-center">
          <div className="w-20 h-20 rounded-full bg-gradient-to-br from-primary to-secondary flex items-center justify-center mb-3">
            <span className="text-3xl font-bold text-white">{user.firstName[0]}{user.lastName[0]}</span>
          </div>
          <h1 className="text-2xl font-bold text-gray-800 mb-1">{user.firstName} {user.lastName}</h1>
          <p className="text-gray-600 mb-2 text-sm">{user.role}</p>
          <button
            onClick={() => setIsModalOpen(true)}
            className="inline-block bg-primary text-white px-5 py-1.5 rounded-md hover:bg-secondary transition text-sm"
            aria-label="Edit Profile"
          >
            Edit Profile
          </button>
        </div>
        <h1 className="text-3xl font-extrabold text-primary mb-2">{user.firstName} {user.lastName}</h1>
        <p className="text-gray-600 mb-2">{user.email}</p>
        {user.bio && <p className="text-gray-700 mb-4 italic">{user.bio}</p>}
        <div className="flex gap-4 mt-4">
          <button className="bg-red-500 text-white px-6 py-2 rounded-lg font-bold hover:bg-red-600 transition">Delete Account</button>
        </div>

        {/* Edit Profile Modal */}
        {isModalOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
              <h2 className="text-2xl font-bold text-center text-primary mb-6">Edit Profile</h2>
              <form onSubmit={handleUpdate} className="space-y-6">
                <div>
                  <label htmlFor="firstName" className="block text-sm font-medium text-gray-700">
                    First Name
                  </label>
                  <input
                    id="firstName"
                    name="firstName"
                    type="text"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="lastName" className="block text-sm font-medium text-gray-700">
                    Last Name
                  </label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    required
                    aria-required="true"
                  />
                </div>
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                    Email
                  </label>
                  <input
                    id="email"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                    required
                    aria-required="true"
                  />
                </div>
                {user.role === 'Instructor' && (
                  <div>
                    <label htmlFor="bio" className="block text-sm font-medium text-gray-700">
                      Bio
                    </label>
                    <textarea
                      id="bio"
                      name="bio"
                      value={formData.bio}
                      onChange={handleInputChange}
                      className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent transition"
                      rows="4"
                      placeholder="Tell us about yourself (e.g., expertise, experience)"
                    />
                  </div>
                )}
                <div className="flex justify-end space-x-4">
                  <button
                    type="button"
                    onClick={() => setIsModalOpen(false)}
                    className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400 transition"
                    aria-label="Cancel"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition"
                    aria-label="Save Changes"
                  >
                    Save Changes
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Enrolled Courses (for Students) */}
        {user.role === 'Student' && (
          <div className="bg-white rounded-xl shadow-lg p-8 mt-10">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Enrolled Courses</h2>
            {enrollments.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                {enrollments.map((enrollment) => {
                  const course = enrollment.course;
                  const instructor = course.instructor;
                  return (
                    <div
                      key={course._id}
                      className="bg-white rounded-2xl shadow-md hover:shadow-xl transition-shadow duration-300 overflow-hidden flex flex-col h-full border border-gray-100 group"
                    >
                      <div className="w-full aspect-[16/9] bg-gradient-to-br from-primary/10 to-secondary/10 flex items-center justify-center overflow-hidden">
                        <img
                          src={course.image || course.coverImage || 'https://via.placeholder.com/300x200?text=Course'}
                          alt={course.title}
                          className="w-full h-full object-cover object-center group-hover:scale-105 transition-transform duration-300"
                          onError={e => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/300x200?text=Course'; }}
                        />
                      </div>
                      <div className="flex-1 flex flex-col p-5">
                        <h3 className="text-xl font-bold text-gray-800 mb-1 truncate" title={course.title}>{course.title}</h3>
                        {instructor && (
                          <div className="text-sm text-gray-500 mb-2">
                            <span className="font-medium">Instructor:</span> {instructor.firstName ? `${instructor.firstName} ${instructor.lastName}` : instructor.name || instructor.email || 'N/A'}
                          </div>
                        )}
                        <div className="flex items-center gap-2 mb-2">
                          <span className="inline-block bg-primary/10 text-primary px-2 py-0.5 rounded text-xs font-semibold">Enrolled: {enrollment.enrolledAt ? new Date(enrollment.enrolledAt).toLocaleDateString() : '-'}</span>
                          {enrollment.certificateIssued && (
                            <span className="inline-block bg-green-100 text-green-700 px-2 py-0.5 rounded text-xs font-semibold">Certified</span>
                          )}
                        </div>
                        <div className="mb-2">
                          <div className="flex justify-between text-xs text-gray-500 mb-1">
                            <span>Progress</span>
                            <span>{enrollment.progress || 0}%</span>
                          </div>
                          <div className="w-full bg-gray-200 rounded-full h-2.5">
                            <div
                              className="bg-gradient-to-r from-primary to-secondary h-2.5 rounded-full transition-all duration-300"
                              style={{ width: `${enrollment.progress || 0}%` }}
                            ></div>
                          </div>
                        </div>
                        <div className="flex-1"></div>
                        <div className="flex items-center justify-between mt-4">
                          <Link
                            to={`/course-contents/${course._id}`}
                            className="inline-block bg-primary text-white text-sm px-4 py-2 rounded-lg font-semibold shadow hover:bg-secondary transition"
                            aria-label={`Continue ${course.title}`}
                          >
                            Continue Learning
                          </Link>
                          {enrollment.certificateIssued && (
                            <button
                              className="ml-2 px-3 py-1 bg-green-600 text-white rounded-lg hover:bg-green-700 text-xs font-semibold shadow"
                              disabled
                              title="Certificate download coming soon"
                            >
                              Download Certificate
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <p className="text-gray-600 text-center">No courses enrolled yet.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
