import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const Navbar = () => {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [token, setToken] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      try {
        const decoded = jwtDecode(storedToken);
        setRole(decoded.role);
        setToken(storedToken);
      } catch (err) {
        console.error('Invalid token:', err);
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    setRole(null);
    navigate('/login', { state: { message: 'Logged out successfully!' } });
  };

  return (
    <nav className="bg-blue-700 shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <Link to="/" className="text-white text-3xl font-extrabold tracking-tight" aria-label="EduVerse Home">
            EduVerse
          </Link>

          {/* Desktop Menu */}
          <div className="hidden md:flex space-x-6 items-center">
            <Link
              to="/"
              className="text-white hover:text-gray-300 font-semibold transition duration-200"
              aria-label="Home"
            >
              Home
            </Link>
            <Link
              to="/courses"
              className="text-white hover:text-gray-300 font-semibold transition duration-200"
              aria-label="Courses"
            >
              Courses
            </Link>

            {token ? (
              <>
                <Link
                  to="/profile"
                  className="text-white hover:text-gray-300 font-semibold transition duration-200"
                  aria-label="Profile"
                >
                  Profile
                </Link>
                {role === 'Admin' && (
                  <Link
                    to="/admin/dashboard"
                    className="text-white hover:text-gray-300 font-semibold transition duration-200"
                    aria-label="Admin Dashboard"
                  >
                    Admin Dashboard
                  </Link>
                )}
                <button
                  onClick={handleLogout}
                  className="ml-4 bg-primary px-4 py-2 rounded-md text-white font-semibold hover:bg-primary-dark transition duration-200 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
                  aria-label="Logout"
                >
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link
                  to="/login"
                  className="text-white hover:text-gray-300 font-semibold transition duration-200"
                  aria-label="Login"
                >
                  Login
                </Link>
                <Link
                  to="/signup"
                  className="ml-4 bg-primary px-4 py-2 rounded-md text-white font-semibold hover:bg-primary-dark transition duration-200 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
                  aria-label="Signup"
                >
                  Signup
                </Link>
              </>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              type="button"
              className="text-white hover:text-gray-300 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
              aria-label="Toggle menu"
              aria-expanded={menuOpen}
            >
              <svg
                className="h-8 w-8"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                aria-hidden="true"
              >
                {menuOpen ? (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                ) : (
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                )}
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="md:hidden bg-blue-700 px-4 pt-2 pb-4 space-y-2 shadow-lg">
          <Link
            to="/"
            onClick={() => setMenuOpen(false)}
            className="block text-white hover:text-gray-300 font-semibold transition duration-200"
            aria-label="Home"
          >
            Home
          </Link>
          <Link
            to="/courses"
            onClick={() => setMenuOpen(false)}
            className="block text-white hover:text-gray-300 font-semibold transition duration-200"
            aria-label="Courses"
          >
            Courses
          </Link>

          {token ? (
            <>
              <Link
                to="/profile"
                onClick={() => setMenuOpen(false)}
                className="block text-white hover:text-gray-300 font-semibold transition duration-200"
                aria-label="Profile"
              >
                Profile
              </Link>
              {role === 'Admin' && (
                <Link
                  to="/admin/dashboard"
                  onClick={() => setMenuOpen(false)}
                  className="block text-white hover:text-gray-300 font-semibold transition duration-200"
                  aria-label="Admin Dashboard"
                >
                  Admin Dashboard
                </Link>
              )}
              <button
                onClick={() => {
                  handleLogout();
                  setMenuOpen(false);
                }}
                className="w-full mt-2 bg-primary px-4 py-2 rounded-md text-white font-semibold hover:bg-primary-dark transition duration-200 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
                aria-label="Logout"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                onClick={() => setMenuOpen(false)}
                className="block text-white hover:text-gray-300 font-semibold transition duration-200"
                aria-label="Login"
              >
                Login
              </Link>
              <Link
                to="/signup"
                onClick={() => setMenuOpen(false)}
                className="block mt-2 bg-primary px-4 py-2 rounded-md text-white font-semibold hover:bg-primary-dark transition duration-200 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2"
                aria-label="Signup"
              >
                Signup
              </Link>
            </>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
