import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import API_CONFIG from '../config/apiConfig';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const res = await fetch(`${API_CONFIG.BASE_URL}/courses`);
        const data = await res.json();
        setCourses(data.courses || []);
      } catch (err) {
        setError('Failed to fetch courses. Please try again later.');
      }
    };
    fetchCourses();
  }, []);

  const filteredCourses = courses.filter(course =>
    course.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="min-h-[80vh] bg-gray-50 py-12 px-4">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-extrabold text-primary mb-8 text-center drop-shadow">All Courses</h1>
        <div className="flex justify-center mb-8">
          <input
            type="text"
            placeholder="Search courses..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            className="w-full max-w-md px-5 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-primary focus:outline-none text-lg shadow"
          />
        </div>
        {error && <p className="text-center text-red-600 mb-8">{error}</p>}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredCourses.length === 0 ? (
            <p className="col-span-full text-center text-gray-500 text-lg">No courses found.</p>
          ) : (
            filteredCourses.map(course => (
              <div
                key={course._id}
                className="bg-white rounded-2xl shadow-lg hover:shadow-2xl transition transform hover:-translate-y-2 overflow-hidden flex flex-col"
              >
                <img src={course.coverImage || 'https://images.unsplash.com/photo-1461749280684-dccba630e2f6?auto=format&fit=crop&w=600&q=80'} alt={course.title} className="w-full h-48 object-cover" />
                <div className="p-6 flex-1 flex flex-col">
                  <h2 className="text-xl font-bold text-primary mb-2">{course.title}</h2>
                  <p className="text-gray-600 mb-4 flex-1">{course.description?.slice(0, 80)}...</p>
                  <div className="flex items-center justify-between mt-auto">
                    <span className="text-lg font-semibold text-indigo-700">${course.price}</span>
                    <Link
                      to={`/courses/${course._id}`}
                      className="bg-primary text-white px-4 py-2 rounded-lg font-semibold hover:bg-secondary transition"
                    >
                      View Details
                    </Link>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Courses;
