import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { Dialog, Transition } from '@headlessui/react';
import { Fragment } from 'react';

const CourseContents = () => {
  const { courseId } = useParams();
  const [contents, setContents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [quizModalOpen, setQuizModalOpen] = useState(false);
  const [activeQuiz, setActiveQuiz] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [quizSubmitting, setQuizSubmitting] = useState(false);
  const [quizResult, setQuizResult] = useState(null);
  const [quizSubmission, setQuizSubmission] = useState({}); // {quizContentId: {score, answers}}

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get(`${API_CONFIG.BASE_URL}/course-content/course/${courseId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setContents(res.data.contents))
      .catch(() => setContents([]))
      .finally(() => setLoading(false));
  }, [courseId]);

  // Fetch quiz submission for each quiz on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    contents.forEach(content => {
      if (content.type === 'quiz') {
        axios.get(`${API_CONFIG.BASE_URL}/quiz-submissions/${content._id}`, {
          headers: { Authorization: `Bearer ${token}` }
        }).then(res => {
          setQuizSubmission(prev => ({ ...prev, [content._id]: res.data.submission }));
        }).catch(() => {});
      }
    });
    // eslint-disable-next-line
  }, [contents]);

  const openQuiz = (quiz) => {
    setActiveQuiz(quiz);
    setQuizAnswers({});
    setQuizResult(null);
    setQuizModalOpen(true);
  };

  const handleQuizAnswer = (q, val) => {
    setQuizAnswers(prev => ({ ...prev, [q]: val }));
  };

  const handleQuizSubmit = async (e) => {
    e.preventDefault();
    setQuizSubmitting(true);
    const token = localStorage.getItem('token');
    const answers = (activeQuiz.questions || []).map(q => ({ question: q.question, selected: quizAnswers[q.question] || '' }));
    try {
      const res = await axios.post(`${API_CONFIG.BASE_URL}/quiz-submissions`, {
        quizContentId: activeQuiz._id,
        answers
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuizResult(res.data);
      setQuizSubmission(prev => ({ ...prev, [activeQuiz._id]: { score: res.data.score, answers: res.data.answers } }));
    } catch (err) {
      setQuizResult({ error: err.response?.data?.message || 'Submission failed' });
    } finally {
      setQuizSubmitting(false);
    }
  };

  if (loading) return <div className="p-8">Loading...</div>;

  return (
    <div className="min-h-screen px-4 py-12 bg-gray-50">
      <div className="max-w-4xl mx-auto bg-white rounded-2xl shadow-lg p-8">
        <h1 className="text-2xl font-bold mb-6 text-primary">Course Contents</h1>
        {contents.length === 0 ? (
          <p className="text-gray-600">No content available for this course yet.</p>
        ) : (
          <div className="space-y-6">
            {contents.map(content => (
              <div key={content._id} className="bg-gray-50 rounded-xl p-4 shadow">
                <div className="flex justify-between items-center mb-2">
                  <span className="font-semibold text-primary">{content.title}</span>
                  <span className="text-xs bg-primary text-white rounded px-2 py-1">{content.type}</span>
                </div>
                <div className="text-gray-700 mb-2">{content.description}</div>
                {content.contentUrl && <a href={content.contentUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">View File/Video</a>}
                {content.externalLink && <a href={content.externalLink} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline ml-2">External Link</a>}
                {content.noticeText && <div className="mt-2 text-yellow-700">Notice: {content.noticeText}</div>}
                {content.liveDate && <div className="mt-2 text-green-700">Live Date: {new Date(content.liveDate).toLocaleString()}</div>}
                {content.dueDate && <div className="mt-2 text-red-700">Due Date: {new Date(content.dueDate).toLocaleDateString()}</div>}
                {content.type === 'quiz' && content.questions && (
                  <div className="mt-2 text-indigo-700">
                    Quiz: {content.questions.length} questions
                    {quizSubmission[content._id] ? (
                      <div className="mt-2">
                        <div className="font-semibold text-green-700">Your Score: {quizSubmission[content._id].score} / {content.questions.length}</div>
                        <ul className="mt-2 space-y-1">
                          {quizSubmission[content._id].answers.map((a, idx) => (
                            <li key={idx} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                              <span className="font-semibold">Q:</span> {a.question}<br />
                              <span className="font-semibold">Your Answer:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                              <span className="font-semibold">Correct:</span> {a.correct}
                            </li>
                          ))}
                        </ul>
                      </div>
                    ) : (
                      <button onClick={() => openQuiz(content)} className="mt-2 px-4 py-2 bg-primary text-white rounded hover:bg-secondary">Take Quiz</button>
                    )}
                  </div>
                )}
                {content.type === 'poll' && content.pollOptions && (
                  <div className="mt-2 text-indigo-700">Poll Options: {content.pollOptions.map(opt => opt.option).join(', ')}</div>
                )}
                {content.type === 'survey' && content.surveyQuestions && (
                  <div className="mt-2 text-indigo-700">Survey: {content.surveyQuestions.length} questions</div>
                )}
              </div>
            ))}
          </div>
        )}
        <Link to="/profile" className="inline-block mt-8 text-primary hover:underline">Back to Profile</Link>
      </div>
      {/* Quiz Modal */}
      <Transition appear show={quizModalOpen} as={Fragment}>
        <Dialog as="div" className="relative z-50" onClose={() => setQuizModalOpen(false)}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black bg-opacity-30" />
          </Transition.Child>
          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen px-4">
              <Transition.Child
                as={Fragment}
                enter="ease-out duration-300"
                enterFrom="opacity-0 scale-95"
                enterTo="opacity-100 scale-100"
                leave="ease-in duration-200"
                leaveFrom="opacity-100 scale-100"
                leaveTo="opacity-0 scale-95"
              >
                <Dialog.Panel className="relative bg-white rounded-lg shadow-xl w-full max-w-lg mx-auto p-8">
                  <Dialog.Title className="text-xl font-bold mb-4">Quiz: {activeQuiz?.title}</Dialog.Title>
                  {quizResult && quizResult.error && (
                    <div className="mb-4 text-red-600 font-semibold">{quizResult.error}</div>
                  )}
                  {quizResult && !quizResult.error ? (
                    <div>
                      <div className="mb-4 text-green-700 font-semibold">Your Score: {quizResult.score} / {activeQuiz.questions.length}</div>
                      <ul className="space-y-2">
                        {quizResult.answers.map((a, idx) => (
                          <li key={idx} className={a.isCorrect ? 'text-green-700' : 'text-red-600'}>
                            <span className="font-semibold">Q:</span> {a.question}<br />
                            <span className="font-semibold">Your Answer:</span> {a.selected || <span className="italic text-gray-400">No answer</span>}<br />
                            <span className="font-semibold">Correct:</span> {a.correct}
                          </li>
                        ))}
                      </ul>
                      <div className="flex justify-end mt-6">
                        <button onClick={() => setQuizModalOpen(false)} className="px-4 py-2 bg-primary text-white rounded">Close</button>
                      </div>
                    </div>
                  ) : (
                    <form onSubmit={handleQuizSubmit} className="space-y-6">
                      {activeQuiz?.questions?.map((q, idx) => (
                        <div key={idx} className="mb-4">
                          <div className="font-semibold mb-2">Q{idx + 1}: {q.question}</div>
                          <div className="space-y-1">
                            {q.options.map((opt, oidx) => (
                              <label key={oidx} className="flex items-center gap-2">
                                <input
                                  type="radio"
                                  name={`q_${idx}`}
                                  value={opt}
                                  checked={quizAnswers[q.question] === opt}
                                  onChange={() => handleQuizAnswer(q.question, opt)}
                                  className="form-radio text-primary"
                                  required
                                />
                                {opt}
                              </label>
                            ))}
                          </div>
                        </div>
                      ))}
                      <div className="flex justify-end gap-2 mt-4">
                        <button type="button" onClick={() => setQuizModalOpen(false)} className="px-4 py-2 bg-gray-200 rounded">Cancel</button>
                        <button type="submit" className="px-4 py-2 bg-primary text-white rounded" disabled={quizSubmitting}>{quizSubmitting ? 'Submitting...' : 'Submit Quiz'}</button>
                      </div>
                    </form>
                  )}
                </Dialog.Panel>
              </Transition.Child>
            </div>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
};

export default CourseContents;
