import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import EditCourseModal from '../components/EditCourseModal';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';

const InstructorProfile = () => {
  const [userData, setUserData] = useState(null);
  const [createdCourses, setCreatedCourses] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    bio: '',
  });
  const [enrolledStudents, setEnrolledStudents] = useState({});
  const [analytics, setAnalytics] = useState([]);
  const [totalEarnings, setTotalEarnings] = useState(0);
  const navigate = useNavigate();

  const fetchCourses = async (token) => {
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/courses/created`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCreatedCourses(res.data.courses);
    } catch {
      setCreatedCourses([]);
    }
  };

  const fetchEnrolledStudents = async (courseId, token) => {
    try {
      const res = await axios.get(`${API_CONFIG.BASE_URL}/enrollments/course/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEnrolledStudents((prev) => ({ ...prev, [courseId]: res.data.enrollments.map(e => e.student) }));
    } catch {
      setEnrolledStudents((prev) => ({ ...prev, [courseId]: [] }));
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decoded = jwtDecode(token);
      if (decoded.role !== 'Instructor') {
        navigate('/profile');
        return;
      }
      axios.get(`${API_CONFIG.BASE_URL}/auth/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then((res) => {
        setUserData(res.data.user);
        setFormData({
          firstName: res.data.user.firstName,
          lastName: res.data.user.lastName,
          email: res.data.user.email,
          bio: res.data.user.bio || '',
        });
      }).catch(() => {
        toast.error('Failed to load Instructor profile');
        localStorage.removeItem('token');
        navigate('/login');
      });
      fetchCourses(token).then(() => {
        createdCourses.forEach((course) => fetchEnrolledStudents(course._id, token));
      });
    } catch (err) {
      console.error('Invalid token:', err);
      localStorage.removeItem('token');
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (userData?.id) {
      axios.get(`${API_CONFIG.BASE_URL}/enrollments/analytics/instructor/${userData.id}`, {
        headers: { Authorization: `Bearer ${token}` },
      }).then((res) => {
        setAnalytics(res.data.analytics);
        setTotalEarnings(res.data.totalEarnings || 0);
      }).catch(() => {
        setAnalytics([]);
        setTotalEarnings(0);
      });
    }
  }, [userData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await axios.put(`${API_CONFIG.BASE_URL}/auth/update`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUserData(response.data.user);
      setIsModalOpen(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.message || 'Failed to update profile');
    }
  };

  const handleEditCourse = (course) => {
    setEditingCourse(course);
    setIsModalOpen(true);
  };

  const handleSaveCourse = async (form) => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      if (value !== null && value !== undefined) formData.append(key, value);
    });
    try {
      await axios.put(`${API_CONFIG.BASE_URL}/courses/${editingCourse._id}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Course updated successfully!');
      setIsModalOpen(false);
      setEditingCourse(null);
      fetchCourses(token);
    } catch {
      toast.error('Failed to update course.');
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm('Are you sure you want to delete this course?')) return;
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`${API_CONFIG.BASE_URL}/courses/${courseId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Course deleted successfully!');
      fetchCourses(token);
    } catch {
      toast.error('Failed to delete course.');
    }
  };

  if (!userData) return null;

  return (
    <section className="min-h-screen px-4 py-12 bg-gray-50">
      <ToastContainer position="top-center" autoClose={3000} theme="colored" />
      <EditCourseModal
        open={isModalOpen}
        onClose={() => { setIsModalOpen(false); setEditingCourse(null); }}
        course={editingCourse}
        onSave={handleSaveCourse}
      />
      <div className="mx-auto max-w-7xl">
        <div className="flex flex-col md:flex-row items-center gap-6 bg-white p-8 shadow rounded-xl mb-10">
          <div className="w-24 h-24 bg-gradient-to-br from-primary to-secondary text-white rounded-full flex items-center justify-center text-3xl font-bold">
            {userData.firstName[0]}{userData.lastName[0]}
          </div>
          <div className="text-center md:text-left">
            <h1 className="text-3xl font-bold text-gray-800">{userData.firstName} {userData.lastName}</h1>
            <p className="text-sm text-gray-600">{userData.email}</p>
            <p className="mt-1 text-xs font-medium text-indigo-900 bg-yellow-400 inline-block px-3 py-1 rounded-full">Instructor</p>
            <p className="mt-2 text-sm italic text-gray-600 max-w-md">{userData.bio || 'No bio provided.'}</p>
            <div className="mt-4 flex flex-col sm:flex-row gap-3">
              <button onClick={() => setIsModalOpen(true)} className="bg-primary text-white px-4 py-2 rounded hover:bg-secondary transition">Edit Profile</button>
              <Link to="/instructor/create-course" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition">New Course</Link>
            </div>
          </div>
        </div>
        {/* Remaining UI stays unchanged */}
      </div>
    </section>
  );
};

export default InstructorProfile;
