import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Static course data (replace with API call later)
const coursesData = [
  {
    id: 1,
    title: 'Web Development Bootcamp',
    description: 'Master HTML, CSS, and JavaScript in 8 weeks.',
    image: 'https://via.placeholder.com/300x200?text=Web+Development',
    category: 'Web Development',
    price: 'Paid',
    level: 'Beginner',
  },
  {
    id: 2,
    title: 'Data Science Fundamentals',
    description: 'Learn Python, Pandas, and Machine Learning basics.',
    image: 'https://via.placeholder.com/300x200?text=Data+Science',
    category: 'Data Science',
    price: 'Paid',
    level: 'Intermediate',
  },
  {
    id: 3,
    title: 'Digital Marketing Mastery',
    description: 'Boost your business with SEO and social media strategies.',
    image: 'https://via.placeholder.com/300x200?text=Digital+Marketing',
    category: 'Marketing',
    price: 'Free',
    level: 'Beginner',
  },
  {
    id: 4,
    title: 'Advanced Machine Learning',
    description: 'Deep dive into neural networks and AI.',
    image: 'https://via.placeholder.com/300x200?text=Machine+Learning',
    category: 'Data Science',
    price: 'Paid',
    level: 'Advanced',
  },
];

const Courses = () => {
  const [categoryFilter, setCategoryFilter] = useState('All');
  const [priceFilter, setPriceFilter] = useState('All');
  const [levelFilter, setLevelFilter] = useState('All');
  const [searchQuery, setSearchQuery] = useState('');

  // Filter courses
  const filteredCourses = coursesData.filter((course) => {
    return (
      (categoryFilter === 'All' || course.category === categoryFilter) &&
      (priceFilter === 'All' || course.price === priceFilter) &&
      (levelFilter === 'All' || course.level === levelFilter) &&
      course.title.toLowerCase().includes(searchQuery.toLowerCase())
    );
  });

  return (
    <div className="flex-1 bg-gray-100 py-12 px-4">
      <div className="container mx-auto">
        {/* Header */}
        <h1 className="text-3xl md:text-4xl font-bold text-center text-primary mb-8">
          Explore Our Courses
        </h1>

        {/* Filters */}
        <div className="mb-8">
          <div className="flex flex-col md:flex-row gap-4 justify-between items-center">
            {/* Search Bar */}
            <input
              type="text"
              placeholder="Search courses..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full md:w-1/3 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
            />

            {/* Filters */}
            <div className="flex flex-col sm:flex-row gap-4">
              {/* Category Filter */}
              <select
                value={categoryFilter}
                onChange={(e) => setCategoryFilter(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
              >
                <option value="All">All Categories</option>
                <option value="Web Development">Web Development</option>
                <option value="Data Science">Data Science</option>
                <option value="Marketing">Marketing</option>
              </select>

              {/* Price Filter */}
              <div className="flex gap-2">
                {['All', 'Free', 'Paid'].map((price) => (
                  <button
                    key={price}
                    onClick={() => setPriceFilter(price)}
                    className={`px-4 py-2 rounded-md ${
                      priceFilter === price
                        ? 'bg-primary text-white'
                        : 'bg-white border border-gray-300 text-primary hover:bg-gray-100'
                    } transition`}
                  >
                    {price}
                  </button>
                ))}
              </div>

              {/* Level Filter */}
              <div className="flex gap-2">
                {['All', 'Beginner', 'Intermediate', 'Advanced'].map((level) => (
                  <button
                    key={level}
                    onClick={() => setLevelFilter(level)}
                    className={`px-4 py-2 rounded-md ${
                      levelFilter === level
                        ? 'bg-primary text-white'
                        : 'bg-white border border-gray-300 text-primary hover:bg-gray-100'
                    } transition`}
                  >
                    {level}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Course Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredCourses.length > 0 ? (
            filteredCourses.map((course) => (
              <div
                key={course.id}
                className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition transform hover:scale-105"
              >
                <img
                  src={course.image}
                  alt={course.title}
                  className="w-full h-48 object-cover"
                />
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-primary mb-2">
                    {course.title}
                  </h3>
                  <p className="text-gray-600 mb-4">{course.description}</p>
                  <p className="text-sm text-gray-500 mb-2">
                    Category: {course.category}
                  </p>
                  <p className="text-sm text-gray-500 mb-2">
                    Level: {course.level}
                  </p>
                  <p className="text-sm text-gray-500 mb-4">
                    Price: {course.price}
                  </p>
                  <Link
                    to={`/courses/${course.id}`}
                    className="inline-block bg-primary text-white px-4 py-2 rounded-md hover:bg-secondary transition"
                  >
                    View Course
                  </Link>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center text-gray-600 col-span-full">
              No courses found matching your filters.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Courses;
