import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API_CONFIG from '../config/apiConfig';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const COLORS = ['#2563eb', '#1e40af', '#22c55e', '#f59e42', '#ef4444'];

const AdminAnalytics = () => {
  const [growth, setGrowth] = useState(null);
  const [roles, setRoles] = useState([]);
  const [revenue, setRevenue] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const [growthRes, rolesRes, revenueRes] = await Promise.all([
        axios.get(`${API_CONFIG.BASE_URL}/admin/analytics/growth`, { headers: { Authorization: `Bearer ${token}` } }),
        axios.get(`${API_CONFIG.BASE_URL}/admin/analytics/roles`, { headers: { Authorization: `Bearer ${token}` } }),
        axios.get(`${API_CONFIG.BASE_URL}/admin/analytics/revenue`, { headers: { Authorization: `Bearer ${token}` } }),
      ]);
      setGrowth(growthRes.data);
      setRoles(rolesRes.data.roles);
      setRevenue(revenueRes.data.totalRevenue);
    } catch (error) {
      toast.error('Failed to fetch analytics');
    } finally {
      setLoading(false);
    }
  };

  const formatMonth = (year, month) => {
    return `${year}-${month.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!growth) {
    return (
      <div className="text-center text-gray-500 py-8">
        Failed to load analytics data
      </div>
    );
  }

  // Pie chart for roles
  const totalRoles = roles.reduce((sum, r) => sum + r.count, 0);
  let acc = 0;
  const pieSlices = roles.map((role, i) => {
    const percent = totalRoles ? (role.count / totalRoles) : 0;
    const start = acc;
    const end = acc + percent;
    acc = end;
    const largeArc = percent > 0.5 ? 1 : 0;
    const x1 = Math.cos(2 * Math.PI * start) * 40 + 50;
    const y1 = Math.sin(2 * Math.PI * start) * 40 + 50;
    const x2 = Math.cos(2 * Math.PI * end) * 40 + 50;
    const y2 = Math.sin(2 * Math.PI * end) * 40 + 50;
    return (
      <path
        key={role.role}
        d={`M50,50 L${x1},${y1} A40,40 0 ${largeArc} 1 ${x2},${y2} Z`}
        fill={COLORS[i % COLORS.length]}
      />
    );
  });

  return (
    <div className="space-y-6">
      <div className="flex flex-wrap gap-6">
        <div className="bg-white rounded-lg shadow-lg p-6 flex-1 min-w-[250px]">
          <div className="text-gray-600 text-sm mb-1">Total Revenue</div>
          <div className="text-3xl font-bold text-green-600">${revenue.toLocaleString()}</div>
        </div>
        <div className="bg-white rounded-lg shadow-lg p-6 flex-1 min-w-[250px]">
          <div className="text-gray-600 text-sm mb-1">Role Distribution</div>
          <div className="flex items-center gap-4">
            <svg width="100" height="100" viewBox="0 0 100 100">
              {pieSlices}
            </svg>
            <div>
              {roles.map((role, i) => (
                <div key={role.role} className="flex items-center gap-2 text-sm">
                  <span style={{ background: COLORS[i % COLORS.length] }} className="inline-block w-3 h-3 rounded-full"></span>
                  {role.role}: <span className="font-bold">{role.count}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">User Growth (12 months)</h3>
          <div className="h-64 flex items-end justify-between space-x-1">
            {growth.userGrowth.map((item, idx) => {
              const maxValue = Math.max(...growth.userGrowth.map(d => d.count));
              const height = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
              return (
                <div key={idx} className="flex flex-col items-center flex-1">
                  <div className="w-full bg-primary rounded-t" style={{ height: `${height}%` }}></div>
                  <div className="text-xs text-gray-500 mt-2 rotate-45 origin-left">
                    {formatMonth(item.year, item.month)}
                  </div>
                  <div className="text-xs font-medium text-gray-700 mt-1">{item.count}</div>
                </div>
              );
            })}
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Course Growth (12 months)</h3>
          <div className="h-64 flex items-end justify-between space-x-1">
            {growth.courseGrowth.map((item, idx) => {
              const maxValue = Math.max(...growth.courseGrowth.map(d => d.count));
              const height = maxValue > 0 ? (item.count / maxValue) * 100 : 0;
              return (
                <div key={idx} className="flex flex-col items-center flex-1">
                  <div className="w-full bg-blue-500 rounded-t" style={{ height: `${height}%` }}></div>
                  <div className="text-xs text-gray-500 mt-2 rotate-45 origin-left">
                    {formatMonth(item.year, item.month)}
                  </div>
                  <div className="text-xs font-medium text-gray-700 mt-1">{item.count}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      <ToastContainer />
    </div>
  );
};

export default AdminAnalytics;
